INSERT INTO `scan_suggest_log` VALUES (10056, '/doc Directory Browsable', '/doc目录可被浏览', '中危', 'Debian GNU/Linux上Apache的一个默认配置设置ServerRoot到/ usr / doc存在漏洞。远程攻击者利用该漏洞读取整个服务器的文档文件', '为 /doc directory设置访问控制权限，如果您使用Apache，您可以需要如此设置：<Directory /usr/doc>允许对本地目录进行访问权限控制，明确允许/拒绝策略。 相关链接： http://projects.webappsec.org/Directory-Indexing', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10061, 'Echo Service Detection', 'UDP炸弹或UDP数据包风暴漏洞', '中危', '回声和chargen，或与UDP服务的其他组合存在漏洞。该漏洞可在串联中涌入服务器，又称为UDP炸弹或UDP数据包风暴。', '参考：https://cve.mitre.org/cgi-bin/cvename.cgi?name=CVE-1999-0103', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10068, 'Finger Service Remote Information Disclosure', 'Finger服务远程信息泄露', '中危', 'Finger服务可用于查询用户的信息，包括网上成员的真实姓名、用户名、最近登录时间和地点等，也可以用来显示当前登录在机器上的所有用户名。攻击者可利用该信息进一步攻击。', '在文件/etc/inetd.conf中 把\'finger\' 对应的行注释，并重启 inetd process', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10069, 'Finger 0@host Unused Account Disclosure', 'fingerd数字用户名获取系统用户列表漏洞', '中危', '远程主机正在运行“finger”服务，该服务存在信息泄露漏洞。具体来说，它允许未经身份验证的攻击者在远程主机上显示从未使用过的帐户列表。这个列表可以帮助攻击者猜测操作系统类型并集中攻击。', '* 如果您并不需要finger服务，您最好关闭finger服务。 详细步骤如下, 以Solaris系统为例： 1. 转变成root用户 $ su - # 2. 关闭finger服务 使用您熟悉的编辑器打开/etc/inetd.conf文件，找到如下行： finger stream tcp nowait nobody /usr/sbin/in.fingerd in.fingerd 在该行的开始处增加\"#\"号来将其注释： #finger stream tcp nowait nobody /usr/sbin/in.fingerd in.fingerd 保存修改，退出编辑器。 3. 重新启动inetd # ps -ef|grep inetd root 167 1 0 Oct 07 ? 0:07 /usr/sbin/inetd -s -t # kill -HUP 167 (上面的例子中，167是inetd的pid) * 过滤访问这个端口 * 最终解决方案升级fingerd服务器。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10073, 'Finger Recursive Request Arbitrary Site Redirection', 'Finger服务允许递归请求任意站点（重定向）', '中危', '远程finger服务接受请求重定向。也就是说，用户可以通过以下命令实验： finger user@host@victim 这允许攻击者使用这台计算机作为中继跳转来收集第三方网络上的信息。此外，通过大量请求来制造拒绝服务攻击远程主机。', '禁用远程finger守护进程服务（在/etc/inetd.conf配置文件中将finger相关配置行注释并重启inetd进程），或者进行finger升级。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10077, 'Microsoft FrontPage Extensions Check', '检查微软FrontPage扩展', '中危', '远程web服务器似乎正在运行FrontPage扩展。FrontPage允许远程web开发人员和管理员从远程位置修改web内容。虽然这是内部局域网中相当典型的场景，但是通过Internet(或任何其他不受信任的第三方网络)匿名用户不应该使用FrontPage扩展。', 'n/a', 'CVE-2000-0114', NULL);
INSERT INTO `scan_suggest_log` VALUES (10079, 'Anonymous FTP Enabled', 'FTP匿名登录', '中危', 'FTP服务使用了匿名登录，允许任何用户访问FTP服务文件。', '禁用FTP匿名登录', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10081, 'FTP Privileged Port Bounce Scan', 'FTP跳转攻击漏洞', '高危', '攻击者可利用该漏洞使用PORT命令让远程FTP服务器作为跳板，跳转连接到的其他主机，从而访问其它主机，让人误认为该访问来自于FTP服务器。', '1）更新升级FTP服务器 2）如果该FTP服务非必要业务，建议关闭', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10087, 'Multiple FTP Server QUOTE CWD Command Home Path Disclosure', '多个FTP服务cwd命令路径暴露漏洞', '中危', '远程主机存在FTP信息泄露漏洞，通过FTP用户的CWD命令，可以确定主目录的完整路径，攻击者可以利用此路径，通过其他安全缺陷，确定哪里存放.rhost文件。', '应用厂商提供的最新补丁信息。 相关链接： http://aix.software.ibm.com/aix/efixes/security/ftpd_advisory.asc', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10157, 'Multiple Unix Netstat Service Remote Information Disclosure', '多个Unix Netstat服务远程信息披露', '中危', '远程主机在此端口上运行“netstat”服务。 因为\'netstat\'服务为攻击者提供了有用的信息 它释放了活动连接的状态。 建议 如果不使用该服务，则禁用此服务。', '通过注释“netstat”行来禁用该服务 在/etc/inetd.conf中重新启动inetd进程。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10166, 'Windows NT FTP \'guest\' Account Present', 'Windows NT FTP服务存在guest默认账户', '高危', 'ftp服务存在默认账户，这可能运行攻击者使用默认账号入侵系统。', '如果不需要FTP服务，建议禁用，如需要，建议禁用guest默认账户。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10167, 'NTMail3 Arbitrary Mail Relay', 'NTMail3任意邮件中继', '高危', '远程SMTP服务器允许任何人将其用作邮件中继，前提是源地址设置为“<>”。 此问题允许任何垃圾邮件发送者使用您的邮件服务器将邮件发送到全世界??，从而充斥您的网络带宽并可能将您的邮件服务器列入黑名单。', '重新配置SMTP服务器，使其不能用作不加区别的SMTP中继。确保服务器使用适当的访问控制来限制可以进行中继的程度', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10188, 'Multiple Web Server printenv CGI Information Disclosure', '多种Web服务printenv CGI信息泄露', '中危', '远程WEB服务存在 \'test-cgi\' 测试脚本，printenv CGI可返回其环境变量，这给攻击者很多信息,如安装目录,服务器的IP地址(如果NAT实现),服务器管理员的电子邮件地址,服务器和模块版本中,shell环境变量……', '从/cgi-bin移除printenv', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10193, 'HTTP Proxy Arbitrary Site/Port Relaying', 'HTTP代理任意站点/端口中继', '中危', '远程代理，允许每个人对任意端口执行请求，例如: “http://www.test.com:110”。这个问题可能允许攻击者通过使用代理连接到敏感端口25 (sendmail)，从而通过防火墙。除此之外，它还可以用来对其他网络进行攻击。', '在适当的位置设置acl，以防止代理接受连接到未经授权的端口。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10194, 'HTTP Proxy POST Request Relaying', 'HTTP代理POST请求中继', '中危', '代理允许用户执行POST请求，例如 POST http://www.test.com:21 没有任何Content-length标记。 此请求可能使攻击者能够拥有交互式会话。 这个请求可能允许攻击者通过您的代理连接到敏感端口23 (telnet)，或者允许内部用户绕过防火墙规则连接到不应该允许的端口。除此之外，代理还可以用来对其他网络进行攻击。', '重新配置您的代理，以便只有内部网络的用户可以使用它，这样它就不能连接到危险或者敏感的端口', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10198, 'Quote of the Day (QOTD) Service Detection', 'UDP炸弹或UDP数据包风暴漏洞', '中危', '回声和chargen，或与UDP服务的其他组合存在漏洞。该漏洞可在串联中涌入服务器，又称为UDP炸弹或UDP数据包风暴。', '参考：https://help.aliyun.com/knowledge_detail/87427.html?spm=5176.10695662.1996646101.searchclickresult.415c36915iNx99&aly_as=PYcAMPPq', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10203, 'rexecd Service Detection', '检测到服务器正在运行rexecd 服务', '紧急', '检测到服务器正在运行rexecd 服务。这项服务是设计允许用户通过网络远程执行命令。 然而，rexecd 未提供较好的身份验证方法，所以它可能被攻击者利用，从而远程执行恶意命令。', '修改/etc/inetd.conf 文件，注释掉\'exec\'所在行，并重新启动inetd进程', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10205, 'rlogin Service Detection', '运行rlogin服务', '高危', '该服务器上开启了rlogin服务，rlogin是一种不安全的远程登录服务，可以远程浏览文件、复制文件等等。', '除非需要使用该服务来做双机热备，否则建议关闭： vi /etc/inetd.conf 用#号注释rlogin行 refresh –s inetd （重启inetd守护进程）', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10245, 'rsh Service Detection', 'rsh服务在远程主机上运行', '高危', 'rsh服务正在远程主机上运行。 这个服务很脆弱，因为数据在rsh客户端和服务器之间通过明文传递。 一个中间人攻击者可以利用这个来嗅探登录和密码。 此外，它可能允许没有密码的认证不良的登录。 如果主机容易受到TCP序列号猜测（来自任何网络）或IP欺骗（包括本地网络上的ARP劫持），则可能有可能绕过验证。最后，rsh是将文件写入访问转换为简单的方法 通过.rhosts或rhosts.equiv文件完整登录。', '注释掉/etc/inetd.conf中的\'rsh\'行，并重新启动inetd进程。 或者，禁用此服务并使用SSH。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10249, 'Multiple Mail Server EXPN/VRFY Information Disclosure', '邮件服务器 EXPN/VRFY 信息泄露', '中危', '远程SMTP服务器能够对EXPN 或 VRFY 命令进行响应。EXPN命令可以用来找到邮件的接收地址的别名或收件人全名，VRFY命令可用于检查帐户的有效性。', '在配置文件 /etc/sendmail.cf 中添加以下内容 O PrivacyOptions=goaway', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10262, 'MTA Open Mail Relaying Allowed', 'MTA允许开放邮件中继功能', '高危', '远程SMTP服务器显示允许邮件传送。这意味着一个未经身份验证的远程用户可能使用邮件服务器发送消息到世界各地,从而浪费网络带宽和计算机资源。这些服务器是批量发送垃圾邮件垃圾邮件发送者的目标。 在某些场景中,队列消息的数量交付可能成千上万,导致邮件服务器崩溃。此外,SMTP服务器,允许传送经常添加到实时阻止列表来维护全球安全使用的网站和公司。如果添加到一个列表,提供合法的邮件可能受到严重冲击,造成拒绝服务的一种形式。', '调查下远程服务器是否需要邮件传送服务，如果不需要，参考产品文档或联系供应商重新配置服务，因此，确保服务使用适当的访问控制限制传输是必要的。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10264, 'SNMP Agent Default Community Names', 'SNMP代理默认团体名', '高危', '远程攻击者可以利用这个值获得敏感信息，或进行部分配置操作。', '修改默认团体名', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10297, 'Web Server Directory Traversal Arbitrary File Access', 'web 服务器目录遍历漏洞', '中危', '低版本的 web 服务器存在目录遍历漏洞，攻击者可利用该漏洞遍历服务器上的任意文件。', '根据web服务器所使用的中间件，如IIS、apache、tomcat等等，更新中间件到最新版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10407, 'X Server Detection', 'X服务器检测', '低危', '远程主机正在运行X11服务器。X11是一种客户机-服务器协议，可用于显示在远程客户机上给定主机上运行的图形应用程序。由于X11通信量没有加密，攻击者有可能窃听连接。', '限制对此端口的访问。如果没有使用X11客户机/服务器功能，请完全禁用X11中的TCP支持（-nolisten TCP）。', NULL, '1');
INSERT INTO `scan_suggest_log` VALUES (10481, 'MySQL Unpassworded Account Check', 'MySQL Null Root Password Weak默认配置漏洞', '高危', 'MySQL 3.23.2到3.23.52的Windows二进制版本中默认配置存在NULL根密码。远程攻击者获得未经授权MySQL数据库根目录访问。', '管理员应该禁用默认帐户，或提供强密码。 DELETE FROM mysql.user; GRANT ALL PRIVILEGES ON *.* TO user@localhost IDENTIFIED BY \'password\' WITH GRANT OPTION;', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10483, 'PostgreSQL Default Unpassworded Account', 'PostgreSQL默认空口令账号', '高危', '服务器端\'postgres\'账号无需口令即可登录，攻击者将以此获取相关权限，获取数据。', '1.登录此主机，并使用“ALTER USER”命令为受影响的帐户设置密码。 2.通过编辑文件会pg_hba配置服务。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10491, 'Microsoft IIS Translate f: ASP/ASA Source Disclosure', 'Microsoft IIS 5.0 \"Translate: f\"头标记导致源码泄露漏洞(MS00-058)', '中危', 'IIS是Microsoft公司开发的流行的HTTP服务器程序，随同Windows操作系统捆绑发布。 IIS 5.0在处理某些带有特殊标记的HTTP请求时存在漏洞，远程攻击者可能利用此漏洞得到服务器上脚本的源码。 如果IIS 5.0接收到一个包含特殊头格式(Translate: f)的HTTP请求，同时URL末尾包含一个特殊字符(\"/\")的话，IIS 会错误得调用脚本处理引擎，可能导致文件源码泄漏给远程用户。', 'Microsoft已经为此发布了一个安全公告(MS00-058)以及相应补丁: MS00-058：Patch Available for \"Specialized Header\" Vulnerability 链接： http://www.microsoft.com/technet/security/bulletin/MS00-058.asp 补丁下载： http://www.microsoft.com/Downloads/Release.asp?ReleaseID=23769', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10498, 'Web Server HTTP Dangerous Method Detection', '不安全HTTP 方法未禁用', '高危', 'WebDAV （Web-based Distributed Authoring and Versioning） 一种基于 HTTP 1.1协议的通信协议。它扩展了HTTP 1.1，在GET、POST、HEAD等几个HTTP标准方法以外添加了一些新的方法，使应用程序可直接对Web Server直接读写，还可以支持文件的版本控制。不合理的权限配置导致任意用户可以通过PUT方法直接上传任意文件到有写权限的目录，例如攻击者可上传WebShell，从而控制网站。', '解决方案： [1] 限制PUT、DELETE、SEARCH、COPY、MOVE等危险的方法的访问权限 [2] 如果不需要使用上述方法，应关闭方法', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10505, 'Apache WebDAV Module PROPFIND Arbitrary Directory Listing', 'SuSE Linux Apache WebDAV目录遍历漏洞', '中危', 'SuSE Linux 6.4的Apache 1.3.12默认配置启用WebDAV，远程攻击者可以利用该漏洞借助PROPFIND HTTP请求方法列出任意目录。', '禁用WebDAV模块或apache-httpd-upgrade-1_3_13 参考：https://www.rapid7.com/db/vulnerabilities/apache-httpd-cve-2000-0869 参考：https://blog.csdn.net/qq_31455515/article/details/80166596', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10539, 'DNS Server Recursive Query Cache Poisoning Weakness', 'DNS缓存器中毒漏洞', '中危', 'DNS服务器允许递归查询，DNS缓存器可能经由BIND通过预测查询IDs而中毒。', '限制允许递归查询的主机。如果您正在使用bind 8,你可以通过使用named.conf“选项”的指令 allow-recursion 来完成。如果您正在使用bing 9,您可以定义一个组织的内部地址，通过使用acl的命令来完成。您可以显式地声明:“allow-recursion { hosts_defined_in_acl }’如果您使用的是其他域名服务器,请参考其文档。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10595, 'DNS Server Zone Transfer Information Disclosure (AXFR)', 'DNS服务器区域传输漏洞', '中危', 'DNS服务器存在漏洞。可以借助该漏洞导致区域传输。', '在“允许区域传送”配置“只允许到下列服务器”，指定要进行区域传送的服务器', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10660, 'Oracle Database Listener Program (tnslsnr) Service Blank Password', 'Oracle数据库侦听器程序(tnslsnr)服务密码空白', '中危', '远程Oracle侦听器程序(tnslsnr)没有指定密码，攻击者可以利用这一点来任意关闭它,从而阻止合法用户使用它。', '使用lsnrctrl CHANGE_PASSWORD命令为侦听器指定一个密码。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10663, 'DHCP Server Detection', 'DHCP服务器检测', '低危', '此脚本将联系远程DHCP服务器（如果有），并尝试检索有关网络布局的信息。某些DHCP服务器提供敏感信息，如NIS域名，或网络布局信息，如网络web服务器列表等。它没有显示任何漏洞，但是本地攻击者可以使用DHCP来熟悉相关网络。', '应用筛选以使此信息远离网络，并删除任何未使用的选项。', NULL, '1');
INSERT INTO `scan_suggest_log` VALUES (10673, 'Microsoft SQL Server sa Account Default Blank Password', 'MSSQL Server “sa”账号空口令', '高危', 'MSSQL Server “sa”账号空口令，攻击者可以利用其直接控制整个数据库，并且可以通过数据库获取到整台数据库系统的控制权限。', '修改MSSQL数据库sa用户密码', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10677, 'Apache mod_status /server-status Information Disclosure', 'Apache mod_status /server-status 信息泄露漏洞', '中危', 'Apache 未禁用或限制 URL \'/server-status\'的访问，从而任意用户可访问到 mod_status /server-status 相关信息。', 'Apache 禁用或限制普通用户 对 mod_status /server-status的访问', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10678, 'Apache mod_info /server-info Information Disclosure', 'Apache mod_info /server-info信息泄露', '中危', '通过请求URL \'/server-info\'，可以获得远程Apache web服务器配置的概述，包括已安装的模块、它们的配置和各种运行时设置等信息。', '如果需要，更新Apache的配置文件，要么禁用mod_info，要么确保访问仅限于有效的用户/主机。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10703, 'Microsoft Windows SMTP Incorrect Credentials Authentication Bypass', 'Microsoft Windows 2000 SMTP错误认证漏洞', '高危', 'Microsoft Windows 2000版本中SMTP服务的认证进程存在漏洞。远程攻击者可以使用不正确的证书来提升特权并且进行例如邮件传递的活动。', 'Microsoft has released a patch which addresses this issue: Microsoft Windows 2000 Professional Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp Microsoft Windows 2000 Server SP2 Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp Microsoft Windows 2000 Advanced Server SP1 Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp Microsoft Windows 2000 Server SP1 Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp Microsoft Windows 2000 Advanced Server SP2 Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp Microsoft Windows 2000 Professional SP2 Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp Microsoft Windows 2000 Advanced Server Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp Microsoft Windows 2000 Professional SP1 Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp Microsoft Windows 2000 Server 补丁链接： Microsoft Q302755 http://www.microsoft.com/windows2000/downloads/security/q302755/defaul t.asp', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10704, 'Apache Multiviews Arbitrary Directory Listing', 'Apache Autoindexing模块可能导致泄漏目录列表', '中危', 'Apache的AutoIndex(自动索引)模块会自动对目录进行索引。 如果该目录下存在index.html文件，那么自动索引将显示index.html的内容。 然而，这个模块存在一个可能的问题，如果提交某些特殊命令，可能会泄漏目录 列表，不管index.html文件是否存在。 问题出在/src/modules/standard/mod_autoindex.c ： #define K_NAME \'N\' /* Sort by file name (default) */ #define K_LAST_MOD \'M\' /* Last modification date */ #define K_SIZE \'S\' /* Size (absolute， not as displayed) */ #define K_DESC \'D\' /* Description */ #define D_ASCENDING \'A\' #define D_DESCENDING \'D\'', '解决方法： 建议您在httpd.conf中关闭\"Index\"选项。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10708, 'SSH 3.0.0 Locked Account Remote Authentication Bypass', 'SSH短密码登陆漏洞', '中危', 'Unix系统的SSH Secure Shell 3.0.0版本不能正确地对sshd2守护程序执行密码认证，本地用户可以获得带有短密码字段的账号的使用权，例如在密码字段中使用\"NP\"的锁定账户。', NULL, NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10722, 'LDAP NULL BASE Search Access', 'LDAP NULL BASE 搜索信息泄露漏洞', '中危', 'Windows 活动目录在服务器上，允许未经身份验证 (NULL) 连接来连接到根 DSA 特定条目 （DSE）。这是由设计，以便遵守请求注释 (RFC) 2251年。用户可以使用这些空值连接用户枚举域命名上下文 （NC） 的潜在敏感信息的服务器。这包括域密码策略信息。', '在 LDAP服务器 关闭NULL BASE 请求', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10723, 'LDAP Server NULL Bind Connection Information Disclosure', 'LDAP服务器NULL绑定连接信息泄露', '中危', '远程主机上的LDAP服务器当前配置为使得a 用户可以连接到它而无需身份验证 - 通过“NULL BIND” - 并查询其信息。 虽然允许的查询 可能会受到相当限制，这可能导致披露 攻击者可以发现有用的信息。', '配置服务以禁止NULL BIND。', 'CVE-2002-0643', NULL);
INSERT INTO `scan_suggest_log` VALUES (10736, 'DCE Services Enumeration', 'DCE服务枚举漏洞', '中危', '通过发送一个查询请求到端口映射器(TCP 135或epmapper 管道)可以枚举 Distributed Computing Environment (DCE)服务。利用这些信息可以通过发送一个RPC请求到远程端口/管道，连接并绑定到每个服务。', '根据实际情况限制其他IP地址访问该端口', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10756, 'Apple Mac OS X Find-By-Content .DS_Store Web Directory Listing', '苹果Mac OS X按内容查找。ds_store Web目录列表', '中危', '远程web服务器上的DS_Store文件,这个文件是由MacOS X Finder创建的;它用于记住图标在桌面上的位置，并包含远程目录中显示的文件和目录列表。注意，删除的文件可能仍然存在于.ds_store文件中', '配置你的网络服务器，以防止下载.ds_store文件- Mac OS X用户应该配置他们的工作站，以禁用创建.ds_store文件在网络共享', 'CVE-2001-1446', NULL);
INSERT INTO `scan_suggest_log` VALUES (10759, 'Web Server HTTP Header Internal IP Disclosure', 'Web服务器HTTP头内部IP泄漏', '低危', '这可能会暴露内部IP地址，这些地址通常隐藏或隐藏在网络地址转换（NAT）防火墙或代理服务器后面。Microsoft IIS 4.0在其默认配置中执行此操作时存在已知问题。这还可能影响其他web服务器、web应用程序、web代理、负载平衡器以及与重定向相关的各种错误配置。', '应用供应商建议的配置。', 'CVE-2000-0649', '1');
INSERT INTO `scan_suggest_log` VALUES (10815, 'Web Server Generic XSS', 'Web 服务器通用类 XSS', '中危', 'Web 服务器软件版本较低导致存在XSS漏洞', '升级Web 服务器软件', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10848, 'Oracle 9iAS DMS / JPM Pages Anonymous Access', 'Oracle 9iAS DMS / JPM 页面匿名访问', '中危', 'Oracle 9i Application Server 1.0.2.x版本的默认配置存在漏洞。远程匿名用户可以无认证访问敏感服务，包括可以被用来控制Java进程的Dynamic Monitoring Services(1)dms0、(2)dms/DMSDump、(3)servlet/DMSDump、(4)servlet/Spy、(5)soap/servlet/Spy和(6)dms/AggreSpy;以及Oracle Java Process Manager(7)oprocmgr-status和(8)oprocmgr-service', '编辑http.conf，限制对/dms0的访问', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10851, 'Oracle 9iAS Java Process Manager /oprocmgr-status Anonymous Process Manipulation', 'Oracle 9iAS Java Process Manager /oprocmgr-status 匿名访问操作', '中危', 'Oracle 9i Application Server 1.0.2.x版本的默认配置存在漏洞。远程匿名用户可以无认证访问敏感服务，包括可以被用来控制Java进程的Dynamic Monitoring Services(1)dms0、(2)dms/DMSDump、(3)servlet/DMSDump、(4)servlet/Spy、(5)soap/servlet/Spy和(6)dms/AggreSpy;以及Oracle Java Process Manager(7)oprocmgr-status和(8)oprocmgr-service', '编辑http.conf，限制对/oprocmgr-status的访问', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10852, 'Oracle 9iAS _pages Directory Compiled JSP Source Disclosure', 'Oracle 9iAS OracleJSP泄漏JSP文件信息漏洞', '中危', 'Oracle 9iAS(Application Server)的web服务使用的是Apache Web Server，它提供了多种应用环境，包括SOAP，PL/SQL，XSQL以及JSP。 Oracle 9iAS的OracleJSP环境中存在一个安全问题，允许远程攻击者获取翻译后的JSP页面的源代码。另外一个问题允许攻击者获取globals.jsa文件的内容。 当用户向运行OracleJSP的服务器请求一个JSP页面时，该JSP页面会首先被翻译，然后编译、执行，并将执行结果返回给客户端。在此过程中，三个临时文件会被创建。如果请求的JSP页面为\"foo.jsp\"，那三个临时文件就是： _foo$__jsp_StaticText.class _foo.class _foo.java 它们会被保存在\"/_pages\"目录下。如果foo.jsp保存在子目录\"bar\"下，则上述临时文件会保存在\"/_pages/_bar\"下。由于翻译后的.java文件中包含JSP源代码，而这些文件又都可以直接通过WEB接口访问，攻击者就可能获取一些敏感信息，例如Oracle数据库的用户名和口令。 另外，如果JSP应用程序使用globals.jsa文件来保存全局设置，攻击者也可以直接访问该文件并获取其内容，如果其中包含一些敏感信息，也同样可能导致较严重的安全问题。', '临时解决方法： 如果您不能立刻安装补丁或者升级，建议您采取以下措施以降低威胁： * 在边界防火墙或者主机防火墙上限制不可信用户对Oracle Apache web server 80/TCP端口的访问。 * 编辑$ORACLE_HOME$/apache/apache/conf/httpd.conf文件： 为了阻止访问globals.jsa文件，增加下列语句： Order allow,deny Deny from all 为了阻止访问.java文件，增加下列语句： Order deny,allow Deny from all 如果JSP文件保存在一个别名目录中(例如不是在\"htdocs\"的子目录下)，那么您必须增加下列语句： Order deny,allow Deny from all 上面的\"dirname\"是别名目录的目录名。 厂商补丁： Oracle已经为此漏洞提供了相应补丁程序，建议您随时关注厂商主页以获取相关补丁： http://metalink.oracle.com', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10862, 'Microsoft SQL Server Default Credentials', 'MSSQL Server “sa”账号弱口令', '高危', 'MSSQL数据库使用了弱口令（sa/sa），攻击者可以利用其直接控制整个数据库，并且可以通过数据库获取到整台数据库系统的控制权限。', '修改MSSQL数据库sa用户密码', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10882, 'SSH Protocol Version 1 Session Key Retrieval', 'SSH v1版本会话密钥检索', '中危', '远程SSH守护进程使用SSH版本为1.33或1.5，这些加密协议已不再安全。', '禁用兼容SSH V1的协议。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10885, 'Microsoft Windows SMTP Service Malformed BDAT Request Remote DoS', 'Microsoft SMTP服务畸形命令拒绝服务攻击漏洞(MS02-012)', '中危', 'Windows系统的IIS服务器自带了一个SMTP服务器组件。 Windows 2000和XP Professional SMTP服务在处理命令上存在问题，远程攻击者可能利用此漏洞对服务器程序实施拒绝服务攻击。 Windows 2000和XP Professional SMTP服务处理某些种类的畸形命令时会出错，远程攻击者可以借此对服务器程序进行拒绝服务攻击，需要重启程序才能恢复正常功能。此漏洞不影响Exchange 5.5。', '临时解决方法： 如果您不能立刻安装补丁或者升级，CNNVD建议您采取以下措施以降低威胁： * 对SMTP服务端口进行访问控制，确信只有合法用户能够访问。 厂商补丁： Microsoft --------- Microsoft已经为此发布了一个安全公告(MS02-012)以及相应补丁: MS02-012：Malformed Data Transfer Request can Cause Windows SMTP Service to Fail 链接： http://www.microsoft.com/technet/security/bulletin/MS02-012.asp 补丁下载： Windows 2000 Server, Professional and Advanced Server http://www.microsoft.com/Downloads/Release.asp?ReleaseID=36556 Windows XP Professional: http://www.microsoft.com/Downloads/Release.asp?ReleaseID=36636', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10891, 'X Display Manager Control Protocol (XDMCP) Detection', '远程服务器使用X Display Manager Control Protocol (XDMCP)', '中危', 'XDMCP是远程桌面管理协议，它不是加密的协议，存在中间人攻击漏洞，攻击者可通过监听网络的方式，窃取合法用户的凭据，以及获取用户的键盘输入。', '如果 XDMCP 服务不需要使用，请禁用。 如果需要使用，建议通过主机防火墙限制允许该服务的IP', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10907, 'Microsoft Windows Guest Account Belongs to a Group', 'Microsoft Windows Guest账户属于一个组', '高危', '使用提供的凭证,是能够确定的Guest以外的用户属于组(去掉546)或Guest、“域Guest”(514)清除掉。Guest 应该没有任何更多的用户特权。', '编辑本地或域政策限制的组成员关系Guest账户 禁用Guest账户', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10922, 'CVS (Web-Based) Entries File Information Disclosure', 'CVS（基于Web的）条目文件信息披露', '中危', '远程web服务器允许访问“CVS/Entries”文件，从而在关联的存储库中公开文件名。', '为受影响的web服务器配置权限，以拒绝访问报告的文件以及其他相关文件，如“CVS/Repository”和“CVS/Root”。', NULL, '1');
INSERT INTO `scan_suggest_log` VALUES (10956, 'Microsoft IIS / Site Server codebrws.asp Arbitrary Source Disclosure', 'IIS和网站服务器codebrws.asp示例文件读取任意文件漏洞', '中危', 'IIS和Site Server的codebrws.asp示例文件中存在漏洞。远程攻击者利用该漏洞读取任意文件。', '删除codebrws.asp示例文件', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (10969, 'SNMP Request Cisco Router Information Disclosure', 'SNMP请求Cisco路由器信息公开', '低危', '通过使用OID 1.3.6.1.4.1.9.1发送SNMP请求，可以确定远程CISCO系统的型号。攻击者可以使用此信息获取有关远程主机的更多信息。', '如果您不使用远程主机上的SNMP服务，请禁用该服务，或者筛选发送到此端口的传入UDP数据包。', NULL, '1');
INSERT INTO `scan_suggest_log` VALUES (10993, 'Microsoft ASP.NET Application Tracing trace.axd Information Disclosure', 'Microsoft ASP.NET应用程序跟踪trace.axd信息泄露', '中危', '远程Web服务器的根目录中运行的ASP.NET Web应用程序启用了应用程序跟踪。这允许未经身份验证的远程攻击者查看向服务器发出的最近50个Web请求，包括会话ID值和所请求文件的物理路径等敏感信息。', '在web.config中设置<trace enabled = false>', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11030, 'Apache Chunked Encoding Remote Overflow', 'Apache分块编码远程溢出', '高危', 'Apache Web Server是一款非常流行的开放源码、功能强大的Web服务器程序，由Apache Software Foundation开发和维护。它可以运行在多种操作系统平台下，例如Unix/Linux/BSD系统以及Windows系统。Apache在处理以分块(chunked)方式传输数据的HTTP请求时存在设计漏洞，远程攻击者可能利用此漏洞在某些Apache服务器上以Web服务器进程的权限执行任意指令或进行拒绝服务攻击。', '升级Apache web服务版本至1.3.26 or 2.0.39及以上版本 相关链接： http://httpd.apache.org/info/security_bulletin_20020617.txt http://httpd.apache.org/info/security_bulletin_20020620.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11037, 'Multiple Server Crafted Request WEB-INF Directory Information Disclosure', '多家厂商WEB-INF目录内容远程泄露漏洞', '中危', 'WEB-INF包含一些JAVA类文件和WEB应用程序的配置信息，所以此目录一般是限制访问的，不过多家厂商的servlet引擎对畸形URL请求处理不够正确，远程攻击者可以提交在WEB-INF包含\'.\'字符的URL请求，可导致服务器返回包含在受限目录下的任意文件内容，攻击者可能可以下载应用程序的.java和.class文件，或者访问其他配置文件，利用这些信息攻击者可以进一步对系统进行攻击。', '请根据相应厂家在其官网获取相关补丁包。（ Sybase Enterprise Application Server、Oracle9i Application Server with OC4J、Orion Server、Macromedia/Allaire JRun、HP Application Server、Pramati Application Server和jo! Webserver等）', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11040, 'HTTP Reverse Proxy Detection', 'WebLogic Server和Express HTTP TRACE Credential Theft 漏洞', '中危', 'BEA WebLogic Server和Express 8.1 SP2及其以前的版本，7.0 SP4及其以前的版本， 6.1至SP6，和5.1至SP13版本的默认配置响应HTTP TRACE请求，远程攻击者使用跨站跟踪攻击（XST)盗取信息，该攻击在易受跨站脚本影响的应用进程中。', '禁用HTTP反向代理', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11052, 'BenHur Firewall Source Port 20 ACL Restriction Bypass', 'BenHur防火墙源端口20 ACL限制旁路', '中危', '可以绕过远程主机上的防火墙', '更新到066修复2或重新配置您的防火墙以拒绝来自20端口的任何流量', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11058, 'RPC rusers Remote Information Disclosure', 'rusers用户信息泄露漏洞', '中危', 'RPC rusers 命令可用于生成登录到远程机的用户列表。攻击者可利用该信息进一步攻击。', '如果rusers 服务不需要，应停用。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11071, 'Multiple Web Server Encoded Space (%20) Request ASP Source Disclosure', '多个Web服务器编码空间（%20）请求ASP源公开', '中危', '似乎可以通过向请求添加“%20”来获取远程ASP脚本的源代码。ASP源代码通常包含登录名和密码等敏感信息。这已经在Simple HTTPD（shttpd）和Mono XSP for ASP中报道过。NET和vWebServer。这种类型的请求也可能会影响其他web服务器。', '目前还没有已知的解决方案。', 'CVE-2001-1248', '1');
INSERT INTO `scan_suggest_log` VALUES (11110, 'MS02-045: Microsoft Windows SMB Protocol SMB_COM_TRANSACTION Packet Remote Overflow DoS (326830) (uncredentialed check)', 'Microsoft网络共享器SMB请求远程缓冲区溢出漏洞(MS02-045)', '高危', 'Microsoft Windows操作系统使用Server Message Block (SMB)协议提供开放交叉平台机制，通过网络对文件和打印共享服务的支持，也称为CIFS (Common Internet File System)，目前CIFS在Windows下的实现是运行在TCP/139和TCP/445端口上，并依赖运行在TCP/IP之上的NETBIOS协议是否启用。Microsoft操作系统对畸形的SMB请求处理存在问题，远程攻击者可以利用这个漏洞进行拒绝服务攻击，可能以系统权限执行任意代码。 SMB_COM_TRANSACTION命令允许客户端和服务器端在特定服务器上的特定资源定义特殊的功能，此功能支持不由协议自身定义，而由客户端和服务器端实现。 通过发送特殊构建的包请求NetServerEnum2， NetServerEnum3或者NetShareEnum transaction(事务处理)，攻击者可以对目标机器进行拒绝服务攻击，可能以系统权限执行任意代码，但没有得到证实。攻击者只需在上述transaction的参数域中将\'Max Param Count\'或\'Max Data Count\'字段设置为零即可进行攻击。 利用NetShareEnum transaction(事务处理)来触发这个漏洞，攻击者需要拥有一个合法的帐户，而利用NetServerEnum2和NetServerEnum3 transaction(事务处理)进行触发，则攻击者无需密码匿名即可进行访问。 Windows操作系统默认情况下允许匿名用户进行访问，因此任意用户可以对默认配置的Windows操作系统进行拒绝服务攻击。 成功利用这个漏洞攻击后，操作系统会出现系统崩溃(蓝屏)，并出现如下类似信息： *** STOP: 0x0000001E (0xC0000005， 0x804B818B， 0x00000001， 0x00760065) KMODE_EXCEPTION_NOT_HANDLED *** Address 804B818B base at 80400000， DateStamp 384d9b17 0 ntoskrnl.exe 物理内存被dump，系统重新启动。', '临时解决方法： 如果您不能立刻安装补丁或者升级，建议您采取以下措施以降低威胁： 可以限制匿名用户对Windows 2000P系统的LSA组件的访问，这可以避免匿名用户进行攻击，但无法防止合法用户的攻击。 对于Windows NT和XP，无法通过修改注册表禁止匿名用户访问，您只能暂时关闭共享服务。 如果您并不需要提供网络共享服务，可以完全关闭共享 厂商补丁： Microsoft已经为此发布了一个安全公告(MS02-045)以及相应补丁: MS02-045：Unchecked Buffer in Network Share Provider Can Lead to Denial of Service(Q326830) 链接：http://www.microsoft.com/technet/security/bulletin/MS02-045.asp', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11137, 'Apache < 1.3.27 Multiple Vulnerabilities (DoS, XSS)', 'Apache版本低于1.3.27，存在多种安全漏洞（DOS，XSS）', '高危', 'Apache版本低于1.3.27，可能导致多种安全漏洞。', '升级Apache web服务版本至1.3.27及以上版本 相关链接： http://archives.neohapsis.com/archives/bugtraq/2002-10/0195.html http://archives.neohapsis.com/archives/vulnwatch/2002-q4/0012.html http://archives.neohapsis.com/archives/bugtraq/2002-11/0137.html http://archives.neohapsis.com/archives/vulnwatch/2002-q4/0003.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11138, 'Citrix Published Applications Remote Enumeration', 'Citrix应用程序远程枚举漏洞', '中危', 'Citrix服务受到一个信息泄露漏洞影响，这可能使得攻击者枚举应用程序。', '参考提供的建议来保护受影响的应用程序，相关链接： http://packetstormsecurity.org/files/29932/hackingcitrix.txt.html http://archives.neohapsis.com/archives/bugtraq/2002-09/0330.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11197, 'Multiple Ethernet Driver Frame Padding Information Disclosure (Etherleak)', '多以太网驱动程序帧填充信息泄漏（Etherleak）', '低危', '远程主机使用一个网络设备驱动程序，该驱动程序在以太网帧上填充不同数据包的数据，这些数据包可能来自内核内存、分配给设备驱动程序的系统内存或其网络接口卡上的硬件缓冲区。此信息泄漏漏洞称为“Etherleak”，如果攻击者与受影响的主机位于同一物理子网中，则攻击者可以利用此漏洞从该主机收集敏感信息。', '请与网络设备驱动程序的供应商联系以获得修复。', 'CVE-2003-0001', '1');
INSERT INTO `scan_suggest_log` VALUES (11213, 'HTTP TRACE / TRACK Methods Allowed', 'HTTP TRACE / TRAC方法未禁用', '中危', '主机支持TRACE和/或TRACK方法。 TRACE和TRACK是HTTP用来调试web服务器连接的方法。', '禁用TRACE / TRAC方法，参考如下： 1、IIS下：IIS信息服务－web服务扩展：禁止\"WebDAV； 2、apache下：修改httpd.conf配置文件中\"TraceEnable on\"为\"TraceEnable off\"', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11214, 'MS02-061: Microsoft SQL Server Multiple Vulnerabilities (uncredentialed check)', 'MS02-061: Microsoft SQL Server多个漏洞攻击', '紧急', '远程数据库服务器可能存在多个缓冲区溢出漏洞。', '微软已经发布相关补丁包： http://technet.microsoft.com/en-us/security/bulletin/ms02-061', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11223, 'Oracle 9iAS soapdocs Directory Remote Information Disclosure', 'Oracle 9iAS soapdocs 目录远程信息泄露', '中危', '默认SOAP安装包含的安装脚本，可能访问Oracle 9iAS Application Server\'s SOAP文件目录，这些文件对攻击者来说可以判定那些应用服务程序在使用。', '从Oracle 9iAS \'http.conf\'中删除\'soapdocs\' 别名', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11224, 'Oracle 9iAS XSQLServlet soapConfig.xml Authentication Credentials Disclosure', 'Oracle 9iAS XSQLServlet soapConfig.xml身份认证凭证泄露', '中危', 'Oracle 9iAS包含了两个重要的配置文件叫\"XSQLConfig.xml\"和\"soapConfig.xml\"，它们分别是XSQL和SOAP组件的配置文件，其中存放了一些敏感信息比如数据库服务器主机名、数据库用户名和口令等等。Oracle 9iAS在这两个敏感文件的访问控制上存在问题，导致远程攻击者可能得到其中的信息。Oracle 9iAS对这些配置文件的访问没有做任何认证，任何客户端可以轻易地通过Web虚拟目录访问到这两个文件。', '临时解决方法：如果您不能立刻安装补丁或者升级，Oracle建议您采取以下措施以降低威胁： * 对两个配置文件设置正确的访问权限，禁止匿名用户访问。 厂商补丁：目前厂商还没有提供补丁或者升级程序，建议用户随时关注厂商的主页以获取最新版本： http://metalink.oracle.com/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11226, 'Oracle 9iAS Nonexistent .jsp File Request Error Message Path Disclosure', 'Oracle 9iAS 不存在的 .jsp文件请求错误信息路径泄露漏洞', '中危', 'Oracle 9i应用服务器1.0.2版本存在漏洞。远程攻击者可以借助对不存在.JSP文件的请求获取服务器根目录下文件的物理路径。该漏洞在出错消息中泄露了路径名。', '1、确保虚拟路径的URL不同于实际的目录路径。同时,不要使用< servletzonepath >目录”ApJServMount < servletzonepath > < servletzone > \'来存储数据或文件。 2、升级到Oracle 9 ias 1.1.2.0.0也可解决这个问题。 相关链接： http://www.nessus.org/u?8d439be5 http://www.nessus.org/u?97653726', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11227, 'Oracle 9iAS Default SOAP Configuration Unauthorized Application Deployment', 'Oracle 9iAS SOAP默认配置漏洞', '高危', 'Oracle 9iAS是一种大型的关系数据库系统，由Oracle公司开发和维护。 它支持简单对象访问协议(SOAP)协议，它是一种基于XML的协议，Oracle用它来对数据库服务进行Web管理。Oracle 9iAS 1.0.2.2.1缺省安装并使用了SOAP组件，远程攻击者无须认证即可配置或关闭(deploy/undploy)SOAP的提供者及服务，与其它漏洞结合，攻击者可能进行更进一步的破坏。', '临时解决方法：如果您不能立刻安装补丁或者升级，David Litchfield 建议您采取以下措施以降低威胁： * 如果您不需要SOAP组件，您应该立刻关闭SOAP。 编辑\"$ORACLE_HOME/Apache/Jserv/etc/jserv.conf\"配置文件，删除或者注释掉下面的行： ApJServGroup group2 1 1 $ORACLE_HOME/Apache/Jserv/etc/jservSoap.properties ApJServMount /soap/servlet ajpv12://localhost:8200/soap ApJServMount /dms2 ajpv12://localhost:8200/soap ApJServGroupMount /soap/servlet balance://group2/soap * 如果您需要SOAP，您应该关闭它的自动deploy功能。 编辑\"$ORACLE_HOME/soap/werbapps/soap/WEB-INF/config/soapConfig.xml\"文件，加入以下的行： * 限制访问SOAP deploy/undeploy功能的用户，应该只有管理员和可信用户可以使用此功能。 厂商补丁： 目前厂商已经提供了解决方案以修复这个安全问题，请参看下列厂商安全公告 http://technet.oracle.com/deploy/security/pdf/ias_soap_alert.pdf', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11255, 'default Password (root) for \'root\' Account', 'root账户默认密码“root”', '紧急', '远程主机root账户使用默认密码root,攻击者利用此漏洞可获得远程主机系统的所有权限。', '修改root默认密码，并且配置合理的密码策略', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11267, 'OpenSSL < 0.9.6j / 0.9.7b Multiple Vulnerabilities', 'OpenSSL版本低于0.9.6j/ 0.9.7b，存在多种安全漏洞', '中危', '远程主机的OpenSSL版本已低于0.9.6j/ 0.9.7b，这可能遭受多种安全威胁攻击，例如中间人攻击等', '升级版本至0.9.6j (0.9.7b)及以上版本 相关链接： http://www.openssl.org/news/secadv_20030219.txt http://eprint.iacr.org/2003/052/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11308, 'Microsoft Windows SMTP Service NTLM Null Session Authorization Bypass (uncredentialed check)', 'Microsoft Windows SMTP服务绕过认证漏洞(MS02-011)', '中危', 'Windows系统的IIS服务器自带了一个SMTP服务器组件。 Windows 2000 SMTP服务和Exchange Server 5.5 Internet Mail Connector服务对发信认证的实现上存在漏洞，可以使本地局域网攻击者得到主机的用户级非法访问权限。 IIS的SMTP组件支持SMTP AUTH认证命令，它支持NTLM认证选项，目的是允许用户用NTLM认证方式认证自己。然而NTLM支持空会话方式，一个匿名用户可能用这种方式\"认证\"自己，当这种认证完成以后，用户就被准许转发邮件了。Exchange 2000被证实不受此漏洞影响。 <*链接：http://archives.neohapsis.com/archives/bugtraq/2002-02/0372.html http://www.microsoft.com/technet/security/bulletin/MS02-011.asp *>', '临时解决方法： 如果您不能立刻安装补丁或者升级，CNNVD建议您采取以下措施以降低威胁： * 如果您不需要提供SMTP服务，关闭此服务。 * 对SMTP服务端口进行访问控制，确信只有合法用户能够访问。 厂商补丁： Microsoft --------- Microsoft已经为此发布了一个安全公告(MS02-011)以及相应补丁: MS02-011：Authentication Flaw Could Allow Unauthorized Users To Authenticate To SMTP Service 链接： http://www.microsoft.com/technet/security/bulletin/MS02-011.asp 补丁下载： Microsoft Windows 2000 Server, Professional and Advanced Server: http://www.microsoft.com/Downloads/Release.asp?ReleaseID=_36556 Exchange Server 5.5: http://www.microsoft.com/Downloads/Release.asp?ReleaseID=33423', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11322, 'MS02-035: MS SQL Installation may leave passwords on system (263968)', 'Microsoft MS-SQL Server安装过程中明文缓存口令漏洞(MS02-035)', '中危', 'Microsoft SQL Server安装过程中存在漏洞，本地攻击者可能利用此问题得到访问数据库的认证信息。', 'Microsoft已经为此发布了一个安全公告(MS02-035)以及相应补丁: MS02-035：SQL Server Installation Process May Leave Passwords on System (Q263968) 链接： http://www.microsoft.com/technet/security/bulletin/MS02-035.asp 补丁下载： * Microsoft SQL 7, MSDE 1.0, and Microsoft SQL Server 2000: http://www.microsoft.com/Downloads/Release.asp?ReleaseID=40205', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11356, 'NFS Exported Share Information Disclosure', 'NFS 共享信息泄露漏洞', '中危', '远程攻击者可以装载Ultrix或OSF中的NFS文件系统，即使它在访问列表中被否认。', '配置NFS，限制为仅允许已通过授权的远程主机可以进行远程挂载NFS共享。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11411, 'Backup Files Disclosure', '备份文件的泄露', '中危', '通过附加各种后缀(即:.old, .bak, ~等等…)的名字 远程主机上的各种文件,可以检索他们的内容,这可能导致泄露敏感信息。', '确保文件不包含任何敏感信息,例如凭证来连接到数据库,删除或保护 不应该访问的文件。 设置备份文件的访问权限', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11444, 'PHP Mail Function Header Spoofing', 'PHP mail函数注入ASCII控制字符欺骗mail头信息漏洞', '中危', 'PHP是一种流行的WEB服务器端编程语言，它功能强大，简单易用，在很多Unix操作系统默认都安装了PHP， 它也可以在Windows系统下运行。 PHP的mail函数没有很好的过滤用户的输入，远程攻击者可能利用此漏洞修改邮件头信息。 PHP的mail函数没有很好的过滤用户输入的邮件字符串变量，导致用户输入的ASCII控制字符可以修改邮件信息，包括邮件头信息。', '更新到最新版本php', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11452, 'Oracle 9iAS PL/SQL Gateway Web Admin Interface Null Authentication', 'Oracle 9iAS Apache PL/SQL模块Web管理页面访问控制漏洞', '高危', 'Oracle 9iAS的Web服务部分是由增加了PL/SQL支持模块的Apache服务器提供。主机上相关的用于管理的Web页面可以使用户修改数据库访问描述符和缓存设置。 Oracle 9iAS的Web管理页面访问控制上存在问题，远程攻击者可能利用此漏洞更改系统设置，造成拒绝服务攻击。 默认安装情况下，对于那些管理页面的访问不需要认证，这就使任意远程攻击者可以通过访问那些管理页面执行某些操作，更改PL/SQL应用程序或者使合法用户不能访问服务器程序。', '临时解决方法： 如果您不能立刻安装补丁或者升级，建议您采取以下措施以降低威胁： * 在/Apache/modplsql/cfg/wdbsvr.app文件中对可以访问的用户名等相关信息进行设置，确信只有合法用户才能访问。 厂商补丁： 目前厂商已经提供了解决方案以修复这个安全问题，请到厂商的主页下载： http://otn.oracle.com/deploy/security/pdf/ias_modplsql_alert.pdf', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11454, 'Microsoft Windows Administrator Default Password Detection (W32/Deloder Worm Susceptibility)', '未修改Administrator 默认密码', '紧急', 'W32/Deloder 是一个蠕虫，可使用已建立的默认密码，尝试连接远程共享，在共享主机上上传非法数据等。', '修改Administrator的密码为强密码，至少包含数字、字母、特殊字符，长度为8位以上。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11467, 'J Walk Application Server Encoded Directory Traversal Arbitrary File Access', 'J Walk应用服务器文件泄漏漏洞', '中危', 'Seagull软件系统J Walk应用服务器3.2C9版本和其他3.3c4之前的版本存在目录遍历漏洞。远程攻击者可以借助URL的 \".%252e\"（编码的点 点）读取任意文件。', '更新升级JWalk 至版本 3.3c4以上', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11468, 'PHP socket_iovec_alloc() Function Overflow', 'PHP emalloc()未明的整数溢出内存腐败漏洞', '中危', 'PHP 4.3.2之前版本的emalloc()函数存在整数符号错误漏洞。远程攻击者借助如(1)socket_recv，(2)socket_recvfrom，和可能其他函数的负参数导致服务拒绝（内存消耗）且可能执行任意代码。', '升级php最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11486, 'WebLogic Servlets Multiple Vulnerabilities', 'WebLogic Servlets SP包未安装', '高危', 'WebLogic Servlets SP包未安装，存在多个漏洞未修复', '升级补丁包： - Apply Service Pack 2 Rolling Patch 3 on WebLogic 6.0 - Apply Service Pack 4 on WebLogic 6.1 - Apply Service Pack 2 on WebLogic 7.0 or 7.0.0.1', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11494, 'l2tpd Malformed Data Remote DoS', 'l2tpd畸形数据远程DoS', '中危', '远程主机正在运行l2tpd，一个网络隧道应用程序。该 安装版本容易受到拒绝服务攻击。 攻击者可能会使用此缺陷来禁用VPN，并阻止合作伙伴/ 员工连接到它。', '删除软件，因为不再支持。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11535, 'SheerDNS < 1.0.1 Multiple Vulnerabilities', 'SheerDNS多个漏洞', '中危', '远程服务器似乎运行SheerDNS 1.0.0或以上。 这个版本很容易受到几个缺陷允许: ——一个远程攻击者读取某些文件使用预定义的名称 (PTR,CNAME,…) ——本地攻击者读取任意文件的第一行 特权的DNS服务器(通常是根) ——本地攻击者执行任意代码通过缓冲区溢出', '升级到SheerDNS 1.0.1或禁用此服务', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11539, 'Default Password for FTP \'admin\' Account', 'FTP默认账户admin存在默认密码', '紧急', '远程的FTP服务的admin账户存在默认密码password，攻击者可远程获得主机权限，发起更深一层的攻击。如果远程主机是NB1300系列路由器，这可能运行攻击者偷到WAN证书，甚至重新设置路由信息。', '更换admin账户默认密码。 相关链接： http://archives.neohapsis.com/archives/bugtraq/2003-04/0202.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11563, 'Oracle Net Services CREATE DATABASE LINK Query Overflow', 'Oracle网络服务 CREATE DATABASE LINK查询溢出漏洞', '高危', 'Oracle是一款企业级数据库服务程序，Oracle的数据库连接功能对参数缺少正确的边界缓冲区检查，远程攻击者可以利用这个漏洞进行典型缓冲区溢出攻击，可能以Oralce进程权限在系统上执行任意指令，Oracle提供数据库连接功能，允许从其他数据库服务器查询当前数据库。提供超长的参数给\'CREATE DATABASE LINK\'查询的连接字符串(默认情况下，\'CREATE DATABASE LINK\'权限只分配给连接用途，多数情况下大多数帐户拥有此权限，权限可比SCOTT和ADAMS还低。)： CREATE DATABASE LINK ngss CONNECT TO hr IDENTIFIED BY hr USING \'longstring\' 通过建立特殊的数据库连接然后执行如下命令： select * from table@ngss 就可以触发缓冲区溢出，覆盖堆栈返回地址。精心构建提交数据可以导致以Oralce进程权限在系统上执行任意指令。 不过厂商认为，除非Oracle数据库直接连接到Internet，否则远程攻击不太可能。', '使用厂家提供的补丁包进行修复。 相关链接： http://www.nessus.org/u?6719c919', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11583, 'Microsoft Windows shlwapi.dll Malformed HTML Tag Handling Null Pointer DoS', 'Windows shlwapi.dll 拒绝服务漏洞', '中危', '旧版本的shlwapi.dll文件在处理HTML form时，存在拒绝服务攻击漏洞。', '更新到最新版本的shlwapi.dll文件', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11606, 'WebLogic Crafted GET Request Hostname Disclosure', 'WebLogic 主机名泄露漏洞', '中危', '当WebLogic 服务器处理请求生成一个重定向时，WebLogic 对公开其NetBIOS 主机名。', '升级 WebLogic 至最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11628, 'WebLogic SSL Certificate Chain User Spoofing', 'WebLogic SSL 证书链欺骗漏洞', '中危', 'WebLogic 服务器对自签名证书验证存在漏洞，攻击者可通过自签名证书来伪造其他用户，从而进行中间人攻击。', '升级 WebLogic 至最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11638, 'Microsoft BizTalk Server Multiple Remote Vulnerabilities', '微软BizTalk Server存在多个远程安全漏洞', '高危', '当前BizTalk Server的服务端口存在以下远程安全漏洞，可允许攻击者通过SQL注入或者执行任意代码来进行攻击： Microsoft BizTalk Server 2002 HTTP接收远程缓冲区溢出漏洞(MS03-016) Microsoft BizTalk Server DTA接口SQL注入漏洞(MS03-016)', 'Microsoft已经为此发布了一个安全公告（MS12-020）以及相应补丁: 链接： http://technet.microsoft.com/en-us/security/bulletin/ms03-016', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11658, 'Sun ONE Application Server Upper Case Request JSP Source Disclosure', 'Sun ONE Application Server源泄露漏洞', '中危', 'Windows 2000/XP平台下的Sun ONE Application Server 7.0存在漏洞。远程攻击者可以借助一个用大写“.JSP”扩展名代替小写.jsp的请求获得JSP源代码。', '补丁下载地址如下： http://wwws.sun.com/software/download/app_servers.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11714, 'Nonexistent Page (404) Physical Path Disclosure', '不存在的页面 (404) 导致物理路径泄露', '中危', '不存在的页面 (404) 导致物理路径泄露', '修改默认错误页面，以免返回信息中包含敏感信息', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11788, 'Apache 2.0.x < 2.0.47 Multiple Vulnerabilities (DoS, Encryption)', 'https://www.tenable.com/plugins/nessus/11788', '中危', '远程主机运行2.0.47之前Apache 2.x的版本。因此，它受到多个漏洞的影响： - 当SSLCipherSuite指令用于升级密码套件时可能会出现问题，这可能导致使用较弱的密码套件而不是升级的密码套件。', '升级到Apache Web服务器版本2.0.47或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11793, 'Apache < 1.3.28 Multiple Vulnerabilities (DoS, ID)', 'Apache版本低于1.3.28，存在多种安全漏洞（Oos，ID）', '高危', 'Apache HTTP服务器是流行的开源WEB服务器程序，可使用在Unix和Windows操作系统下。 Apache HTTP 1.3.28修补了多个漏洞，这些漏洞包括拒绝服务攻击、文件描述符泄露、日志记录失败等问题。', '临时解决方法： 如果您不能立刻安装补丁或者升级，建议您采取以下措施以降低威胁： * 在CustomLog或ErrorLog中禁用rotatelog工具。 厂商补丁： Apache Software Foundation -------------------------- 目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载： Apache Software Foundation Upgrade Apache httpd 1.3.28 http://httpd.apache.org/download.cgi', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11808, 'MS03-026: Microsoft RPC Interface Buffer Overrun (823980) (uncredentialed check)', 'MS03-026: Microsoft Windows DCOM RPC接口长主机名远程缓冲区溢出漏洞', '高危', 'Remote Procedure Call (RPC)是Windows操作系统使用的一种远程过程调用协议，RPC提供进程间交互通信机制，允许在某台计算机上运行的程序无缝地在远程系统上执行代码。协议本身源自OSF RPC协议，但增加了Microsoft特定的扩展。 Microsoft的RPC部分在通过TCP/IP处理信息交换时存在问题，远程攻击者可以利用这个漏洞以本地系统权限在系统上执行任意指令。 此漏洞是由于不正确处理畸形消息所致，漏洞影响使用RPC的DCOM接口。此接口处理由客户端机器发送给服务器的DCOM对象激活请求(如UNC路径)。攻击者成功利用此漏洞可以以本地系统权限执行任意指令。攻击者可以在系统上执行任意操作，如安装程序、查看或更改、删除数据或建立系统管理员权限的帐户。 要利用这个漏洞，攻击者需要发送特殊形式的请求到远程机器上的135端口。', 'Microsoft已经为此发布了一个安全公告(MS03-026)以及相应补丁: MS03-026：Buffer Overrun In RPC Interface Could Allow Code Execution(Q823980) 链接： http://www.microsoft.com/technet/security/bulletin/MS03-026.asp', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11835, 'MS03-039: Microsoft RPC Interface Buffer Overrun (824146) (uncredentialed check)', 'MS03-039：微软RPC接口缓冲区溢出', '紧急', '远程主机可执行shellcode代码，远程主机运行的Windows RPC接口版本存在漏洞,这可能允许攻击者执行任意代码,从而获得系统权限。', '微软已经发布了Windows NT补丁、2000、XP和2003系统的相关补丁包： http://technet.microsoft.com/en-us/security/bulletin/ms03-039', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11850, 'PHP < 4.3.3 Multiple Vulnerabilities', 'PHP wordwrap()远程堆破坏漏洞', '高危', 'HP语言中的wordwrap()函数对用户输入缺少正确缓冲区边界检查，远程攻击者可以利用这个漏洞进行基于堆的溢出攻击，可能以Web服务进程权限在系统上执行任意指令。 攻击者提供超长参数wordwrap()函数，可以覆盖一个字节的内存信息，通过重定向程序流程到恶意指令，可以导致以Web服务进程权限在系统上执行任意指令。', '升级到php最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11852, 'MTA Open Mail Relaying Allowed (thorough test)', 'SMTP Server 提权漏洞', '高危', 'SMTP Server是一款强大易用的SMTP/POP3邮件服务器工具 Host的SMTP Server组件中存在提权漏洞。攻击者可利用该漏洞获取提升的权限，影响可用性。', '厂商暂未发布修复措施解决此安全问题，建议使用此软件的用户随时关注厂商主页或参考网址以获取解决办法： https://opensource.com/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11853, 'Apache 2.0.x < 2.0.48 Multiple Vulnerabilities (OF, Info Disc.)', 'Apache 2.0.x <2.0.48多个漏洞（OF，Info Disc。）', '紧急', '远程主机在2.0.48之前运行Apache 2.0.x的版本。因此，它受多个漏洞的影响： - mod_rewrite和mod_alias模块无法处理包含超过9次捕获的正则表达式，从而导致缓冲区溢出。 - 由于错误处理CGI重定向路径而导致mod_cgid模块中可能出现漏洞。这可能导致Apache将CGI程序的输出发送到错误的客户端。', '升级到Apache Web服务器版本2.0.48或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11890, 'MS03-043: Buffer Overrun in Messenger Service (828035) (uncredentialed check)', 'MS03-043: Messenger Service存在缓冲区溢出漏洞', '紧急', 'Messenger Service存在一个安全漏洞可使受影响的系统上执行任意代码。成功利用此漏洞在本地系统权限上执行任意代码的攻击者，将可能导致Messenger Service终止。 禁用Messenger Service将防止攻击的可能性。', '微软已发布 Windows NT, 2000, XP and 2003平台的相关版本： http://technet.microsoft.com/en-us/security/bulletin/ms03-043', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (11915, 'Apache < 1.3.29 Multiple Modules Local Overflow', 'Apache版本低于1.3.29，多种模块存在缓冲区溢出漏洞', '高危', 'Apache是一款广泛使用的开放源代码WEB服务程序。Apache的mod_alias和mod_rewrite模块中存在缓冲区溢出漏洞，可能允许本地攻击者在有漏洞的主机上执行任意代码。如果正则表达式配置了多于9个捕获括号的话，就可能导致mod_alias和mod_rewrite模块出现缓冲区溢出。攻击者可以通过创建特制的配置文件导致拒绝服务(崩溃)或执行任意代码。', '目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载： http://httpd.apache.org/download.cgi', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12037, 'Serv-U SITE CHMOD Command Multiple Vulnerabilities', 'Serv-U FTP服务器SITE CHMOD命令超长文件名远程溢出漏洞', '高危', 'Serv-U是一个Windows平台下使用非常广泛的FTP服务器软件。 Serv-U在处理\"site chmod\"命令的时候，如果后面文件名参数过长将导致缓冲区溢出，远程攻击者可以利用这个漏洞以Serv-U进程的权限执行任意指令。 Serv-U在给不存在的文件执行chmod命令的时候，会给用户返回该文件或目录不存在的信息，这个信息字串使用类似如下的代码创建： sprintf(dst， \"', '厂商补丁： RhinoSoft --------- 目前厂商已经在5.0及以上版本的软件中修复了这个安全问题，请到厂商的主页下载： http://www.serv-u.com/: No such file or directory.\"， filename); 变量dst的长度是256字节，如果发送超长的filename，Serv-U将崩溃。 要成功利用这个漏洞，必须要有一个Serv-U的登陆账号并且要有一个可写的目录。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12047, 'Oracle Database 9i Multiple Functions Local Overflow', 'Oracle数据库9i多个函数本地溢出漏洞', '紧急', 'Oracle在处理部分参数和函数时缺少充分缓冲区边界检查，远程攻击者可以利用这个漏洞进行缓冲区溢出攻击，可能以数据库进程权限执行任意指令。', '厂商补丁： Oracle 9i Database Release 2, version 9.2.0.3已经修正此漏洞，要更多Oracle安全补丁信息，请访问Oracle Metalink site： http://metalink.oracle.com', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12054, 'MS04-007: ASN.1 Vulnerability Could Allow Code Execution (828028) (uncredentialed check) (NTLM)', 'MS04-007: Microsoft ASN.1库远程代码可执行漏洞', '紧急', '在Microsoft ASN.1库中存在的安全漏洞可能允许在受影响系统中执行代码。这个漏洞的起因是Microsoft ASN.1库中未检查的缓冲区，这个缓冲区可能导致缓冲区溢出。成功利用这个缓冲区溢出漏洞的攻击者可能在受影响系统中以系统权限执行任意代码。然后攻击者就可以在系统中采取任何行为，包括安装程序，浏览数据，更改数据，删除数据，或以完全权限创建新帐号。', 'Microsoft已为Windows NT, 2000, XP, and 2003平台发布补丁修复包，相关链接： http://technet.microsoft.com/en-us/security/bulletin/ms04-007', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12065, 'ASN.1 Multiple Integer Overflows (SMTP check)', 'ASN.1多个整数溢出漏洞（SMTP检测）', '紧急', '远程windows主机的ASN.1库，存在多个整数溢出漏洞，这些问题可能会导致出现基于堆的缓冲区溢出。远程攻击者可以利用这些问题来执行任意代码，通过发送一个畸形SMTP授权包，检测远程主机是否修复。', 'Microsoft已为Windows NT, 2000, XP, and 2003发布一系列补丁包。 相关链接： http://technet.microsoft.com/en-us/security/bulletin/ms04-007', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12067, 'Oracle Multiple Products SOAP Message Crafted DTD Remote DoS', 'Oracle 9i应用/数据库服务器SOAP XML DTD拒绝服务攻击漏洞', '中危', 'Oracle Database是一款商业性质大型数据库系统，Oracle 9i应用服务器基于Apache Web服务器，支持SOAP、PL/SQL、XSQL、JSP等环境。Oracle 9i应用/数据库服务器在处理部分SOAP消息时存在问题，远程攻击者可以利用这个漏洞对服务器进行拒绝服务攻击。攻击者可以构建XML中包含特殊的数据类型定义(DTDs)SOAP消息，Oracle 9i应用/数据库服务器处理时会产生错误，导致拒绝服务。目前没有详细漏洞细节提供。', '厂商补丁： Oracle已经为此发布了一个安全公告(OracleSA#65)以及相应补丁: OracleSA#65：Security Vulnerability in Oracle9i Application and Database Servers 链接： http://otn.oracle.com/deploy/security/pdf/2004alert65.pdf 补丁下载： http://metalink.oracle.com/metalink/plsql/ml2_documents.showDocument?p_database_id=NOT&p_id=259556.1', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12080, 'Serv-U MDTM Command Overflow', 'Serv-U FTP服务器MDTM命令远程缓冲区溢出漏洞', '高危', 'Serv-U是一个Windows平台下使用非常广泛的FTP服务器软件。 Serv-U在处理\"MDTM\"命令的参数时缺少正确的缓冲区边界检查，远程攻击者可以利用这个漏洞对FTP服务程序进行缓冲区溢出攻击，可能以FTP进程权限在系统上执行任意指令。 Serv-U提供FTP命令\"MDTM\"用于用户更改文件时间，当用户成功登录系统，并发送畸形超长的时区数据作为命令参数，可触发缓冲区溢出，精心构建参数数据可能以FTP进程权限在系统上执行任意指令。 利用此漏洞需要用户拥有合法帐户登录到系统，但不需要写及其他权限。', '厂商补丁： RhinoSoft --------- 目前厂商已经在最新版本的软件中修复了这个安全问题，请到厂商的主页下载： http://www.serv-u.com/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12085, 'Apache Tomcat servlet/JSP container default files', 'Apache tomcat默认文件未删除', '中危', 'Apache Tomcat的 servlet / JSP容器中安装有示例JSP和Servlet文件。应该删除这些文件，因为它们可以帮助 攻击者发现Tomcat或者远程主机的安装信息。而且他们本身可能也存在安全弱点，比如：跨站点脚本问题。', '确认以下目录的文件与业务系统是否无关 /examples/ /docs/ 若不需要些文件，建议移除', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12110, 'OpenSSL < 0.9.6m / 0.9.7d Multiple Remote DoS', 'OpenSSL版本低于0.9.6m / 0.9.7d，存在多种远程Dos攻击', '中危', '根据主机的OpenSSL版本信息，可能导致攻击者对远程主机发起拒绝服务攻击', '升级版本至0.9.6m / 0.9.7d 及以上版本 相关链接： http://www.openssl.org/news/secadv_20040317.txt http://archives.neohapsis.com/archives/bugtraq/2004-03/0165.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12112, 'Oracle 9iAS iSQLplus XSS', 'Oracle 9iAS iSQLplus XSS漏洞', '中危', '通过用户名和密码参数，Oracle9i iSQLplus登录页面允许注入HTML和JavaScript代码，远程主机正在运行的 Oracle9i \'isqlplus\' CGI 版本存在跨站脚本攻击漏洞，攻击者可以利用这个漏洞窃取合法用户cookie。', '参考厂商发布的补丁包进行修复。 相关链接： http://www.securitytracker.com/alerts/2004/Jan/1008838.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12209, 'MS04-011: Security Update for Microsoft Windows (835732) (uncredentialed check)', 'MS04-011: 微软安全更新漏洞', '紧急', 'Windows版本包含的LSASS函数\'DsRolerUpgradeDownlevelServer\'可能运行攻击者中远程主机上使用系统权限执行shellcode代码。 一系列的蠕虫包括震荡波疯狂地利用此漏洞。', '微软已发布 Windows NT, 2000, XP and 2003平台的相关版本： http://technet.microsoft.com/en-us/security/bulletin/ms04-011', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12217, 'DNS Server Cache Snooping Remote Information Disclosure', 'DNS缓存窥探信息泄露漏洞', '中危', 'DNS缓存窥探是确定某一个资源记录是否存在于一个特定的DNS缓存中的过程。通过这个过程攻击者可以得到一些信息，例如解析器处理了哪些域名查询，攻击者可以了解到最近哪些主机被访问了，从而寻找可攻击对象。', '根据相应DNS软件，安装最新安全补丁', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12218, 'mDNS Detection (Remote Network)', 'mDNS检测（远程网络）', '中危', '远程服务理解Bonjour协议（也称为ZeroConf或mdns）,它允许任何人发现远程主机的信息，例如操作系统类型及确切的版本，主机名及主机正在运行的服务列表。', '如果需要的话，禁用UDP5353端口号，过滤流进的数据流。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12225, 'Web Server Reverse Proxy Detection', 'Web服务器反向代理检测', '中危', '远程web服务器似乎允许任何匿名用户使用它作为一个反向代理。', '禁用或限制访问反向代理。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12249, 'IBM Lotus Domino ?ReadDesign Request Design Element Disclosure', 'IBM Lotus Domino请求设计元素泄露', '中危', '远程web服务运行攻击者通过发送特殊伪造的HTTP请求包给Lotus Domino服务，从而浏览设计元素的XML列表，相关链接：http://[target]/names.nsf/view?ReadDesign', '管理员可通过设计一个服务重定向文件，这文件可限制进入的带有“ReadDesign”标签的URLs，并转向一个当前错误页面。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12255, 'mod_ssl ssl_util_uuencode_binary Remote Overflow', 'Apache Mod_SSL SSL_Util_UUEncode_Binary堆栈缓冲区溢出漏洞', '高危', 'Mod_SSL是Apache服务器上的SSL实现，用来为Apache Web服务器提供加密支持。Apache mod_ssl 2.8.17及其以前版本的实现中模块ssl_util.c的‘ssl_util_uuencode_binary’函数存在问题，远程攻击者可以利用使用该函数的ssl_engine_kernel.c模块进行拒绝服务攻击或可能以WEB进程执行任意指令。', '升级Apache版本至2.8.18 (Apache 1.3)或者2.0.50.', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12280, 'Apache < 1.3.31 / 2.0.49 Socket Connection Blocking Race Condition DoS', 'Apache版本低于1.3.31 / 2.0.49，存在拒绝服务攻击漏洞', '中危', '运行在远程主机上的Apache版本低于1.3.31 / 2.0.49，这些版本容易受到拒绝服务攻击，攻击者可以通过一个极少使用的端口连接侦探SOCKET，从而阻断其它新的连接。这种问题可影响一些AIX Solaris和Tru64系统版本，不会影响 FreeBSD 或Linux', '升级Apache版本至2.0.49 or 1.3.31。 相关链接： http://archives.neohapsis.com/archives/bugtraq/2004-03/0192.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (12293, 'Apache 2.x < 2.0.50 Multiple Remote DoS', 'Apache 2.x <2.0.50多个远程DoS', '中危', '远程主机运行的是2.0.x之前的Apache 2.x版本。因此，它受到拒绝服务漏洞的影响，该漏洞可以通过发送特制的HTTP请求来触发，从而导致消耗任意数量的内存。在具有4GB以上虚拟内存的64位系统上，这可能会导致基于堆的缓冲区溢出。 mod_ssl的\'ssl_io_filter_cleanup\'函数中还存在拒绝服务漏洞。通过SSL向易受攻击的服务器发送请求并在服务器发送响应之前关闭连接，攻击者可能会导致内存冲突，从而导致服务器崩溃。', '升级到Apache 2.0.50或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (13650, 'PHP < 4.3.8 Multiple Vulnerabilities', 'PHP执行任意代码漏洞', '中危', 'PHP 4.x到 4.3.7版本以及5.x 到5.0.0RC3版本的memory_limit函数存在漏洞。在例如当启用register_globals条件下时，远程攻击者在zend_hash_init函数的执行过程中触发memory_limit中止以及在关键数据结构初始化完成之前覆盖HashTable析构函数指针来执行任意代码。', '升级到PHP 4.3.8h或最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (13651, 'Apache mod_ssl ssl_engine_log.c mod_proxy Hook Function Remote Format String', 'Apache mod_ssl ssl_engine_log.c mod_proxy Hook Function远程格式化字符串漏洞', '高危', 'mod_ssl版本低于2.8.19，远程主机的日志函数模块存在格式化字符串，使得攻击者可以执行任意代码。', '升级 mod_ssl版本至2.8.19及以上 相关链接： http://marc.info/?l=apache-modssl&m=109001100906749&w=2 http://marc.info/?l=bugtraq&m=109005001205991&w=2', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (13852, 'MS04-022: Microsoft Windows Task Scheduler Remote Overflow (841873) (uncredentialed check)', 'MS04-022：Task Scheduler 远程溢出漏洞', '紧急', '微软 Task Scheduler在处理应用程序文件名验证时存在问题，远程攻击者可以利用这个漏洞以系统权限在系统上执行任意指令', '升级补丁MS04-022 http://technet.microsoft.com/zh-cn/security/bulletin/ms04-022', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (14343, 'MySQL < 4.0.21 mysqlhotcopy Insecure Temporary File Creation', 'MySQL 版本低于 4.0.21', '中危', '使用mysql-server包scp类函数的mysql 4.0.20及其早期版本中mysqlhotcopy脚本存在漏洞。本地用户可以借助对临时文件的符号链接攻击覆盖任意文件。', '升级mysql版本至4.0.21及以上版本。 相关链接： http://www.mysql.com/downloads/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (14641, 'Oracle Database Multiple Remote Vulnerabilities', 'Oracle 数据库多个远程安全漏洞', '高危', '根据远程主机的Oracle数据库版本，可能存在远程指令执行漏洞，允许攻击者执行SQL语句，这些语句可能有可执行指令权限。', '根据厂商提供的补丁包进行修复。 相关链接： http://www.nessus.org/u?e1d0c17a', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (14722, 'WebLogic < 8.1 SP3 Multiple Vulnerabilities', 'WebLogic 版本低于 8.1 SP3', '高危', 'Apache版本较低，导致存在多处漏洞。', '升级WebLogic 版本到8.1 SP3 以上', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (14748, 'Apache 2.0.x < 2.0.51 Multiple Vulnerabilities (OF, DoS)', 'Apache Web Server配置文件环境变量本地缓冲区溢出漏洞', '中危', 'Apache是一款开放源代码WEB服务程序。 Apache在处理配置文件环境变量时存在缓冲区溢出，本地攻击者可以利用这个漏洞以Apache进程权限执行任意指令。 问题存在于当扩展.htaccess或httpd.conf文件中的${ENVVAR}结构时，server/util.c文件中的ap_resolve_env()函数从环境变量拷贝数据到使用strcat(3)的tmp字符数组时缺少正确边界缓冲区检查，可导致缓冲区溢出，精心构建提交数据可能以Apache进程权限执行任意指令。', 'Apache 2.0.51及以上版本已经修正此问题 厂商补丁： http://httpd.apache.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (14803, 'Apache <= 2.0.51 Satisfy Directive Access Control Bypass', 'Apache Satisfy命令访问控制绕过漏洞', '高危', 'Apache是一款开放源代码WEB服务程序。 Apache Web Server中存在访问控制绕过漏洞，起因是在合并Satisfy命令时的未明错误。远程攻击者可以利用这个漏洞绕过访问控制，非授权访问受限的资源。', '升级版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15436, 'PHP php_variables.c Multiple Variable Open Bracket Memory Disclosure', '远程服务器受到信息披露的影响漏洞', '中危', '远程主机运行的版本是早于5.0.2的PHP版本 或4.39。 该软件的远程版本受到内存泄漏的影响PHP_Variables中的漏洞。 攻击者可能利用这个缺陷远程读取httpd进程的内存部分远程主机', '升级到PHP 5.0.2或4.3.9', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15449, 'MySQL < 3.23.59 / 4.0.21 Multiple Vulnerabilities', 'MySQL 版本低于 3.23.59 / 4.0.21', '高危', 'MySQL是一款开放源代码数据库系统。 MySQL的多个线程发送\'alter\'命令存在问题，远程攻击者可以利用这个漏洞对数据库服务程序进行拒绝服务攻击。 多个线程针对\'merge\'表发送\'alter\'命令修改\'union\'，可导致目标服务程序崩溃。', '升级mysql版本至3.23.59 / 4.0.21及以上版本。 相关链接： http://www.mysql.com/downloads/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15555, 'Apache mod_proxy Content-Length Overflow', 'Apache mod_proxy远程缓冲区溢出漏洞', '紧急', 'mod_proxy是一个可在Apache使用的代理模块。mod_proxy在处理负的Content-Length值时存在问题，远程攻击者可以利用这个漏洞进行缓冲区溢出攻击，可能以Apache进程权限在系统上执行任意指令。 连接远程服务器使其返回非法(负值)的Content-Length，可使Mod_proxy触发基于整数的溢出，会导致Apache子进程崩溃。问题存在于proxy_util.c中： n = ap_bread(f， buf， MIN((int)buf_size，(int)(len - total_bytes_rcvd)));这里len可为负值。在旧的OpenBSD/FreeBSD系统中可利用memcpy的反向拷贝实现来执行任意指令。新的BSD系统中由于攻击者可以控制3个任意字节也能用于执行任意指令。如果define AP_ENABLE_EXCEPTION_HOOK启用的情况下，可能在任何平台都可被利用。', '升级Apache版本至1.3.32及以上版本 相关链接： http://archives.neohapsis.com/archives/fulldisclosure/2004-06/0276.html http://archives.neohapsis.com/archives/fulldisclosure/2004-06/0279.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15753, 'Multiple Vendor DNS Response Flooding Denial Of Service', '多个供应商DNS响应泛滥拒绝服务漏洞', '中危', 'DNS协议的多种实现方式存在漏洞，包括：(1)如Posadis所使用的Poslib 1.0.2-1及其以前的版本，(2) 固件3.13以前版本中的AxisNetwork产品，和(3)Men & Mice Suite 2.2.3以前的2.2x版本和3.5.2以前的3.5.x版本，远程攻击者借助(a)具有本地主机的DNS查询包作为一个欺骗性的源地址或者(b)触发一个响应包的应答包，从而触发一个通讯环导致服务拒绝（CPU和网络带宽消耗）。', '解决办法： http://cve.scap.org.cn/cve-2004-0789.html http://www.securityfocus.com/bid/11642/info http://www.cnnvd.org.cn/vulnerability/show/cv_cnnvdid/CNNVD-200412-718', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15767, 'Danware NetOp Host HELO Request Remote Information Disclosure', 'Danware NetOp Host HELO 请求远程信息泄露', '中危', 'Danware NetOp Host和Guest产品提供各种操作系统的远程控制功能，对交换数据进行验证和加密。 Danware NetOp Host在交换初始化阶段通信处理存在问题，远程攻击者可以利用这个漏洞获得部分敏感信息。 Danware NetOp Host和guest产品使用多种标准协议封装数据交换，虽然验证和数据等交换进行了加密，但是早期的会话初始化过程(在验证和加密之前)的通信会泄露主机名，用户名和本地IP地址等信息。 如果一个合法的NetOp Helo请求发送给主机，应答的数据包中就会包含明文的NetOp主机名和用户名及本地IP地址信息。', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.danware.com/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15855, 'POP3 Cleartext Logins Permitted', '允许POP3明文登录', '低危', '远程主机正在运行一个POP3守护程序，该守护程序允许通过未加密的连接进行明文登录。如果使用不太安全的身份验证机制（例如，user command、AUTH PLAIN、AUTH LOGIN），攻击者可以通过嗅探到POP3守护进程的通信量来发现用户名和密码。', '请与您的供应商联系，以使用stunnel修复或加密SSL/TLS流量。', NULL, '1');
INSERT INTO `scan_suggest_log` VALUES (15901, 'SSL Certificate Expiry', 'SSL证书过期', '中危', '远程主机的SSL证书已经过期。', '购买或生成一个新的SSL证书来替换现有的。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15970, 'MS04-035: WINS Code Execution (870763) (uncredentialed check)', 'WINS代码执行漏洞', '紧急', 'Microsoft Windows WINS是Microsoft NetBIOS名字服务，用于解析NetBIOS计算机名到IP地址。Microsoft Windows WINS在处理关联的内容验证时存在问题，远程攻击者可以利用这个漏洞以系统进程权限执行任意指令。由于对名字验证处理时缺少充分验证，攻击者可以构建恶意网络包触发缓冲区溢出，精心构建提交数据可能以系统进程权限执行任意指令。在Windows Server 2003中，目前看起来只能进行拒绝服务攻击。 WinNT4.0、Win2000及Win2003中的WINS服务(wins.exe)存在代码执行漏洞。 远程攻击者可利用WINS复制功能，通过构造特殊的WINS复制包发送至TCP 24端口，就可修改任意内存地址，从而可能导致执行任意代码。', 'Microsoft已为Windows NT, 2000, XP, and 2003发布一系列补丁包。 相关链接： http://technet.microsoft.com/en-us/security/bulletin/ms04-045', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15973, 'PHP < 4.3.10 / 5.0.3 Multiple Vulnerabilities', 'PHP 版本低于 4.3.10 / 5.0.3', '高危', 'PHP版本较低，导致存在多个漏洞。', '建议升级 PHP 4.3.10 / 5.0.3 或者最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (15984, 'NFS Share User Mountable', 'NFS 共享允许远程挂载', '高危', '服务器的NFS共享允许通过远程挂载，攻击者可能会利用此漏洞获取远程主机文件的读权限（甚至是写）。', '配置NFS，限制为仅允许已通过授权的远程主机可以进行远程挂载NFS共享。', NULL, NULL);
