INSERT INTO `scan_suggest_log` VALUES (17154, 'HTTP Proxy CONNECT Loop DoS', 'HTTP代理连接循环DoS', '中危', '代理允许用户执行重复的连接请求本身。这允许任何人使代理CPU、内存资源饱和，从而造成拒绝服务攻击', '重新配置代理,拒绝连接请求本身。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (17162, 'Sybase SQL sa Account Blank Password', 'Sybase SQL sa帐户空白密码', '高危', '远程Sybase SQL服务器在没有任何密码的情况下启用了默认的“sa”帐户。攻击者可能使用这个缺陷对远程主机执行命令，并读取数据库内容。', '禁用此帐户，或者为其设置密码', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (17163, 'Sybase Adaptive Server Enterprise < 12.5.4.0 Multiple Vulnerabilities', 'Sybase Adaptive Server Enterprise < 12.5.4.0 存在多个漏洞', '高危', '远程主机正在运行Sybase adaptiveserverenterprise，这是一台具有网络功能的SQL服务器。该软件的远程版本早于12.5.4.0，这些版本受到一些未指定的安全缺陷的影响。', '升级到12.5.4.0或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (17204, 'News Server (NNTP) Anonymous Read / Write Access', 'NNTP服务允许匿名用户读写', '中危', '远程主机开启了远程用户服务方式，一些用户更喜欢开启公共的匿名NNTP服务，用来读写或上传文件。非预期的连接可能消耗您的带宽，如果恶意的攻击者使用您的服务器上传恶意文件，这可能让您陷入非法纠纷中。', '强制身份认证，或者过滤非法链接数。', NULL, NULL);
