INSERT INTO `scan_suggest_log` VALUES (17654, 'Oracle 8i/9i Database Server UTL_FILE Traversal Arbitrary File Manipulation', 'Oracle 8i/9i数据库服务 UTL_FILE遍历任意文件处理漏洞', '中危', '根据远程主机的Oracle数据库版本，可能存在多个任意目录文件遍历漏洞，这使得攻击者可能利用此漏洞读写或重命名任意文件，一个已认证用户可通过伪造SQL查询语句在同一驱动受影响的应用服务上检索系统上的任何文件和修改文件。', '应用2005年1月份补丁紧急漏洞补丁更新包。 相关链接： http://www.argeniss.com/research/ARGENISS-ADV-030501.txt http://www.nessus.org/u?68b438fb http://www.nessus.org/u?f926c604', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (17687, 'PHP Multiple Image Processing Functions File Handling DoS', 'PHP图形文件格式远程拒绝服务漏洞', '中危', 'PHP是用途非常广泛的通用目的脚本语言，可嵌入在HTML中。 PHP Group PHP中存在远程拒绝服务漏洞，起因是应用程序没能正确的处理恶意创建的图形格式文件(IFF)图形文件。', '目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载： www.php.net/distributions/php-5.0.4.tar.gz www.php.net/distributions/php-5.0.4.tar.bz2', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (17690, 'MySQL Zero-length Scrambled String Crafted Packet Authentication Bypass', 'MySQL空口令HASH绕过认证及缓冲区溢出漏洞', '高危', 'MySQL验证机制实现存在问题，远程攻击者可以利用这个漏洞无需用户密码通过验证。 通过提交特殊构建的验证包，可使攻击者绕过MySQL 4.1中的口令认证。', 'MySQL AB 4.1.3已经修补此漏洞，或者5.0 Build版本也已经修正此问题：', 'CVE-2005-1208', NULL);
INSERT INTO `scan_suggest_log` VALUES (17696, 'Apache HTTP Server 403 Error Page UTF-7 Encoded XSS', 'Apache HTTP 服务 403 错误页面 UTF-7 编码 XSS', '中危', '远程web服务器容易受到跨站点脚本攻击，构造一个精心设计的请求把脚本代码注入到一个utf-7编码的 403响应页面中，从而导致跨站脚本攻击。 实际上这个漏洞由于web浏览器不符合RFC 2616 而导致的。Apache HTTP服务器本身不存在这个漏洞，只是由于其默认配置会使攻击者能够在存在漏洞的浏览器触发该漏洞。', '升级Apache到 2.2.8版本或以上。', NULL, NULL);
