INSERT INTO `scan_suggest_log` VALUES (26023, 'Apache < 2.2.6 Multiple Vulnerabilities (DoS, XSS, Info Disc)', 'Apache版本低于2.2.6，存在多个安全威胁', '中危', 'Apache版本过低，可能受到拒绝服务攻击、跨站脚本攻击等多种安全威胁的攻击。', '确保受影响的模块未使用或配置，以避免存在安全问题，也可升级Apache版本至2.2.6 及以上。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (26057, 'lighttpd mod_fastcgi HTTP Request Header Remote Overflow', 'lighttpd mod_fastcgi HTTP请求数据包头远程溢出漏洞', '中危', '远程web服务显示使用 FastCGI 模块，此模块版本可能受到缓冲区溢出漏洞攻击，通过发送带有长数据包头的伪造请求，远程攻击者可利用这个问题，增加或更换数据包头并传递给PHP，例如SCRIPT_FILENAME,这可能造成任意代码执行攻击。', '禁用FastCGI模块，或者升级lighttpd版本至1.4.18及以上。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (26194, 'Web Server Transmits Cleartext Credentials', 'Web服务器传输明文凭证', '低危', '远程web服务器包含几个HTML表单字段，其中包含“password”类型的输入，这些输入以明文形式将其信息传输到远程web服务器。窃听web浏览器和服务器之间通信的攻击者可能获得有效用户的登录名和密码。', '确保每个敏感表单都通过HTTPS传输内容。', NULL, '1');
