INSERT INTO `scan_suggest_log` VALUES (26920, 'Microsoft Windows SMB NULL Session Authentication', 'SMB空会话认证漏洞', '中危', '根据主机的安全配置，匿名用户可通过SMB空会话认证访问远程主机。潜在的攻击者可通过空会话从远程主机获取大量信息，比如用户列表、服务列表、共享列表等等。这些信息有助于攻击者进行其它攻击。', '1）打开注册表，进行以下设置： -HKLM\\SYSTEM\\CurrentControlSet\\Control\\LSA\\RestrictAnonymous=1 -HKLM\\SYSTEM\\CurrentControlSet\\Services\\lanmanserver\\parameters\\restrictnullsessaccess=1 2）打开注册表，移除以下项： HKLM\\SYSTEM\\CurrentControlSet\\Services\\lanmanserver\\parameters\\NullSessionPipes 设置完成后重启主机系统以使其生效。', NULL, NULL);
