INSERT INTO `scan_suggest_log` VALUES (31423, 'Sun Java Web Console < 3.0.5 Remote File Enumeration', 'sun java_web_console 访问权限绕过漏洞', '高危', 'Sun Java Web Console存在未知漏洞。远程攻击者绕过预设的访问限制和通过未知向量来决定文件或目录的存在。', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： Sun Solaris 9_x86 Sun 125951-07 http://sunsolve.sun.com/search/document.do?assetkey=urn:cds:docid:1-21 -125951-07-1', NULL, NULL);
