INSERT INTO `scan_suggest_log` VALUES (33850, 'Unsupported Unix Operating System', 'Unix操作系统版本过低', '紧急', '根据操作系统的版本，远程UNIX操作系统已经过时，厂商或供应商不再对此系统版本提供支持。', '建议更新操作系统版本。', NULL, NULL);
