INSERT INTO `scan_suggest_log` VALUES (42044, 'IBM DB2 9.1 < Fix Pack 8 Multiple Vulnerabilities', 'IBM DB2 9.1 < Fix Pack 8 存在多个漏洞', '中危', 'IBM DB2 9.1 缺少补丁包 Fix Pack 8 ，从而导致存在多个漏洞。', '官方补丁下载地址： http://www-933.ibm.com/support/fixcentral/swg/selectFixes?parent=ibm~Information%2BManagement&product=ibm/Information+Management/DB2&release=9.1.*&platform=All&function=recommended', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42052, 'Apache 2.2 < 2.2.14 Multiple Vulnerabilities', 'Apache版本低于2.2.14', '高危', 'Apache版本较低，导致存在多处漏洞。', '升级apache版本到2.2.29以上 http://httpd.apache.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42053, 'SSL Certificate Null Character Spoofing Weakness', 'SSL证书NULL字符欺骗漏洞', '高危', '远程主机使用的SSH证书存在一个NULL字符，这意味着一种妥协或一种例如SSLsniff的程序,欺骗SSL证书，并通过中间人攻击截取数据流量。', '重新生成SSL证书，相关链接： http://thoughtcrime.org/papers/null-prefix-attacks.pdf', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42106, 'MS09-050: Vulnerabilities in SMBv2 Could Allow Remote Code Execution', 'MS09-050：SMBv2远程代码执行漏洞', '紧急', 'Windows系统捆绑了新版的SMB2协议实现，SRV2.SYS驱动没有正确地处理包含畸形SMB头结构数据的NEGOTIATE PROTOCOL REQUEST请求，如果远程攻击者在发送的SMB报文的Process Id High头字段中包含有畸形数据的话，就会在_Smb2ValidateProviderCallback()函数中触发越界内存引用，可能导致执行任意指令或导致系统蓝屏死机', '升级补丁MS09-050 http://technet.microsoft.com/zh-CN/security/bulletin/ms09-050', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42256, 'NFS Shares World Readable', 'NFS 共享可读', '中危', '远程NFS服务器允许导出一个或更多的共享，而没有限制访问（如基于主机名，IP或IP范围）。', '配置NFS，限制为仅允许已通过授权的远程主机可以进行远程挂载NFS共享。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42263, 'Unencrypted Telnet Server', '未加密的Telnet服务', '中危', '远程主机正运行着Telnet服务，此项服务明文传输，使用未加密的Telnet服务，登录名、密码及命令均采取明文传输，这使得中间人攻击者截取Telnet会话，获取登录凭证或其它敏感信息，并在服务器和客户端之间篡改报文。', '禁用Telnet服务，并使用SSH代替。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42373, 'Sun Java JRE Multiple Vulnerabilities (269868 / 269869 / 270476 ..)', 'Sun Java SE和OpenJDK 模块\"ICC_Profile.getInstance\" 目录遍历漏洞', '高危', 'Solaris系统的Java运行时环境(JRE)为JAVA应用程序提供可靠的运行环境。 Sun Java SE和OpenJDK 模块\"ICC_Profile.getInstance\" 存在目录遍历漏洞，远程攻击者通过在pathname中加入..探测本地是否存在International Color Consortium (ICC)配置文件。', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://sunsolve.sun.com/search/document.do?assetkey=1-66-270475-1', 'CVE-2009-2570', NULL);
INSERT INTO `scan_suggest_log` VALUES (42411, 'Microsoft Windows SMB Shares Unprivileged Access', 'Microsoft Windows SMB 共享未授权访问漏洞', '高危', '通过给出的证书，远程主机拥有一个或多个网络共享，使远程攻击者利用这些共享权限读写保密信息。', '限制基于Windows下或浏览器下的共享权限，合理设置共享权限。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42821, 'IBM WebSphere Application Server 7.0 < Fix Pack 7', 'IBM WAS 标签\"IsSecurityEnabled\" 信息泄露漏洞', '中危', 'IBM WebSphere Application Server (WAS) 6.1版本至6.1.0.24版本以及7.0版本至7.0.0.4版本,IBM WebSphere Portal Server 5.1版本至6.0版本,和IBM Integrated Solutions Console (ISC) 6.0.1版本在WebSphere Member Manager (WMM)向Virtual Member Manager (VMM)和联合贮存器的移动中没有适当地设置securityEnabled安全标记，攻击者可以借助未明向量，获得贮存器中的敏感信息。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?uid=swg21375859', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42873, 'SSL Medium Strength Cipher Suites Supported', '允许SSL采用中强度加密', '中危', '远程主机支持SSL使用的密码采用中强度解密，即使用的KEY解密长度至少64位，但少于112位。注意：如果攻击者在同一个物理网段，密码很容易暴露。', '重新设置受影响的应用程序，可能的话，避免使用中强度加密，改为强度较高的加密算法。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42880, 'SSL / TLS Renegotiation Handshakes MiTM Plaintext Data Injection', 'SSL/TLS握手过程中间人明文注入攻击', '中危', '在SSL/TLS握手协议连接过程中，数据通信过程采用明文数据，这可能遭受中间人攻击，攻击者通过篡改明文数据，实现伪装。', '联系厂商获取相关补丁包。 http://www.ietf.org/mail-archive/web/tls/current/msg03948.html http://www.g-sec.lu/practicaltls.pdf http://tools.ietf.org/html/rfc5746', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (42934, 'Serv-U < 9.1.0.0', 'Serv-U版本低于9.1.0.0', '高危', '低版本的Serv-U存在多处安全漏洞，会话cookie导致缓冲区溢出、TEA解码算法导致缓冲区溢出、还有拒绝服务等漏洞', '建议升级到Serv-U14.0.2.0或者以上的版本 http://www.serv-u.com/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (43089, 'MS KB955759: Security Enhancements for the Indeo Codec', 'MS KB955759: 缺少 Indeo Codec 安全更新', '高危', 'Microsoft Windows 2000 SP4, XP SP2和SP3, 以及Server 2003 SP2中的Indeo32 codec中的ir32_32.dll 3.24.15.3，使远程攻击者可以借助一个多媒体文件中的畸形数据引起一个拒绝服务(堆崩溃)或执行任意代码。例如一个AVI文件。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://support.microsoft.com/kb/KB955759', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (43156, 'NTP ntpd Mode 7 Error Response Packet Loop Remote DoS', 'NTP Mode 7 (MODE_PRIVATE)报文远程拒绝服务漏洞', '中危', 'NTP (Network Time Protocol)是一款客户端用于与时间服务器同步日期和时间的协议。 NTP处理mode 7请求存在错误，远程攻击者可以利用漏洞对应用程序进行拒绝服务攻击。', '建议升级NTP到 NTP 4.2.4p8 / 4.2.6或者最新版本 http://www.ntp.org/downloads.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (43172, 'IBM DB2 9.5 < Fix Pack 5 Multiple Unspecified Vulnerabilities', 'IBM DB2 客户端接口组件未明漏洞', '中危', 'IBM DB2是美国IBM公司的一套关系型数据库管理系统。该系统的执行环境主要有UNIX、Linux、IBM i、z/OS以及Windows服务器版本。IBM DB2 FP18,FP8,FP5,以及FP1的客户端接口组件不能确认一个未明线索，攻击者可以借助未明向量复写\"外存储器\"。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-933.ibm.com/support/fixcentral/options', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (43351, 'PHP < 5.2.12 Multiple Vulnerabilities', 'PHP版本低导致多个漏洞', '中危', 'Apache版本低，可能会受到多个漏洞影响。', '升级PHP版本到5.2.12以上', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (43352, 'Oracle WebLogic Default Credentials', '远程Web应用程序使用默认凭据。', '高危', '通过提供默认凭据可以登录远程WebLogic安装。 一个远程攻击者可以利用这个来获得对这个安装的管理控制。', '使用强密码保护所有默认帐户。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (43369, 'Serv-U < 9.2.0.1', 'Serv-U File Server User Directory目录遍历漏洞', '中危', 'Serv-U是美国SolarWinds公司的一套FTP服务器软件。该软件提供修改FTP密码、控制带宽、断点续传等功能。Serv-U存在目录遍历漏洞，远程认证用户可以通过未明向量读取任意文件。', '升级软件到最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (43390, 'Adobe Flash Media Server < 3.0.5 / 3.5.3 Multiple Vulnerabilities (APSB09-18)', 'Adobe Flash Media Server媒体服务器资源耗尽远程拒绝服务漏洞（版本低于3.0.5 / 3.5.3）', '紧急', 'Adobe Flash Media Server (FMS)存在未明漏洞，攻击者可以借助未明向量引起一个拒绝服务(资源耗尽)。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://download.macromedia.com/pub/flashmediaserver/updates/3_5_3/Wind ows/FlashMediaServer3.5.exe http://download.macromedia.com/pub/flashmediaserver/updates/3_5_3/Linu x/FlashMediaServer3.5.tar.gz', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44065, 'OpenSSH < 5.2 CBC Plaintext Disclosure', 'OpenSSH版本低于5.2，CBC模式信息泄露漏洞', '中危', 'OpenSSH是一种开放源码的SSH协议的实现，初始版本用于OpenBSD平台，现在已经被移植到多种Unix/Linux类操作系统下。 如果配置为CBC模式的话，OpenSSH没有正确地处理分组密码算法加密的SSH会话中所出现的错误，导致可能泄露密文中任意块最多32位纯文本。在以标准配置使用OpenSSH时，攻击者恢复32位纯文本的成功概率为2^{-18}，此外另一种攻击变种恢复14位纯文本的成功概率为2^{-14}。', '升级OpenSSH版本至5.2及以上版本。 相关链接： http://www.nessus.org/u?4984aeb9 http://www.openssh.com/txt/cbc.adv http://www.openssh.com/txt/release-5.2', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44076, 'OpenSSH < 4.3 scp Command Line Filename Processing Command Injection', 'OpenSSH SCP本地Shell命令执行漏洞', '中危', 'OpenSSH的是一种开放源码的SSH协议的实现，初始版本用于OpenBSD的平台，现在已经被移植到多种Unix / Linux操作系统类操作系统下。 OpenSSH的4.2及以下版本的SCP工具实现上存在漏洞，本地攻击者可能利用此漏洞在主机以高权限执行任意命令，从而提升自己的权限。 SCP工具在使用系统（）调用执行外部程序时没有对用户提交的参数做充分的检查，本地可以通过提交精心构造的输入数据以高权限执行任意命令。', '升级到OpenSSH 4.3或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44077, 'OpenSSH < 4.5 Multiple Vulnerabilities', '低版本OpenSSH多个安全漏洞', '高危', 'Web服务器上的SSH版本较低受到多个漏洞的影响', '升级到OpenSSH 4.5或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44078, 'OpenSSH < 4.7 Trusted X11 Cookie Connection Policy Bypass', 'OpenSSH版本低于4.7， X11 Cookie本地绕过认证漏洞', '高危', 'OpenSSH是一种开放源码的SSH协议的实现，初始版本用于OpenBSD平台，现在已经被移植到多种Unix/Linux类操作系统下。 OpenSSH X11在处理Cookie时存在漏洞，本地攻击者可能利用此漏洞提升自己的权限。如果无法生成不可信任的Cookie的话，OpenSSH中的ssh就会使用可信任的X11 Cookie，这可能将X客户端处理为可信任，允许本地攻击者破坏预期的策略获得权限提升。', '升级OpenSSH版本至4.7及以上版本。 相关链接： http://www.openssh.com/txt/release-4.7', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44079, 'OpenSSH < 4.9 \'ForceCommand\' Directive Bypass', 'OpenSSH版本低于4.9，存在绕过ForceCommand指令漏洞', '中危', 'OpenSSH是一种开放源码的SSH协议的实现，初始版本用于OpenBSD平台，现在已经被移植到多种Unix/Linux类操作系统下。 OpenSSH没有正确地实现ForceCommand指令，如果本地攻击者修改了~/.ssh/rc会话文件的话，则即使ForceCommand指令仍有效，攻击者仍可以执行任意代码。', '升级OpenSSH版本至4.9及以上版本。 相关链接： http://www.openssh.org/txt/release-4.9', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44135, 'Web Server Generic Cookie Injection', 'Web 服务器通用类 Cookie 注入漏洞', '中危', 'Web 服务器软件版本较低导致存在Cookie 注入漏洞', '升级Web 服务器软件', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44418, 'MS10-008: Cumulative Security Update of ActiveX Kill Bits (978262)', 'MS10-008：ActiveX Kill Bits 的累积安全更新', '中危', 'Microsoft数据分析器ActiveX控件有一个远程代码 执行漏洞。该系统还可以具有一个或多个 安装脆弱第三方ActiveX控件。 远程攻击者可以通过诱使用户利用这些问题 请求一个恶意制作的网页，从而导致任意代码 执行。', '微软已经发布了补丁集适用于Windows 2000，XP，2003， Vista中，2008年和7 https://technet.microsoft.com/library/security/MS10-008', 'CVE-2009-3555', NULL);
INSERT INTO `scan_suggest_log` VALUES (44422, 'MS10-012: Vulnerabilities in SMB Could Allow Remote Code Execution', 'MS10-012：SMB远程代码执行漏洞', '高危', 'Microsoft SMB协议软件没有正确的处理在协商期间解析SMB报文时可能出现的竞争条件，未经认证的攻击者可以通过向运行Server服务的计算机发送特制网络消息来利用该漏洞，导致受影响系统停止响应，直至手动重新启动', '升级补丁ms10-012 http://technet.microsoft.com/zh-CN/security/bulletin/ms10-012', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44588, 'IBM WebSphere Application Server 6.0 < 6.0.2.39 Multiple Vulnerabilities', 'IBM WebSphere Application Server 敏感信息泄露漏洞', '中危', 'IBM WebSphere Application Server (WAS) 6.0.2.39之前的6.0版本，6.1.0.29之前的6.1版本和7.0.0.7之前的7.0版本存在漏洞。由于Java Naming and Directory Interface (JNDI)的实现不能正确限制访问UserRegistry对象类函数，远程攻击者可借助特制的类函数调用获得敏感信息。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?uid=swg1PK99480', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44589, 'Apache 1.3.x < 1.3.42 mod_proxy Integer Overflow', 'Apache版本低于1.3.42 ，存在mod_proxy 整数溢出漏洞', '紧急', 'Apache HTTP Server是一款流行的Web服务器。 Apache服务器的mod_proxy模块在64位操作系统中存在堆栈溢出漏洞，允许远程原服务器引起拒绝服务攻击或可能执行任意代码利用一个大块的数据引起堆栈溢出。', '确保受影响的模块未使用或配置，以避免存在安全问题，也可升级Apache版本至1.3.42及以上。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (44921, 'PHP < 5.3.2 / 5.2.13 Multiple Vulnerabilities', 'PHP版本低于5.3.2 / 5.2.13', '中危', 'Apache版本低，可能会受到多个漏洞影响。', '升级PHP版本到5.3.2以上', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45004, 'Apache 2.2 < 2.2.15 Multiple Vulnerabilities', 'Apache版本低于2.2.15', '紧急', 'Apache版本低，可能会受到多个漏洞影响。', '升级apache版本到2.2.15以上 http://httpd.apache.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45112, 'FileZilla Server < 0.9.31 Denial of Service', 'FileZilla版本低于0.9.31 导致拒绝服务攻击漏洞', '中危', '经过测试，发现服务器上FileZilla版本低于0.9.31，这种版本的SSL代码未知漏洞可被远程攻击者引发拒绝服务的情况。', '升级到FileZilla到0.9.31或者更新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45140, 'Serv-U < 9.4.0.0', 'Serv-U受多个漏洞的影响。', '中危', 'Serv-U的安装版本小于9.4.0.0，可能受以下问题的影响： - 导入用户时，受限管理员可以在其主目录之外创建用户帐户。 - 导出用户时，受限管理员可以查看用户的主目录，虚拟路径和目录访问规则的完整路径。', '升级到Serv-U版最新版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45379, 'Oracle Java SE Multiple Vulnerabilities (March 2010 CPU)', '多个厂商TLS协议和SSL协议会话协商明文注入漏洞', '高危', '传输层安全协议（TLS）是确保互联网上通信应用和其用户隐私的协议。 Apache HTTP Server 2.2.14及之前版本，OpenSSL 0.9.8l之前版本，GnuTLS 2.8.5及之前版本，Mozilla Network Security Services (NSS) 3.12.4及之前版本，多个Cisco产品，以及其他产品的TLS协议和SSL协议中存在会话协商明文注入漏洞。由于TLS协议和SSL协议实现模块没有适当将会话协商与现存连接关联，中间人攻击者可以通过发送一个未认证的请求，将数据注入到受TLS和SSL协议保护的HTTP会话和其它类型会话中。', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://cvs.openssl.org/chngview?cn=18790 http://www.gnu.org/software/gnutls/download.html http://httpd.apache.org/download.cgi', 'CVE-2010-0252', NULL);
INSERT INTO `scan_suggest_log` VALUES (45411, 'SSL Certificate with Wrong Hostname', 'SSL证书主机名不匹配', '中危', 'SSL现有认证的commonName (CN)是供其它主机使用的。', '为此项服务购买或生产一个合适的证书', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45416, 'IBM WebSphere Application Server 6.0 < 6.0.2.19 HTTP Response Splitting', 'IBM WebSphere应用程序服务器 跨站请求伪造漏洞', '高危', 'IBM WebSphere应用程序服务器(WAS)6.0.2.19之前版本中存在跨站请求伪造漏洞。远程攻击者可以借助无效的多行头中的单个CRLF序列，注入任意的HTTP头和执行HTTP响应分裂攻击。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?uid=swg1PK39732', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45417, 'IBM WebSphere Application Server 6.0 < 6.0.2.21 Multiple Vulnerabilities', 'IBM WebSphere应用服务器关闭连接信息泄露漏洞', '高危', 'Websphere在处理连接的切断时存在漏洞，远程攻击者可能利用此漏洞获取某些内存中的敏感信息。 如果出现了关闭连接异常错误的话，例如在发送请求响应期间取消了请求，Web容器就可能破坏用于发送响应的缓冲区，导致将所取消请求的一些请求数据添加到之后请求的响应中，或将第一个之后请求的某些响应数据添加到不同请求的响应中，这样用户就可以在响应中获得敏感信息。', '应用Fix Pack 21（6.0.2.21）或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45418, 'IBM WebSphere Application Server 6.0 < 6.0.2.23 Unspecified Vulnerability (PK45726)', 'IBM WebSphere Application Server Administrative Scripting Tools 未明攻击漏洞', '紧急', 'IBM WebSphere Application Server 5.x 和6.0.x版本的Administrative Scripting Tools (such as wsadmin or ANT)中存在未明漏洞。该漏洞具有未知影响和攻击向量。', 'Fix Pack 23（6.0.2.23）或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45419, 'IBM WebSphere Application Server 6.0 < 6.0.2.25 Multiple Vulnerabilities', 'IBM WebSphere Application Server 安全漏洞', '紧急', 'IBM WAS Fix Pack 13之前6.1版本和Fix Pack 25之前6.0.2版本的Administrative Console中存在安全漏洞。攻击者可利用该漏洞造成未知影响。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?rs=180&uid=swg27007951', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45420, 'IBM WebSphere Application Server 6.1 < 6.1.0.7 Multiple Vulnerabilities', 'IBM WebSphere Application Server Java Message Service拒绝服务漏洞', '紧急', 'IBM WebSphere应用程序服务器(WAS)的Java Message Service (JMS)存在拒绝服务漏洞。攻击者可以借助未知向量，引起拒绝服务攻击。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?rs=180&uid=swg27007951', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45421, 'IBM WebSphere Application Server 6.1 < 6.1.0.9 Cross-session Information Disclosure', 'IBM WebSphere应用服务器关闭连接信息泄露漏洞', '中危', 'IBM Websphere应用服务器以Java和Servlet引擎为基础，支持多种HTTP服务，可帮助用户完成从开发、发布到维护交互式的动态网站的所有工作。 Websphere在处理连接的切断时存在漏洞，远程攻击者可能利用此漏洞获取某些内存中的敏感信息。 如果出现了关闭连接异常错误的话，例如在发送请求响应期间取消了请求，Web容器就可能破坏用于发送响应的缓冲区，导致将所取消请求的一些请求数据添加到之后请求的响应中，或将第一个之后请求的某些响应数据添加到不同请求的响应中，这样用户就可以在响应中获得敏感信息。', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： IBM --- http://www-1.ibm.com/support/docview.wss?rs=180&uid=swg27004980', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45422, 'IBM WebSphere Application Server < 6.1.0.15 Multiple Vulnerabilities', 'IBM webSphere http_plugin.log敏感信息泄露漏洞', '高危', 'IBM webSphere Application Server (WAS) 6.0.2 Fix Pack 25 (6.0.2.25)之前版本和Fix Pack 15 (6.1.0.15)之前的6.1版本把未明明文信息写进http_plugin.log。本地用户可通过读取该文件获得敏感信息。', '升级到版本6.0.2.25或更高版本，因为它已被报告来修复此漏洞。 需要升级，因为没有已知的解决方法。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45423, 'IBM WebSphere Application Server 6.1 < 6.1.0.13 Multiple Vulnerabilities', 'IBM WebSphere Application Server 用户控制台 \"uddigui/navigateTree.do\"多个跨站脚本攻击漏洞', '紧急', 'IBM WebSphere Application Server (WAS)的UDDI用户控制台的uddigui/navigateTree.do中存在多个跨站脚本攻击漏洞。远程攻击者可以借助(1) keyField, (2) nameField, (3) valueField, 和 (4) frameReturn参数，注入任意web脚本或HTML。', '如果使用WebSphere Application Server，请更新应用Fix Pack 13（6.1.0.13）或 之后的版本。 否则，如果使用嵌入式WebSphere Application Server打包Tivoli Directory Server，应用最新推荐的eWAS修订包。 版本更新地址： http://www-01.ibm.com/support/docview.wss?uid=swg21404665', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45429, 'IBM WebSphere Application Server 6.0 < 6.0.2.41 Multiple Vulnerabilities', 'IBM WebSphere Application Server (WAS) 管理控制台跨站脚本攻击漏洞', '中危', 'IBM WebSphere Application Server (WAS) 中的管理控制台存在跨站脚本攻击漏洞。远程攻击者可以通过URI注入任意web脚本或HTML。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?rs=180&uid=swg27004980', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45430, 'IBM WebSphere Application Server 6.1 < 6.1.0.31 Multiple Vulnerabilities', 'IBM WebSphere应用服务版本低于6.1.0.31，存在多个安全漏洞', '中危', 'IBM WebSphere应用服务版本低于 6.1.0.27，存在多个安全漏洞，例如 (WAS) 管理控制台跨站脚本攻击漏洞； (WAS) \'wsadmin scripting J2CConnectionFactory\'对象信息泄露漏洞；(WAS) SSL连接拒绝服务漏洞等。', '如果使用WebSphere应用服务, 应用修复包Fix Pack 31 (6.1.0.31) 及以上版本，如果使用嵌入式WebSphere应用服务包，则推荐应用最新的 eWAS fix pack修复包。 相关链接： http://www-01.ibm.com/support/docview.wss?uid=swg21404665 http://www-01.ibm.com/support/docview.wss?uid=swg27009778 http://www-01.ibm.com/support/docview.wss?uid=swg27004980 http://www-01.ibm.com/support/docview.wss?uid=swg1PK88606 http://www-01.ibm.com/support/docview.wss?rs=180&uid=swg27007951#61031', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45431, 'IBM WebSphere Application Server 7.0 < Fix Pack 9', 'IBM WebSphere Application Server (WAS) 管理控制台跨站脚本攻击漏洞', '中危', 'IBM WebSphere Application Server是一个完善的、开放的Web应用服务器，它是IBM电子商务应用架构的核心。 IBM WebSphere Application Server (WAS) 中的管理控制台存在跨站脚本攻击漏洞。远程攻击者可以通过URI注入任意web脚本或HTML。', '前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?rs=180&uid=swg27004980', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (45517, 'MS10-024: Vulnerabilities in Microsoft Exchange and Windows SMTP Service Could Allow Denial of Service (981832) (uncredentialed check)', 'MS10-024：Microsoft Exchange 和 Windows SMTP 拒绝服务攻击漏洞', '中危', 'Microsoft Exchange 和 Windows SMTP 服务中存在两个安全漏洞。 如果攻击者将特制的 DNS 响应发送到运行 SMTP 服务的计算机，可能导致拒绝服务。', '微软已经为此发布了一个安全公告（MS10-024）以及相应补丁: http://www.microsoft.com/technet/security/Bulletin/MS10-024.mspx?pf=true', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (46015, 'HP System Management Homepage < 6.0.0.96 / 6.0.0-95 Multiple Vulnerabilities', 'HP系统管理主页版本低于 6.0.0.96 / 6.0.0-95，存在多个安全漏洞', '紧急', 'HP系统管理主页版本低于6.0.0.96 / 6.0.0-95，可能存在Namazu \'namazu.cgi\'跨站脚本攻击漏洞（CVE-2008-1468）、libxml2库的xmlSAX2Characters()函数整数溢出漏洞(CVE-2008-4226)、PHP mbstring扩展堆溢出漏洞（CVE-2008-5557）、php 跨站脚本攻击漏洞（CVE-2008-5814）等安全漏洞。', '升级HP系统管理主页版本至6.0.0.96(Windows) / 6.0.0-95（Linux）及以上，相关链接： http://www.procheckup.com/vulnerability_manager/vulnerabilities/pr09-15 http://archives.neohapsis.com/archives/bugtraq/2010-04/0207.html http://archives.neohapsis.com/archives/bugtraq/2010-02/0048.html http://www.nessus.org/u?2eb58026 http://www.nessus.org/u?399c92c8', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (46017, 'MS10-025: Vulnerability in Microsoft Windows Media Services Could Allow Remote Code Execution (980858) (uncredentialed check)', 'Microsoft Windows媒体服务运行远程代码执行漏洞', '紧急', 'Microsoft Windows是微软发布的非常流行的操作系统。Windows媒体单播服务(nsum.exe)处理MMS TRANSPORT_INFO报文的方式存在栈溢出漏洞。远程攻击者可以通过向运行可选Windows Media Services组件(非默认安装)的Windows 2000 Server SP4系统发送畸形报文触发这个溢出，导致执行任意代码。', 'Microsoft已为Windows NT, 2000, XP, and 2003发布一系列补丁包。 相关链接： http://technet.microsoft.com/en-us/security/bulletin/MS10-025', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (46173, 'IBM DB2 9.1 < Fix Pack 9 Multiple Vulnerabilities', 'IBM DB2 9.1 < Fix Pack 9 存在多个漏洞', '中危', 'IBM DB2 9.1 缺少补丁包 Fix Pack 9 ，从而导致存在多个漏洞。', '官方补丁下载地址： http://www-933.ibm.com/support/fixcentral/swg/selectFixes?parent=ibm~Information%2BManagement&product=ibm/Information+Management/DB2&release=9.1.*&platform=All&function=recommended', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (46181, 'JBoss Enterprise Application Platform \'/web-console\' Authentication Bypass', 'Red Hat JBoss企业应用平台多个漏洞', '中危', 'Red Hat Jboss企业应用平台(即Jboss EAP或JBEAP)的JBossAs的Web Console(即web-console)只对GET和POST方法进行访问控制，远程攻击者可使用其它的方法通过未明请求获取敏感信息。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.jboss.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (46255, 'HP Mercury LoadRunner Agent Remote Command Execution', 'HP LoadRunner Agent远程代码执行漏洞', '高危', '低版本HP LoadRunner Agent存在magentproc.exe服务引起的执行任意代码漏洞；各server_ip_name引起的缓冲区溢出漏洞', '升级到HP LoadRunner v9.50版本以上更高版本， 并开启\'Secure Channel\' 通信', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (46677, 'HP System Management Homepage < 6.1.0.102 / 6.1.0-103 Multiple Vulnerabilities', 'HP系统管理主页版本低于 6.1.0.102 / 6.1.0-103，存在多个安全漏洞', '高危', 'HP系统管理主页版本低于 6.1.0.102 / 6.1.0-103，存在多个厂商TLS协议和SSL协议会话协商明文注入漏洞（CVE-2009-3555）。', '升级HP系统管理主页版本至6.1.0.102(Windows) / 6.1.0.103（Linux）及以上，相关链接： http://archives.neohapsis.com/archives/bugtraq/2010-05/0141.html http://www.nessus.org/u?4e8707ba', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (46766, 'IBM DB2 9.7 < Fix Pack 2 Multiple Vulnerabilities', 'IBM DB2未明安全漏洞', '中危', 'IBM DB2 FP18之前的8版本，FP8之前的9.1版本，FP4之前的9.5版本，以及FP2之前的9.7版本没有完成对函数的特权流失的预期删除操作，这会引起未明影响和远程攻击向量。', '使用IBM DB2 V9.7 FP2或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (46841, 'MS10-034: Cumulative Security Update of ActiveX Kill Bits (980195)', 'Microsoft Data Analyzer ActiveX控件栈破坏漏洞', '中危', 'Microsoft Data Analyzer ActiveX控件允许从基于COM的开发应用程序(如Visual Basic)以编程方式控制Data Analyzer。 ActiveX控件存在栈破坏漏洞。在Internet Explorer中实例化Microsoft Data Analyzer ActiveX控件(max3activex.dll)时，该控件可能会破坏栈状态，导致执行任意代码。', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： Microsoft Windows XP Media Center Edition SP2 Microsoft Cumulative Security Update for ActiveX Killbits for Windows XP (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=7bcf3945-0552 -478e-b7f3-bbca97dd1b5d Microsoft Windows Server 2008 for 32-bit Systems SP2 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Server 2008 (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=9a85b1bf-7427 -47d0-9e1b-21dbe824a62c Microsoft Windows Server 2008 for x64-based Systems R2 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Server 2008 R2 x64 Edition (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=cda31c54-8b81 -4185-a623-64480ad4b73c Microsoft Windows Vista x64 Edition 0 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Vista for x64-based Systems (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=f349f7aa-d020 -4e0d-a35f-518a63ec92df Microsoft Windows XP Tablet PC Edition SP2 Microsoft Cumulative Security Update for ActiveX Killbits for Windows XP (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=7bcf3945-0552 -478e-b7f3-bbca97dd1b5d Microsoft Windows XP Media Center Edition SP3 Microsoft Cumulative Security Update for ActiveX Killbits for Windows XP (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=7bcf3945-0552 -478e-b7f3-bbca97dd1b5d Microsoft Windows 7 for 32-bit Systems 0 Microsoft Cumulative Security Update for ActiveX Killbits for Windows 7 (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=ec6d996f-dffa -45ad-9467-e96a4ac63e5f Microsoft Windows Server 2003 Web Edition SP2 Microsoft Security Update for ActiveX Killbits for Windows Server 2003 (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=29ff72f7-1663 -4f35-a794-2dfe3c17b39c Microsoft Windows XP Professional x64 Edition SP2 Microsoft Cumulative Security Update for ActiveX Killbits for Windows XP x64 Edition (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyId=25ef97e8-e76e -44c2-953c-f94cbac552cf Microsoft Windows Vista 0 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Vista (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=2c897ddd-f441 -41d4-b5b4-d794cfc96e6b Microsoft Windows Server 2003 x64 SP2 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Server 2003 x64 Edition (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=d4a97bb7-4f74 -4884-9a6e-7a4df9c540fb Microsoft Windows Vista x64 Edition SP1 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Vista for x64-based Systems (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=f349f7aa-d020 -4e0d-a35f-518a63ec92df Microsoft Windows XP Tablet PC Edition SP3 Microsoft Cumulative Security Update for ActiveX Killbits for Windows XP (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=7bcf3945-0552 -478e-b7f3-bbca97dd1b5d Microsoft Windows Vista SP1 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Vista (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=2c897ddd-f441 -41d4-b5b4-d794cfc96e6b Microsoft Windows Server 2008 for x64-based Systems 0 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Server 2008 x64 Edition (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=fde218c3-90ab -4664-852d-25ca55835054 Microsoft Windows Server 2008 for Itanium-based Systems SP2 Microsoft Cumulative Security Update for ActiveX Killbits for Windows Server 2008 for Itanium-based Systems (K http://www.microsoft.com/downloads/details.aspx?FamilyID=5b6e9451-df38 -4626-bb1d-4fc160d7a40e Microsoft Windows 2000 Advanced Server SP4 Microsoft Cumulative Security Update for ActiveX Killbits for Windows 2000 (KB978262) http://www.microsoft.com/downloads/details.aspx?FamilyID=543dc6a7-fa76 -4ba9-81e4-25fdf2013548 3DM Software Disk', 'CVE-2009-3555', NULL);
INSERT INTO `scan_suggest_log` VALUES (46868, 'Apache Tomcat 5.x < 5.5.21 Multiple Vulnerabilities', 'Apache Tomcat 5.x<5.5.21多个漏洞', '中危', '根据其自我报告的版本号，在远程主机上侦听的ApacheTomcat5.x实例早于5.5.21。因此，它受到以下漏洞的影响：-远程Apache Tomcat安装易受跨站点脚本攻击。客户端提供的Accept Language标头未经验证，这使得攻击者可以使用巧尽心思构建的URL将任意HTML和脚本代码注入用户的浏览器(CVE-2007-1358）-如果远程Apache Tomcat安装配置为使用SingleSignOn阀，则如果通过HTTPS进行身份验证，则JSESSIONIDSSO cookie不会设置“secure”属性。这允许在请求HTTP内容时将JSESSIONIDSSO cookie发送到同一服务器(CVE-2008-0128）-远程Apache Tomcat安装受到信息泄漏漏洞的影响。对于某些错误情况，doRead方法无法返回正确的错误代码，这可能导致POST内容被发送到不同的、不正确的请求(（CVE-2008-4308）请注意，Nessus没有针对这些问题进行测试，而是仅依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 5.5.21或更高版本。', 'CVE-2007-1358', '1');
INSERT INTO `scan_suggest_log` VALUES (47028, 'Apache Tomcat 5.x < 5.5.1 Information Disclosure', 'Apache Tomcat 5.x<5.5.1信息披露', '中危', '根据其自我报告的版本号，在远程主机上侦听的ApacheTomcat5.x实例早于5.5.1。因此，它受到信息披露漏洞的影响。具体来说，它可以允许来自不允许的IP地址的请求访问受扩展RequestFilterValve的阀保护的上下文。请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 5.5.1或更高版本。', 'CVE-2008-3271', '1');
INSERT INTO `scan_suggest_log` VALUES (47112, 'IBM WebSphere Application Server 7.0 < Fix Pack 11 Multiple Vulnerabilities', 'IBM WebSphere Application Server JAX-RPC WS-Security和JAX-WS访问限制绕过漏洞', '紧急', 'IBM WebSphere Application Server (WAS)的(1)JAX-RPC WS-Security，和(2)JAX-WS运行实现无法正确处理WebServices PKCS#7和PKIPath令牌，远程攻击者可通过未明向量绕开访问限制。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www-01.ibm.com/support/docview.wss?rs=180&uid=swg27004980', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (47556, 'MS10-012: Vulnerabilities in SMB Could Allow Remote Code Execution (971468) (uncredentialed check)', 'MS10-012：SMB远程代码执行漏洞', '高危', 'Microsoft SMB协议软件没有正确的处理在协商期间解析SMB报文时可能出现的竞争条件，未经认证的攻击者可以通过向运行Server服务的计算机发送特制网络消息来利用该漏洞，导致受影响系统停止响应，直至手动重新启动', '升级补丁ms10-012 http://technet.microsoft.com/zh-CN/security/bulletin/ms10-012', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (47709, 'Microsoft Windows 2000 Unsupported Installation Detection', '微软已不支持Microsoft Windows 2000', '紧急', '微软不支持该操作系统，这意味着微软不仅不提供新的安全补丁包，也不提供相关威胁报告。', '升级该主机操作系统', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (47715, 'Tomcat 4.1 XSS', 'Tomcat版本为4.1，存在跨站脚本攻击威胁', '中危', '远程主机运行的web服务，未能充分过滤恶意的JaveScript脚本请求，通过利用此漏洞，攻击者可能在用户浏览器上网页中受影响的站点实施任意HTML和脚本执行攻击。', '升级Tomcat版本至4.1.2及以上版本。 相关链接： http://archives.neohapsis.com/archives/vuln-dev/2002-q3/0482.html http://tomcat.apache.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48205, 'Apache 2.2 < 2.2.16 Multiple Vulnerabilities', 'Apache版本低于2.2.16', '中危', 'Apache版本较低，导致存在多处漏洞。', '升级apache版本到2.2.16以上 http://httpd.apache.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48244, 'PHP 5.2 < 5.2.14 Multiple Vulnerabilities', 'PHP版本低于5.2.14', '高危', 'Apache版本低，可能会受到多个漏洞影响。', '升级PHP版本到5.2.14以上', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48245, 'PHP 5.3 < 5.3.3 Multiple Vulnerabilities', 'PHP版本低于5.3.3 存在多个漏洞', '高危', '服务器上运行的PHP版本过低，这将导致受到多个漏洞影响，非常危险。', '更新到5.3.3或者更新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48291, 'MS10-054: Vulnerabilities in SMB Server Could Allow Remote Code Execution (982214)', 'MS10-054：SMB远程代码执行漏洞', '紧急', 'Microsoft SMB协议软件处理特制SMB报文的方式存在缓冲区溢出、变量验证和栈耗尽漏洞。利用这些漏洞不要求进行认证，攻击者可以通过向运行Server服务的计算机发送特制网络消息来利用这些漏洞，导致拒绝服务或完全入侵系统', '升级补丁MS10-054 http://technet.microsoft.com/zh-CN/security/bulletin/ms10-054', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48298, 'Adobe Flash Media Server < 3.0.6 / 3.5.4 Multiple Vulnerabilities (APSB10-19)', 'Adobe Flash Media Server 代码注入漏洞（版本低于3.0.6 / 3.5.4）', '紧急', 'Adobe Flash Media Server是基于Flash应用程序的服务器，可提供运行交互式应用及音频视频流的环境。 Adobe Flash Media Server (FMS) 3.0.6之前的版本，以及3.5.4之前的3.5.x版本存在漏洞。攻击者可以借助未明向量执行任意代码。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.adobe.com/support/security/bulletins/apsb10-19.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48405, 'MS10-054: Vulnerabilities in SMB Server Could Allow Remote Code Execution (982214) (remote check)', 'SMB服务允许远程代码执行漏洞', '紧急', '远程主机SMB服务允许远程攻击者执行任意代码攻击或拒绝服务攻击。', '目前厂商已发布Windows XP, Vista, 2008, 7, and 2008 R2平台的相关补丁 相关链接： http://technet.microsoft.com/en-us/security/bulletin/MS10-054', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48435, 'Serv-U < 10.2.0.0', 'Serv-U 版本低于 10.2.0.0', '中危', 'Serv-U版本较低，导致存在多个漏洞。', '建议升级 Serv-U 10.2.0.0或者最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48762, 'MS KB2269637: Insecure Library Loading Could Allow Remote Code Execution', 'MS KB2269637: 不安全的库文件加载远程代码执行漏洞', '高危', '应用程序解析自己的当前工作目录的DLL文件实现方式不安全，如果一个恶意DLL与所需的DLL同名，且位于应用程序的当前工作目录，那么该恶意DLL将被加载。 攻击者可利用该漏洞来远程执行恶意代码。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://support.microsoft.com/kb/2264107', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48989, 'IOS Heap-based Overflow Vulnerability in System Timers - Cisco Systems', 'Cisco IOS系统定时器堆溢出漏洞', '高危', 'Cisco IOS中存在堆溢出漏洞。在很多情况下，如果由一直在监控内存破坏的\"Check Heaps\"进程检测的话，该溢出仅会破坏系统内存并触发系统重载。如果成功的攻击了合适的堆溢出话，就可能无需直接的设备崩溃便可实现代码执行。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.cisco.com/warp/public/707/cisco-sa-20051102-timers.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48991, 'IOS HTTP Server Command Injection Vulnerability', 'Cisco IOS Web Server跨站脚本漏洞', '中危', 'Cisco IOS是Cisco设备所使用的操作系统。 Cisco IOS Web Server fot IOS 12.0(2a)中存在跨站脚本漏洞，这允许远程攻击者通过以下方式注入任意的Web脚本或HTML。 (1) 包含HTML的数据包； (2) 发送带有HTML负载的Cisco Discovery Protocol (CDP)数据包 。 注意：此漏洞原始报告中要求在/level/15/exec/-/show/buffers中启用dump 和 packet 功能。', '使用思科安全通报cisco-sa-20051201-http中的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48994, 'DLSw Vulnerability - Cisco Systems', 'Cisco IOS数据链路交换拒绝服务漏洞', '高危', 'Cisco IOS DLSw功能在协调交互过程中处理某些无效选项时存在漏洞，远程攻击者可能利用此漏洞执行拒绝服务攻击，导致设备重载。 创建DLSw通讯包括以下几个操作阶段。 1. 在第一阶段，DLSw对等端通过TCP 2065或2067端口相互创建两个TCP连接，这些TCP连接是DLSw通讯的基础。 2. 创建连接之后DLSw伙伴在第二阶段交换所支持功能的列表，这有助于对等端使用相同的选项，如果DLSw伙伴由不同厂商生产的话这一点尤其重要。 3. 下一步DLSw伙伴在SNA或NetBIOS终端系统之间创建线路，信息帧可以通过线路传输。 如果配置了DLSw的话某些Cisco IOS软件版本中就会存在漏洞，在创建连接后如果设备在功能交换期间收到了无效选项的话就可能出现重载。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20070110-dlsw.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48997, 'Crafted TCP Packet Can Cause Denial of Service', 'Cisco IOS特制TCP报文远程拒绝服务漏洞', '高危', 'Cisco IOS在处理特定畸形的TCP报文时存在漏洞，远程攻击者可能利用此漏洞对设备执行拒绝服务攻击，导致设备耗尽所有内存无法正常工作。如果将Cisco IOS设备配置为接收TCP报文的话，则发送给Cisco IOS设备物理或虚拟接口IPv4地址的特制报文就可能导致泄漏少量的内存。这种内存泄漏可能造成耗尽内存资源，降低系统的性能。请注意攻击者无需完成TCP三重握手就可以触发这个漏洞，因此伪造源址的TCP报文也可以完成攻击。这个漏洞仅适用于目标为Cisco IOS设备的通讯，穿越Cisco IOS设备的通讯不会触发这个漏洞。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20070124-crafted-tcp.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48998, 'IPv6 Routing Header Vulnerability', 'Cisco IOS畸形IPv6路由头结构处理远程拒绝服务漏洞', '高危', 'Cisco IOS在处理畸形的IPv6报文时存在漏洞，远程攻击者可能利用此漏洞导致设备无法正常工作或在设备上执行任意指令。如果运行Cisco IOS的设备启用了IPv6的话，则在处理特制的IPv6 Type 0 Routing头结构时就可能会导致内存破坏。攻击报文的目标必须为设备上所定义的任意IPv6地址，但与具体报文类型无关(如TCP、ICMP、UDP均可触发)，因为漏洞出现在IP层。如果解包后的IPv6地址是设备上所定义的IPv6地址的话，则通过IPv4网络隧道传输的IPv6报文也可以触发这个漏洞，且与具体的封装方式无关。由于是个内存破坏漏洞，因此成功攻击也可能导致执行任意指令。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20070124-IOS-IPv6.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (48999, 'SIP Packets Reload IOS Devices with support for SIP', 'Cisco IOS SIP报文处理远程拒绝服务漏洞', '高危', '运行某些IOS版本且支持SIP服务的Cisco设备中存在漏洞，可能允许通过向TCP 5060端口或UDP 5060端口发送一系列特制的SIP报文导致设备重载。此外，即使没有配置SIP操作的话，某些支持SIP服务的IOS版本也可能处理SIP消息。如果要处理SIP消息IOS需要打开UDP 5060端口和TCP 5060端口进行监听。设备必须有开放的SIP端口才会受这个漏洞影响。没有监听TCP 5060或UDP 5060的设备不受漏洞影响。由于SIP使用UDP进行传输，因此可以伪造发送者的IP地址，这可能导致允许从可信任IP地址到这些端口通讯的ACL失效。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20070131-sip.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49003, 'Multiple Vulnerabilities in the IOS FTP Server', 'Cisco IOS FTP Server非授权访问及拒绝服务漏洞', '高危', 'Cisco IOS所带的FTP Server处理访问请求时存在漏洞，远程攻击者可能利用此漏洞非授权访问系统文件或导致拒绝服务。 启用了IOS FTP Server功能的Cisco IOS没有正确检查用户授权，可能允许攻击者非授权读写设备文件系统中的任意文件，包括设备保存的配置，其中可能有口令或其他敏感信息；此外这种配置的Cisco IOS还可能导致在通过FTP传输文件时IOS重载。', '应用思科安全通报cisco-sa-20070509-iosftp中的相关补丁修复', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49004, 'Vulnerability In Crypto Library - Cisco Systems', 'RSA BSAFE库远程ASN.1解析拒绝服务漏洞', '中危', 'RSA BSAFE所提供的Crypto-C和Cert-C库的实现上存在漏洞，远程攻击者可能利用此漏洞导致设备拒绝服务。 如果用户通过任何使用了上述库的应用程序解析了畸形的ASN.1对象的话，就会触发这个漏洞，导致受影响的应用或设备崩溃。', '应用思科安全通报cisco-sa-20070522-crypto中的相关补丁修复', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49005, 'Multiple Vulnerabilities in Cisco IOS While Processing SSL Packets - Cisco Systems', 'Cisco IOS SSL连接多个远程拒绝服务漏洞', '高危', 'Cisco IOS在处理SSL会话连接时存在漏洞，远程攻击者可能利用此漏洞导致设备拒绝服务。 如果配置为使用SSL协议协议的话，Cisco IOS设备在处理畸形SSL报文时可能崩溃。如果要触发这些漏洞，恶意的客户端必须在与有漏洞设备交换SSL协议期间发送畸形的ClientHello、ChangeCipherSpec或Finished报文。 攻击者可以在创建TCP连接后但交换认证凭据(如用户名/口令或证书)之前触发这些漏洞，要求完成TCP三重握手降低了通过使用伪造IP地址利用这些漏洞的概率。如果已经创建了SSL会话的话，拦截两台受影响设备之间通讯的攻击者无法利用这个漏洞，因为SSL可以防范这种注入。但是，这种攻击可以通过TCP RST不正常的终止已有的会话，然后攻击者可以等待创建新的SSL会话并在新的SSL会话开始时注入恶意报文，以此触发漏洞。', '应用思科安全通报cisco-sa-20070522-SSL中的相关补丁修复', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49007, 'Voice Vulnerabilities in Cisco IOS and Cisco Unified Communications Manager - Cisco Systems', '思科IOS语音漏洞和思科通讯管理漏洞-Cisco系统', '高危', '在思科IOS软件中识别出多个与语音相关的漏洞，其中一个漏洞也与思科统一通信经理共享。这些漏洞属于以下协议或特性:会话发起协议(SIP)、媒体网关控制协议(MGCP)、信号协议H.323, H.254、实时传输协议(RTP)', '应用Cisco Security Advisory Cisco -sa-20070808- ios -voice中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49009, 'Cisco IOS Secure Copy Authorization Bypass Vulnerability', '思科IOS安全复制授权绕过漏洞', '高危', '该漏洞允许有效用户检索或写入设备文件系统上的任何文件，包括设备保存的配置。此配置文件可能包括密码或其他敏感信息。IOS安全复制服务器是一个可选的服务，默认情况下是禁用的。没有特别配置以启用IOS安全复制服务器服务的设备不受此漏洞影响。', '应用Cisco Security Advisory Cisco -sa-20070808-scp中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49010, 'Multiple DLSw Denial of Service Vulnerabilities in Cisco IOS - Cisco Systems', '多个DLSw拒绝服务存在思科IOS', '高危', 'Cisco IOS包含数据链路交换(DLSw)特性中的多个漏洞，这些漏洞在处理特别设计的UDP或IP协议91包时可能导致重新加载或内存泄漏。', '应用Cisco Security Advisory Cisco -sa-20080326-dlsw中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49011, 'Cisco IOS User Datagram Protocol Delivery Issue For IPv4/IPv6 Dual-stack Routers - Cisco Systems', '思科IOS用户数据报协议交付问题的IPv4/IPv6双栈路由器-思科系统', '高危', '运行Cisco IOS软件并启用了Internet协议version 6 (IPv6)的设备可能会受到拒绝服务(DoS)攻击。 要使设备受到此漏洞的影响，设备还必须启用某些Internet协议version 4 (IPv4)用户数据报协议(UDP)服务。要利用这个漏洞，必须将攻击IPv6数据包定向到设备。通过路由器路由的数据包不能触发此漏洞。成功的利用将阻止接口接收任何额外的通信量。唯一的例外是资源保留协议(RSVP)服务，如果被利用，将导致设备崩溃，只有利用漏洞的接口才会受到影响。', '应用Cisco Security Advisory Cisco -sa-20080326- ipv4ipv6的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49012, 'Cisco IOS Multicast Virtual Private Network (MVPN) Data Leak - Cisco Systems', '思科IOS多播虚拟专用网(MVPN)数据泄露——思科系统', '高危', '多播的脆弱性在思科实现虚拟专用网(MVPN)受到剥削,允许恶意用户可以创建额外的多播州核心路由器或接收多播流量从其他基于多协议标签交换(MPLS)的虚拟专用网络(VPN)通过发送特殊的消息。', '应用Cisco Security Advisory Cisco -sa-20080326-mvpn中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49016, 'SNMP Version 3 Authentication Vulnerabilities (cisco-sa-20080610-snmpv3)', 'Net-SNMP认证绕过漏洞', '紧急', 'Net-SNMP处理认证时存在漏洞。Net-SNMP的认证代码依赖于用户输入中所指定的HMAC长度读取所要检查的长度。SNMPv3的认证是使用HMAC实现的，如果用户在认证代码字段中提供了单字节的HMAC代码的话，由于仅会检查第一个字节，因此就会有1/256的概率匹配正确的HMAC并通过认证，这大大的提高了暴力猜测的成功率。攻击者可利用此漏洞读取和修改任何使用登录系统的认证凭据可访问的SNMP对象。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20080610-snmpv3.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49017, 'Multiple Cisco Products Vulnerable to DNS Cache Poisoning Attacks', '多家厂商DNS实现缓存中毒漏洞', '中危', 'DNS协议是TCP/IP协议组的一部分，允许DNS客户端查询DNS数据库将主机名解析为IP地址。 出于与处理DNS查询相关的处理时间和带宽考虑，大多数DNS服务器都会本地存储从其他DNS服务器所接收到的响应，存储这些响应的区域被称为缓存。一旦将响应存储到了缓存，DNS服务器就可以在再次查询DNS服务器以刷新本地缓存的响应拷贝之前的一段时间(被称为存活时间)使用本地存储的响应。 DNS缓存中毒攻击指的是更改了DNS服务器的DNS缓存中某项，这样缓存中与主机名相关的IP地址就不再指向正确的位置。例如，如果www.example.com映射到IP地址192.168.0.1且DNS服务器的缓存中存在这个映射，则成功向这个服务器的DNS缓存投毒的攻击者就可以将www.example.com映射到10.0.0.1。在这种情况下，试图访问www.example.com的用户就可能与错误的Web服务器联络。 DNS协议实现规范中包括一个16位的事件ID字段。如果正确地实现了这个规范且通过强随机数生成器随机的选择事件ID的话，攻击者就需要平均32,768次尝试才能成功的预测到这个ID。但由于协议实现中的弱点，用于验证DNS响应的DNS事件ID和源端口号随机性不够强，可以轻易的预测，这就允许攻击者创建匹配期望值的DSN请求伪造响应，而DNS服务器会认为该响应有效，因此简化了缓存中毒攻击。 成功利用这个漏洞可能导致DNS服务器的用户联络错误的网络服务供应商，最终的影响各不相同，从简单的拒绝服务到网络钓鱼和金融诈骗。', '应用思科安全通报cisco-sa-20080708-dns中的相关补丁修复', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49019, 'Cisco IOS IPS Denial of Service Vulnerability - Cisco Systems', 'Cisco IOS IPS功能SERVICE.DNS拒绝服务漏洞', '高危', '如果Cisco IOS启用了入侵保护系统(IPS)功能的话，则可以触发SERVICE.DNS引擎的IPS特征的网络通讯可能导致路由器崩溃或挂起，造成拒绝服务的情况。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20080924-iosips.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49020, 'Cisco 10000, uBR10012, uBR7200 Series Devices IPC Vulnerability - Cisco Systems', 'Cisco IOS远程IPC拒绝服务漏洞', '高危', 'Cisco 10000、uBR10012和uBR7200系列设备使用基于UDP的IPC信道，这个信道使用127.0.0.0/8范围的地址和UDP 1975端口。运行受影响版本Cisco IOS的Cisco 10000、uBR10012和uBR7200系列设备会处理设备外部发送给UDP 1975端口的IPC消息，攻击者可以利用这种行为导致设备或线卡或以上二者同时重载，造成拒绝服务。', '应用思科安全通报cisco-sa-20080924-ipc中的相关补丁修复', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49022, 'Cisco IOS MPLS Forwarding Infrastructure Denial of Service Vulnerability - Cisco Systems', 'Cisco IOS MPLS转发基础架构远程拒绝服务漏洞', '高危', '在新版Cisco IOS软件中引入了新的报文转发基础架构以提高可扩展性和性能。这个转发架构叫做MFI。运行Cisco IOS软件且支持MFI的设备如果配置了MPLS的话，就会受拒绝服务攻击的影响，远程攻击者可以发送恶意报文导致设备重载。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20080924-mfi.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49023, 'Multiple Multicast Vulnerabilities in Cisco IOS Software - Cisco Systems', '思科IOS系统中多个多播漏洞', '高危', '在Cisco IOS软件中存在两种独立协议多播(PIM)包漏洞，这可能导致拒绝服务(DoS)条件。', '应用Cisco Security Advisory Cisco -sa-20080924多播中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49025, 'Multiple Cisco IOS Session Initiation Protocol Denial of Service Vulnerabilities', 'Cisco IOS SIP报文处理内存泄露拒绝服务漏洞', '高危', 'Cisco IOS的SIP协议实现中的内存泄露或导致拒绝服务攻击。运行受影响的Cisco IOS版本且处理SIP消息的Cisco设备受影响。这些漏洞的唯一要求就是Cisco IOS设备所配置的VoIP功能处理了SIP消息。远程攻击者通过未明有效SIP消息导致拒绝服务。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20080924-sip.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49028, 'Cisco IOS MPLS VPN May Leak Information - Cisco Systems', 'Cisco IOS MPLS VPN信息泄露漏洞', '中危', '运行Cisco IOS的设备如果配置了MPLS VPN或VRF Lite的话，在CE与PE设备之间使用BGP可能允许在VPN之间广播信息。如果使用了扩展团体，MPLS VPN可能错误的使用被破坏的路由目标(RT)转发通讯。在这种情况下，通讯可能从一个MPLS VPN泄露给另一个。 只要受影响的PE设备在MPLS VPN VRF中运行了BGP会话就会出现这个漏洞，但攻击者无法可靠的利用这个漏洞。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20080924-vpn.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49030, 'Cisco IOS Software Multiple Features IP Sockets Vulnerability', '思科IOS软件多特性IP套接字漏洞', '高危', 'IP套接字处理中的漏洞可能会导致设备在启用Cisco IOS软件的任何一个特性时容易受到拒绝服务攻击。一系列特别设计的TCP/IP数据包可能导致以下任何结果', '应用Cisco Security Advisory Cisco -sa-20090325-ip中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49031, 'Cisco IOS Software Mobile IP and Mobile IPv6 Vulnerabilities - Cisco Systems', 'Cisco IOS移动IP/移动IPv6功能多个远程拒绝服务漏洞', '高危', '移动IP是IPv4和IPv6标准的一部分，允许通过单个IP地址识别主机设备。无论在不同网络之间如何移动，仍无需用户交互便可无缝的实现在不同点的连接。 运行受影响Cisco IOS软件版本且配置了移动IPv6或移动IP NAT遍历功能的设备受拒绝服务漏洞影响，成功利用这个漏洞可导致接口停止处理通讯，直到重启系统。攻击报文必须明确发送给路由器而不是中间报文的情况下才可以成功利用这个漏洞。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20090325-mobileip.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49032, 'Cisco IOS Software Secure Copy Privilege Escalation Vulnerability - Cisco Systems', 'Cisco IOS安全拷贝功能权限提升漏洞', '高危', '安全拷贝(SCP)协议类似于远程拷贝(RCP)协议，允许在系统之间传输文件。Cisco IOS软件中SCP实现的服务器端存在漏洞，可能允许附加了CLI视图的认证用户从配置为SCP服务器的Cisco IOS设备传输文件，无论CLI视图配置授权给哪些用户上述权限。这个漏洞允许有效用户在设备的系统文件上检索或写入任意文件(包括设备保存的配置和Cisco IOS镜像文件)，即使附加给用户的CLI视图不允许这些操作。配置文件中可能包含有口令或其他敏感信息。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20090325-scp.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49034, 'Cisco IOS Software Multiple Features Crafted TCP Sequence Vulnerability - Cisco Systems', '思科IOS软件的多重功能精心打造了TCP序列漏洞——思科系统', '高危', '思科IOS软件包含多种功能漏洞，攻击者可以在受影响的设备上造成拒绝服务(DoS)状态。一系列特别设计的TCP包可能导致脆弱的设备重新加载。', '应用Cisco Security Advisory Cisco -sa-20090325-tcp中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49035, 'Cisco IOS Software Multiple Features Crafted UDP Packet Vulnerability - Cisco Systems', '思科IOS软件的多重功能精心打造了UDP数据包漏洞——思科系统', '高危', '思科IOS软件中的一些特性受到UDP包漏洞的影响。如果启用了任何受影响的特性，成功的攻击将导致入站接口上的输入队列阻塞。只有专门为设备设计的UDP数据包才会导致接口被阻塞。过境交通不会阻塞接口。', '应用Cisco Security Advisory Cisco -sa-20090325-udp中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49036, 'Cisco IOS Software WebVPN and SSLVPN Vulnerabilities - Cisco Systems', 'Cisco IOS安全拷贝功能权限提升漏洞', '高危', '安全拷贝(SCP)协议类似于远程拷贝(RCP)协议，允许在系统之间传输文件。Cisco IOS软件中SCP实现的服务器端存在漏洞，可能允许附加了CLI视图的认证用户从配置为SCP服务器的Cisco IOS设备传输文件，无论CLI视图配置授权给哪些用户上述权限。这个漏洞允许有效用户在设备的系统文件上检索或写入任意文件(包括设备保存的配置和Cisco IOS镜像文件)，即使附加给用户的CLI视图不允许这些操作。配置文件中可能包含有口令或其他敏感信息。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20090325-scp.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49038, 'TCP State Manipulation Denial of Service Vulnerabilities in Multiple Cisco Products - Cisco Systems', 'TCP状态操纵拒绝服务漏洞在多个思科产品-思科系统', '高危', '多个Cisco产品受到拒绝服务(DoS)漏洞的影响，这些漏洞操纵传输控制协议(TCP)连接的状态。通过操作TCP连接的状态，攻击者可以强制TCP连接保持长期的状态，可能是无限期的。如果有足够多的TCP连接被迫处于长期或不确定的状态，则可能会消耗系统上遭受攻击的资源，从而阻止接受新的TCP连接。在某些情况下，为了恢复正常的系统操作，可能需要重新启动系统。要利用这些漏洞，攻击者必须能够与脆弱的系统完成TCP三向握手。除了这些漏洞，思科Nexus 5000设备还包含一个TCP DoS漏洞，可能导致系统崩溃', '应用Cisco Security Advisory Cisco -sa-20090908-tcp24中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49040, 'Cisco IOS Software Authentication Proxy Vulnerability - Cisco Systems', '思科IOS软件认证代理漏洞-思科系统', '高危', '配置了HTTP(S)、Web身份验证或同意特性的身份验证代理的Cisco IOS软件包含一个漏洞，该漏洞允许未经身份验证的会话绕过身份验证代理服务器或绕过同意页面。', '应用Cisco Security Advisory Cisco -sa-20090923-auth-proxy中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49042, 'Cisco IOS Software H.323 Denial of Service Vulnerability - Cisco Systems', 'Cisco IOS H.323报文拒绝服务漏洞', '高危', 'Cisco IOS是美国思科（Cisco）公司为其网络设备开发的操作系统。 Cisco IOS软件的H.323实现中存在漏洞，攻击者可以通过向运行Cisco IOS软件的设备发送特制H.323报文导致设备重载。必须完成TCP三重握手才能利用这个漏洞。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.cisco.com/warp/public/707/cisco-sa-20090923-cme.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49044, 'Cisco IOS Software Internet Key Exchange Resource Exhaustion Vulnerability - Cisco Systems', 'Cisco IOS IKE报文拒绝服务漏洞', '高危', '如果使用了基于证书的认证，则Cisco IOS软件的IKE实现中存在漏洞。成功利用这个漏洞可能导致分配所有可用的Phase 1 SA，无法创建新的IPSec会话。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.cisco.com/warp/public/707/cisco-sa-20090923-ipsec.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49048, 'Cisco IOS Software Tunnels Vulnerability - Cisco Systems', '思科IOS软件隧道漏洞-思科系统', '高危', '如果配置为IP隧道和Cisco Express转发，运行受影响版本的Cisco IOS软件的思科设备很容易受到拒绝服务(DoS)攻击。', '应用Cisco Security Advisory Cisco -sa-20090923隧道中引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49049, 'Cisco Unified Communications Manager Express Denial of Service Vulnerabilities (cisco-sa-20100324-cucme)', 'Cisco IOS Unified CME和Unified SRST组件多个拒绝服务漏洞', '高危', '当Cisco Unified Communications Manager Express (CME) 或 Cisco Unified Survivable Remote Site Telephony (SRST)功能启用时，攻击者就可以注册自己的IP地址然后发送畸形负载利用这些漏洞，导致拒绝服务。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.cisco.com/warp/public/707/cisco-sa-20100324-cucme.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49050, 'Cisco IOS Software H.323 Denial of Service Vulnerabilities (cisco-sa-20100324-h323)', 'Cisco IOS H.323 拒绝服务漏洞', '高危', 'Cisco IOS的H.323包存在拒绝服务漏洞。远程攻击者可以借助畸形H.323包，导致拒绝服务。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.cisco.com/warp/public/707/cisco-sa-20100324-bundle.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49051, 'Cisco IOS Software IPsec Vulnerability (cisco-sa-20100324-ipsec)', 'Cisco IOS IPsec Internet Key Exchange (IKE) Malformed Packet 拒绝服务漏洞', '高危', 'Cisco IOS的IPsec Internet Key Exchange (IKE) Malformed IPsec Internet Key Exchange (IKE) Malformed Packet 存在拒绝服务漏洞。远程攻击者可以借助畸形IKE包，导致拒绝服务（设备重载）。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.cisco.com/web/about/security/intelligence/Cisco_ERP_mar10.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49052, 'Cisco IOS Software Multiprotocol Label Switching Packet Vulnerability (cisco-sa-20100324-ldp)', '思科IOS软件多协议标签交换包漏洞(Cisco -sa-20100324-ldp)', '高危', '如果配置为多协议标签交换(MPLS)并支持标签分发协议(LDP)，就容易受到远程拒绝服务(DoS)条件的影响。一个精心制作的LDP UDP数据包可能会导致一个运行Cisco IOS软件或Cisco IOS XE软件的设备重新加载。在运行受影响的思科IOS XR软件版本的设备上，这些数据包可能导致设备重启mpls_ldp进程。如果系统配置了LDP或Tag Distribution Protocol (TDP)，那么系统就很容易受到攻击。', '应用Cisco Security Advisory Cisco -sa-20100324-ldp引用的相关补丁。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49054, 'Cisco IOS Software Session Initiation Protocol Denial of Service Vulnerabilities (cisco-sa-20100324-sip)', 'Cisco IOS SIP Message 远程代码执行和拒绝服务漏洞', '紧急', 'Cisco IOS的SIP可执行程序存在远程代码执行与拒绝服务漏洞。远程攻击者可以借助畸形SIP信息执行任意的远程代码，导致拒绝服务（设备重载）。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://tools.cisco.com/security/center/viewAlert.x?alertId=20063', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49055, 'Cisco IOS Software Crafted TCP Packet Denial of Service Vulnerability (cisco-sa-20100324-tcp)', 'Cisco IOS Software 畸形TCP 包拒绝服务漏洞', '高危', 'Cisco IOS存在拒绝服务漏洞。当特定的PMTUD、 SNAT、或 window-size配置启用时，远程攻击者可以利用一个具有特制选项的TCP段信息，导致拒绝服务（无限循环和设备重载或挂起）。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.cisco.com/web/about/security/intelligence/Cisco_ERP_mar10.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49120, 'IBM DB2 9.5 < Fix Pack 6a Multiple Vulnerabilities', 'IBM DB2未明安全漏洞', '紧急', 'BM DB2 FP18之前的8版本，FP8之前的9.1版本，FP4之前的9.5版本，以及FP2之前的9.7版本没有完成对函数的特权流失的预期删除操作，这会引起未明影响和远程攻击向量。', '使用IBM DB2 V9.5 FP6或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49219, 'MS10-061: Vulnerability in Print Spooler Service Could Allow Remote Code Execution (2347290)', 'MS10-061：Windows Print Spooler服务输入验证漏洞', '高危', '打印后台程序服务中一个公开披露的漏洞。 如果攻击者将特制打印请求发送到通过 RPC 暴露打印后台程序接口的易受攻击系统，该漏洞可以允许远程执行代码。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://technet.microsoft.com/zh-CN/security/bulletin/ms10-061', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49272, 'HP System Management Homepage < 6.2 Multiple Vulnerabilities', 'HP系统管理主页< 6.2 存在多个漏洞', '高危', 'HP系统管理主页版本低于 6.2，存在多个安全漏洞：多个厂商TLS协议和SSL协议会话协商明文注入漏洞（CVE-2009-3555）、 PHP \'SESSION superglobal数组和session.save_path()\'未明任意代码执行漏洞（CVE-2009-4143）、HP系统管理主页RedirectUrl参数URI重新定向漏洞（CVE-2010-1586）', '升级HP系统管理主页版本至6.2及以上，相关链接： http://www.securityfocus.com/archive/1/513684/30/0/threaded http://www.securityfocus.com/archive/1/513771/30/0/threaded http://www.securityfocus.com/archive/1/513840/30/0/threaded http://www.securityfocus.com/archive/1/513917/30/0/threaded http://www.securityfocus.com/archive/1/513918/30/0/threaded http://www.securityfocus.com/archive/1/513920/30/0/threaded', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49648, 'Cisco IOS Software Session Initiation Protocol Denial of Service Vulnerabilities (cisco-sa-20100922-sip)', 'Cisco统一通讯管理器畸形SIP报文拒绝服务漏洞', '高危', 'Cisco统一通讯管理器（CUCM，之前被称为CallManager）是Cisco IP电话解决方案中的呼叫处理组件。 Cisco统一通讯管理器处理SIP时存在拒绝服务漏洞。远程攻击者通过畸形的SIP INVITE消息引起对sipSafeStrlen函数的不正确调用从而造成拒绝服务（voice-services资源用尽）。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www.cisco.com/warp/public/707/cisco-sa-20090826-cucm.shtml', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49690, 'IBM WebSphere Application Server 6.0 < 6.0.2.43 Multiple Vulnerabilities', 'IBM WebSphere Application Server Web Container信息泄露漏洞', '中危', 'IBM WebSphere Application Server (WAS)的Web Container在response.sendRedirect调用时，无法正确处理分块传输编码，远程攻击者可以通过GET请求引发拒绝服务。', '厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://www-01.ibm.com/support/docview.wss?rs=180&uid=swg27004980', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49691, 'IBM WebSphere Application Server 6.1 < 6.1.0.33 Multiple Vulnerabilities', 'IBM WebSphere应用服务版本低于6.1.0.33，存在多个安全漏洞', '紧急', 'IBM WebSphere应用服务版本低于 6.1.0.33，存在多个安全漏洞，例如管理控制台跨站脚本攻击漏洞；远程认证用户可以借助特制的URL导致拒绝服务(CPU消耗)；IBM WebSphere Application Server和Web Services WebSphere Application Server Feature Pack输入验证漏洞等。', '如果使用WebSphere应用服务, 应用修复包Fix Pack 33 (6.1.0.33)及以上版本，如果使用嵌入式WebSphere应用服务包，则推荐应用最新的 eWAS fix pack修复包。 相关链接： http://www-01.ibm.com/support/docview.wss?uid=swg21404665 http://www-01.ibm.com/support/docview.wss?uid=swg27009778 http://www-01.ibm.com/support/docview.wss?uid=swg1PM02636 http://www-01.ibm.com/support/docview.wss?uid=swg21443736 http://www-01.ibm.com/support/docview.wss?uid=swg1PM12065 http://www-01.ibm.com/support/docview.wss?uid=swg27007951#61033', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (49996, 'Oracle Java SE Multiple Vulnerabilities (October 2010 CPU)', '多个厂商TLS协议和SSL协议会话协商明文注入漏洞', '紧急', '传输层安全协议（TLS）是确保互联网上通信应用和其用户隐私的协议。 Apache HTTP Server 2.2.14及之前版本，OpenSSL 0.9.8l之前版本，GnuTLS 2.8.5及之前版本，Mozilla Network Security Services (NSS) 3.12.4及之前版本，多个Cisco产品，以及其他产品的TLS协议和SSL协议中存在会话协商明文注入漏洞。由于TLS协议和SSL协议实现模块没有适当将会话协商与现存连接关联，中间人攻击者可以通过发送一个未认证的请求，将数据注入到受TLS和SSL协议保护的HTTP会话和其它类型会话中。', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://cvs.openssl.org/chngview?cn=18790 http://www.gnu.org/software/gnutls/download.html http://httpd.apache.org/download.cgi', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50069, 'Apache 2.0 < 2.0.64 Multiple Vulnerabilities', 'Apache版本低于 2.0.64', '高危', 'Apache版本较低，导致存在多处漏洞。', '升级apache版本到2.0.64 以上 http://httpd.apache.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50070, 'Apache 2.2 < 2.2.17 Multiple Vulnerabilities', 'Apache版本较低于2.2.17', '中危', 'Apache版本较低，导致存在多处漏洞。', '升级apache版本到2.2.17以上 http://httpd.apache.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50451, 'IBM DB2 9.7 < Fix Pack 3 Multiple Vulnerabilities', 'IBM DB2 9.7 < Fix Pack 3 存在多个漏洞', '中危', 'IBM DB2容易出现多个远程漏洞。 这些问题的成功利用将允许攻击者绕过某些限制，执行未经授权的操作，并在数据库中获得更高的特权。 攻击者可能需要一个有效的用户帐户来执行此攻击。 IBM DB2 9.7 Fix Pack 2和以前的版本都会受到影响', '应用IBM DB2 version 9.7 Fix Pack 3或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50561, 'IBM WebSphere Application Server 7.0 < Fix Pack 13 Multiple Vulnerabilities', 'IBM WebSphere Application Server未明漏洞', '高危', 'IBM WebSphere Application Server (WAS) 6.1.0.33之前的6.1版本的管理员控制台存在未明漏洞。远程认证用户可以借助特制的URL导致拒绝服务(CPU消耗)。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?uid=swg27007951#61033', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50562, 'Adobe Flash Media Server < 3.0.7 / 3.5.5 / 4.0.1 Multiple Vulnerabilities (APSB10-27)', 'Adobe Flash Media Server内存泄露漏洞（版本低于3.0.7 / 3.5.5 / 4.0.1 ）', '紧急', 'Adobe Flash Media Server 将传统的流媒体功能与灵活的开发环境结合起来, 为最广泛的群体创建和提供创新的、交互式媒体应用。 Adobe Flash Media Server(FMS)3.0.7之前的3.0.x版本，3.5.5之前的3.5.x版本，4.0.1之前的4.0.x版本中存在内存泄露漏洞。攻击者可以借助未明向量导致拒绝服务(内存消耗)。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.adobe.com/support/security/bulletins/apsb10-27.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50652, 'Oracle Database, October 2010 Critical Patch Update', 'Oracle数据库2010年10月紧急补丁更新', '高危', 'Oracle数据库服务未修复2010年10月份的补丁包（CPU），企业管理控制器、JAVA虚拟器、OLAP、JOB查询、XDK、核心RDBMS、Perl层面可能存在安全问题。', '应用2010年10月份的补丁包（cpu），相关链接： http://www.nessus.org/u?3ed3b5c9', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50659, 'Serv-U < 10.3.0.1 SFTP Authentication Bypass', 'Serv-U 版本低于 10.3.0.1 SFTP 认证绕过漏洞', '中危', '如果SFTP server配置为允许通过public key 进行认证,则攻击者则无知道密码即可绕过登录验证。', '升级 Serv-U 版本到10.3.0.1 以上', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50686, 'IP Forwarding Enabled', 'IP转发功能未禁用', '中危', 'IP转发在非路由器或防火墙的机器上可用。', '如果该设备为路由器或防火墙，则可忽悠该问题； 否则建议关闭IP转发功能。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (50831, 'Trend Micro OfficeScan TMTDI Module Local Privilege Escalation', '趋势科技防毒墙网络版TMTDI模块本地特权升级', '高危', '远程主机要么运行趋势科技防毒墙网络版或趋势 科技防毒墙网络版客户端。附带的安装模块TMTDI 版本是由未指定的漏洞，可能允许受影响的 本地攻击者远程系统上执行任意代码。', 'V10.0 -升级到Service Pack 1修补2，并应用 关键补丁2820 - V10.5 -应用关键补丁1161. http://www.trendmicro.com/ftp/documentation/readme/Readme_2820.txt http://www.trendmicro.com/ftp/documentation/readme/Readme_1161.txt', 'CVE-2008-7270', NULL);
INSERT INTO `scan_suggest_log` VALUES (51059, 'Apache Tomcat 5.0.x <= 5.0.30 / 5.5.x < 5.5.25 Multiple Vulnerabilities', 'Apache Tomcat 5.0.x<=5.0.30/5.5.x<5.5.25多个漏洞', '中危', '根据其自我报告的版本号，在远程主机上运行的apachetomcat实例是5.0.x等于或早于5.0.30或5.5.25之前的5.5.x。因此，它受到多个漏洞的影响：-多个JSP示例文件中存在一个错误，允许使用“；”通过URL注入脚本性格(CVE-2007-2449）-管理器和主机管理器应用程序未正确清理“/Manager/html/upload”脚本的“filename”参数，这可能导致跨站点脚本攻击(CVE-2007-2450）-处理包含单引号的cookie值时存在错误，Tomcat将其视为分隔符。这可能允许泄露敏感信息，如会话ID(CVE-2007-3382）-处理包含反斜杠（Tomcat将其视为分隔符）的cookie值时存在错误。这可能允许泄露敏感信息，如会话ID(CVE-2007-3385）-主机管理器应用程序中存在允许脚本注入的错误(（CVE-2007-3386）请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 5.5.25版本。或者，对5.0.x使用最新的SVN源。5.0.x分支在SVN修订号588821中固定。', 'CVE-2007-2449', '1');
INSERT INTO `scan_suggest_log` VALUES (51139, 'PHP 5.2 < 5.2.15 Multiple Vulnerabilities', 'PHP版本低于5.2.15', '中危', 'Apache版本低，可能会受到多个漏洞影响。', '升级PHP版本到5.2.15以上', NULL, NULL);
