INSERT INTO `scan_suggest_log` VALUES (51368, 'iSCSI Unauthenticated Target Detection', 'iSCSI认证目标检测', '高危', '远程主机上的一个或多个的iSCSI（互联网小型计算机系统接口） 被配置为不使用身份验证机制，可能允许未经授权的对象访问目标。', '配置身份验证策略，限制访问授权人。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (51418, 'HP StorageWorks MSA P2000 Default Credentials', 'HP StorageWorks MSA P2000 默认账号口令', '紧急', 'HP StorageWorks是系列存储备份产品家族。 HP StorageWorks MSA P2000 存在默认账号口令未修改。', '[1] 禁用不需要使用的账号 [2] 修改默认账号口令', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (51439, 'PHP 5.2 < 5.2.17 / 5.3 < 5.3.5 String To Double Conversion DoS', 'PHP 版本低于5.2.17 / 5.3 < 5.3.5 拒绝服务攻击漏洞', '中危', 'PHP版本低于5.2.17或低于5.3.5，这可能受到拒绝服务攻击。', '升级PHP版本至5.3.5及以上版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (51510, 'IBM WebSphere Application Server 6.1 < 6.1.0.35 Multiple Vulnerabilities', 'IBM WebSphere应用服务器版本低于6.1.0.35，存在多种安全漏洞', '中危', 'IBM WebSphere Application Server 是一个完善的、开放的Web应用服务器，是IBM电子商务应用架构的核心。 IBM WebSphere Application Server (WAS) 6.1.0.35之前的6.1版本，以及7.0.0.13之前的7.0版本的管理控制台中存在跨站脚本攻击漏洞。远程攻击者可以借助未明向量注入任意web脚本或者HTML。', '升级IBM WebSphere应用服务器版本至6.1.0.35及以上 相关链接： http://www-01.ibm.com/support/docview.wss?uid=swg21404665 http://www-01.ibm.com/support/docview.wss?uid=swg27009778 http://www-01.ibm.com/support/docview.wss?uid=swg27007951#61035', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (51573, 'Oracle Database, January 2011 Critical Patch Update', 'Oracle数据库2011年1月紧急补丁更新', '高危', 'Oracle数据库服务未修复2011年1月份的补丁包（CPU），客户端系统分析器、数据库、Oracle空间、UIX、调度器代理可能存在安全问题。', '应用2011年1月份的补丁包（cpu），相关链接： http://www.nessus.org/u?0fd6b4f5', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (51840, 'IBM DB2 9.1 < Fix Pack 10 Multiple Vulnerabilities', 'IBM DB2 9.1 < Fix Pack 10 存在多个漏洞', '紧急', 'IBM DB2 9.1 缺少补丁包 Fix Pack 10 ，从而导致存在多个漏洞。', '官方补丁下载地址： http://www-933.ibm.com/support/fixcentral/swg/selectFixes?parent=ibm~Information%2BManagement&product=ibm/Information+Management/DB2&release=9.1.*&platform=All&function=recommended', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (51841, 'IBM DB2 9.5 < Fix Pack 7 Multiple Vulnerabilities', 'IBM DB2 DAS组件缓冲区溢出漏洞', '紧急', '基于Linux，UNIX，以及Windows平台的IBM DB2 FP10之前的9.1版本，FP7之前的9.5版本，以及FP3之前的9.7版本中的DB2 Administration Server(DAS)组件中存在缓冲区溢出漏洞。远程攻击者可以借助未明向量执行任意代码。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://www-304.ibm.com/support/docview.wss?uid=swg1IC71203', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (51892, 'OpenSSL SSL_OP_NETSCAPE_REUSE_CIPHER_CHANGE_BUG Session Resume Ciphersuite Downgrade Issue', 'OpenSSL密码集降级安全漏洞', '中危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。当SSL_OP_NETSCAPE_REUSE_CIPHER_CHANGE_BUG启用时，OpenSSL 0.9.8q之前版本及1.0.0c之前的1.0.x版本不能正确阻止会话缓存中对加密套接字的修改。远程攻击者可借助和嗅探网络通信以发现会话标识符有关的向量强迫意外密码降低保密程度。', '升级版本至 OpenSSL 0.9.8q / 1.0.0.c或者联系厂商。 相关链接： http://openssl.org/news/secadv_20101202.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (51893, 'OpenSSL SSL_OP_NETSCAPE_REUSE_CIPHER_CHANGE_BUG Ciphersuite Disabled Cipher Issue', 'OpenSSL OpenSSL实现加密问题漏洞', '中危', '当SSL_OP_NETSCAPE_REUSE_CIPHER_CHANGE_BUG启用时，OpenSSL 0.9.8j之前版本不能阻止会话缓存中对密码套件的修改。远程攻击者可以借助和嗅探网络通信以发现会话标识符有关的向量强迫使用禁用的密码。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.openssl.org/source/openssl-0.9.8j.tar.gz', 'CVE-2010-4422', NULL);
INSERT INTO `scan_suggest_log` VALUES (52002, 'Oracle Java SE Multiple Vulnerabilities (February 2011 CPU)', 'Oracle Java SE和Java for Business远程JAVA运行时环境漏洞', '紧急', 'Oracle Java SE是标准版的Java平台是一个Java2的平台，为用户提供一个程序开发环境。这个程序开发环境提供了开发与运行Java软件的编译器等开发工具、软件库及Java虚拟机。它也是Java2平台、企业版本和Java网页服务的基础。 Java for Business是一套自定义的Java SE改版，企业可通过它快速访问关键修复，特点是长期支持和企业级定位。 Oracle Java SE和Java for Business 6 Update 23及之前版本中的Java运行时环境（JRE）中存在未明漏洞。远程攻击者可以借助和Deployment有关的未知向量影响保密性、完整性和可用性。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.oracle.com/technetwork/topics/security/javacpufeb2011-304611.html', 'CVE-2011-0786', NULL);
INSERT INTO `scan_suggest_log` VALUES (52609, 'IMAP Service STARTTLS Plaintext Command Injection', 'IMAP Server STARTTLS 明文命令注入漏洞', '中危', 'IMAP Server 中的STARTTLS实现不能正确限制I/O缓冲。中间人攻击者可以在TLS实现就位后，通过发送已处理的明文命令，向加密的SMTP会话插入命令。', '请根据当前IMAP Server 所使用的软件厂商，获取相应的补丁升级程序', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (52610, 'POP3 Service STLS Plaintext Command Injection', 'Postfix STARTTLS实现明文命令注入漏洞', '中危', 'Postfix 2.4.16之前的2.4.x版本，2.5.12之前的2.5.x版本，2.6.9之前的2.6.x版本，以及2.7.3之前的2.7.x版本中的STARTTLS实现没有正确限制I/O缓冲作用。中间人攻击者可以通过发送明文命令（在TLS就位后得到处理），向加密的SMTP会话注入任意命令。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.postfix.cn/source/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (52611, 'SMTP Service STARTTLS Plaintext Command Injection', 'SMTP服务STARTTLS明文命令注入', '中危', '远程邮件服务在协商加密通信隧道时，允许明文命令注入，这允许远程未认证的的攻击者在明文协议阶段注入命令，使其在密文协议阶段执行。', '联系厂商获取相关补丁包。 http://tools.ietf.org/html/rfc2487 http://www.securityfocus.com/archive/1/516901/30/0/threaded', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (52615, 'IBM WebSphere Application Server 7.0 < Fix Pack 15 Multiple Vulnerabilities', 'IBM WebSphere Application Server 7.0 < Fix Pack 15 存在多个漏洞', '高危', 'IBM WebSphere Application Server 7.0 缺少补丁包Fix Pack 15，从而导致存在多个漏洞。', 'WebSphere Application Server 7.0.0补丁下载地址如下： https://www-947.ibm.com/support/entry/portal/product/websphere/websphere_application_server?productContext=-1917189887', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (52633, 'Unprotected memcached', '不受保护的memcached', '中危', 'Memcached是一个基于内存的对象存储。该程序不包含任何安全机制（即：身份验证），任何人都可以连接到此服务器并对其执行查询。', '确保机器受到防火墙的正确保护，限制主机权限。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (52669, 'Host is Listed in Known Bot Database', '主机存在已知僵死主机列表中', '高危', '远程主机作为僵尸网络的一部分列在公共数据库中。 这意味着它已被感染并用于攻击第三方 主办和/或进行非法活动。', '从头重新安装远程系统', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (52717, 'PHP 5.3 < 5.3.6 Multiple Vulnerabilities', 'PHP 版本低于 5.3.6', '高危', 'PHP版本较低，导致存在多个漏洞。', '建议升级 PHP 5.3.6 或者最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53337, 'JBoss Enterprise Application Platform \'/jmx-console\' Authentication Bypass', 'JBoss企业应用平台JMX控制台存在安全绕过漏洞', '中危', 'JBoss Enterprise Application Platform WEB控制台(/web-console)缺少正确限制，攻击者提交没有指定GET或POST的HTTP请求绕过验证，访问系统。JBOSS在配置HTTP verbs时，访问控制的实现存在安全问题，导致如HEAD，PUT或DELETE等未在配置文件中允许的verb能够直接以jbossadmin身份调用GET处理器, 从而绕过认证。', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接： http://kbase.redhat.com/faq/docs/DOC-30741 http://xforce.iss.net/xforce/xfdb/58147 http://secunia.com/advisories/39563/ http://www.securityfocus.com/bid/39710 http://blog.mindedsecurity.com/2010/04/good-bye-critical-jboss-0day.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53377, 'MS11-020: Vulnerability in SMB Server Could Allow Remote Code Execution (2508429)', 'MS11-020：SMB远程代码执行漏洞', '紧急', 'Microsoft Server Message Block (SMB)协议软件处理特制SMB报文时存在未验证远程代码执行漏洞。无需验证即可利用此漏洞，攻击者通过发送特制的SMB报文到运行服务器服务的计算机利用此漏洞。成功利用此漏洞可完全控制受影响系统', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://technet.microsoft.com/zh-CN/security/bulletin/ms11-020', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53387, 'MS11-030: Vulnerability in DNS Resolution Could Allow Remote Code Execution (2509553)', 'MS11-030：DNS 解析远程代码执行漏洞', '高危', 'Windows DNS 解析中存在漏洞。如果攻击者获得网络访问权限，然后创建一个自定义程序以将特制的 LLMNR 广播查询发送到目标系统，则该漏洞可能允许远程执行代码。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://technet.microsoft.com/zh-CN/security/bulletin/ms11-030', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53475, 'IBM WebSphere Application Server 6.1 < 6.1.0.37 Multiple Vulnerabilities', 'IBM WebSphere应用服务器版本低于6.1.0.37，存在多种安全漏洞', '中危', 'IBM WebSphere Application Server 是一个完善的、开放的Web应用服务器，是IBM电子商务应用架构的核心。 IBM WebSphere Application Server (WAS) 6.1.0.37之前的6.1版本管理控制台中存在多种安全漏洞。', '升级IBM WebSphere应用服务器版本至6.1.0.36及以上 相关链接： http://www-01.ibm.com/support/docview.wss?uid=swg21404665 http://www-01.ibm.com/support/docview.wss?uid=swg27009778 http://www-01.ibm.com/support/docview.wss?uid=swg21473989 http://www-01.ibm.com/support/docview.wss?uid=swg1PM19534 http://www-1.ibm.com/support/docview.wss?uid=swg1PM24668 http://www-01.ibm.com/support/docview.wss?uid=swg27007951#61037', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53503, 'MS11-020: Vulnerability in SMB Server Could Allow Remote Code Execution (2508429) (remote check)', 'Microsoft Windows SMB操作解析远程代码执行漏洞', '紧急', 'Microsoft Windows是微软发布的非常流行的操作系统。 Microsoft Server Message Block (SMB)协议软件处理特制SMB报文时存在未经身份验证的远程代码执行漏洞。利用该漏洞不要求进行身份验证，从而使攻击者通过发送特制的SMB报文到运行Server服务的计算机来利用此漏洞。成功利用此漏洞可完全控制受影响系统。', '目前厂商已发布Windows XP, Vista, 2008, 7, and 2008 R2平台的相关补丁 相关链接： http://technet.microsoft.com/en-us/security/bulletin/ms11-020', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53514, 'MS11-030: Vulnerability in DNS Resolution Could Allow Remote Code Execution (2509553) (remote check)', 'MS11-030：DNS 解析远程代码执行漏洞', '紧急', 'Windows DNS 解析中存在漏洞。如果攻击者获得网络访问权限，然后创建一个自定义程序以将特制的 LLMNR 广播查询发送到目标系统，则该漏洞可能允许远程执行代码。', '目前厂商已经发布了Windows XP, 2003, Vista, 2008, 7, and 2008 R2平台的升级补丁包以修复此安全问题，补丁获取链接： http://technet.microsoft.com/zh-CN/security/bulletin/ms11-030', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53532, 'HP System Management Homepage < 6.3 Multiple Vulnerabilities', 'HP系统管理主页版本低于 6.3，存在多个安全漏洞', '紧急', 'HP系统管理主页版本低于6.3，存在多个安全漏洞，例如PHP堆栈消耗漏洞（CVE-2010-1917）、PHP var_export函数信息泄露漏洞（CVE-2010-2531）、OpenSSL客户端ssl3_get_key_exchange函数未明漏洞（CVE-2010-2939）、PHP \'stream.c\'格式化字符串漏洞（CVE-2010-2950）、PHP ZipArchive::getArchiveComment函数输入验证漏洞(CVE-2010-3709)等', '升级HP系统管理主页版本至6.2及以上，相关链接： http://www.securityfocus.com/archive/1/517597/30/0/threaded', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53534, 'IBM Lotus Domino iCalendar Email Address ORGANIZER:mailto Header Remote Overflow', 'IBM Lotus Domino iCalendar邮件地址解析栈溢出漏洞', '高危', 'Lotus Domino是集电子邮件、文档数据库、快速应用开发技术以及Web技术为一体的电子邮件与群集平台。 IBM Lotus Domino 8.0.2 FP5之前的8.0.x版本以及8.5.1 FP2之前的8.5.x版本的服务器的nrouter.exe服务中的nnotes.dll文件的MailCheck821Address函数中存在基于栈的缓冲区溢出漏洞。远程攻击者可以借助超长电子邮件地址执行任意代码，该邮件地址位于iCalendar calendar-invitation电子邮件消息中的ORGANIZER:mailto头中。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?uid=swg21446515', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53547, 'IBM DB2 9.7 < Fix Pack 4 Multiple Vulnerabilities', 'IBM DB2 9.7 < Fix Pack 4 存在多个漏洞', '中危', '根据其版本，它受到下列一个或多个问题的影响，可以利用关系数据服务组件中未指定的错误来更新表的统计信息，而不需要适当的特权。关系数据服务组件中的一个错误可能会在角色成员资格从组中撤销后授予用户执行非ddl语句的权限。', '应用IBM DB2 version 9.7 Fix Pack 4或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53641, 'HP Data Protector Remote Command Execution', 'HP Data Protector客户端任意Perl代码执行漏洞', '紧急', 'HP Data Protector 是一款成熟的企业级数据保护平台，可不受距离限制通过磁盘或磁带自动执行高性能备份和恢复。 HP Data Protector中的客户端没有正确验证EXEC_CMD参数。远程攻击者可以借助特制命令执行任意Perl代码。', '1.HP Data Protector升级到 A.06.20 2.服务器和客户端上启用加密控制通信（ECC）服务。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53847, 'FTP Service AUTH TLS Plaintext Command Injection', 'FTP服务认证TLS明文命令注入', '中危', '远程FTP服务器在其AUTH TLS实现中包含一个软件缺陷，该缺陷可能允许远程、未经身份验证的攻击者在明文协议阶段注入将在密文协议阶段执行的命令。成功利用该漏洞可以允许攻击者修改FTP服务器上的文件并暴露用户的凭据。', '请与供应商联系，看看是否有更新可用。', 'CVE-2011-1575', NULL);
INSERT INTO `scan_suggest_log` VALUES (53895, 'Adobe Flash Media Server < 3.5.6 / 4.0.2 Multiple Vulnerabilities (APSB11-11)', 'Adobe Flash Media Server存在多个安全漏洞（版本低于3.5.6 / 4.0.2）', '紧急', 'Adobe Flash Media Server是基于Flash应用程序的服务器，可提供运行交互式应用及音频视频流的环境。 Adobe Flash Media Server(FMS)3.5.6之前版本以及4.0.2之前的4.x版本中存在XML数据破坏漏洞。远程攻击者可以借助未明向量导致拒绝服务。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.adobe.com/support/security/bulletins/apsb11-11.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53896, 'Apache 2.2 < 2.2.18 APR apr_fnmatch DoS', 'Apache版本低于2.2.18', '中危', 'Apache版本较低，由于发生在 \'apr_fnmatch\'函数的错误，可能会受到DOS攻击。', '确保 \'IndexOptions\' 设置选项设置为 \'IgnoreClient\'，或者升级Apache版本至2.2.18及以上。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (53897, 'Oracle Database, April 2011 Critical Patch Update', 'Oracle数据库2011年4月紧急补丁更新', '高危', 'Oracle数据库服务未修复2011年2月份的补丁包（CPU），Oracle仓库、Oracle安全服务、应用程序服务级别管理、网络功能、Oracle帮助、UIX等可能存在安全问题。', '应用2011年1月份的补丁包（cpu），相关链接： http://www.nessus.org/u?0fd6b4f6', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (54582, 'SMTP Service Cleartext Login Permitted', '允许SMTP服务明文登录', '低危', '远程主机正在运行一个SMTP服务器，该服务器播发允许通过未加密的连接进行明文登录。如果使用不太安全的身份验证机制（即登录或普通身份验证），攻击者可以通过嗅探到服务器的流量来发现用户名和密码。', '将服务配置为仅在加密通道上支持不太安全的身份验证机制。', NULL, '1');
INSERT INTO `scan_suggest_log` VALUES (54585, 'MS11-035: Vulnerability in WINS Could Allow Remote Code Execution (2524426) (uncredentialed check)', 'MS11-035: WINS服务允许远程代码执行漏洞', '高危', '主机wins服务可远程执行shellcode代码。', '微软厂商已发布windows2003，2008和2008 R2的相关补丁包。 http://technet.microsoft.com/en-us/security/bulletin/ms11-035', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (54997, 'Oracle Java SE Multiple Vulnerabilities (June 2011 CPU)', 'Oracle Java SE Deployment子组件远程Java运行时环境漏洞', '紧急', 'Java是由Sun Microsystems公司于1995年5月推出的Java程序设计语言和Java平台的总称。 当运行于Windows平台上时，Oracle Java SE 6 Update 25及之前版本中存在远程Java运行时环境漏洞。远程不可信Java Web Start应用程序和不可信Java applets可借助与Deployment子组件有关的未知向量影响机密性、完整性和可用性。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.oracle.com/technetwork/java/javase/downloads/index.html', 'CVE-2011-0331', NULL);
INSERT INTO `scan_suggest_log` VALUES (55128, 'MS11-048: Vulnerability in SMB Server Could Allow Denial of Service (2536275)', 'MS11-048：SMB 拒绝服务漏洞', '高危', '当攻击者向存在该漏洞的系统发送特制的 SMB 数据包后，漏洞系统会产生拒绝服务现象。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://technet.microsoft.com/library/security/ms11-048', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55169, 'IBM WebSphere Application Server 7.0 < Fix Pack 17 Multiple Vulnerabilities', 'IBM WebSphere Application Server 7.0 < Fix Pack 17 存在多个漏洞', '中危', 'IBM WebSphere Application Server 7.0 缺少补丁包Fix Pack 17，从而导致存在多个漏洞。', 'WebSphere Application Server 7.0.0补丁下载地址如下： https://www-947.ibm.com/support/entry/portal/product/websphere/websphere_application_server?productContext=-1917189887', NULL, NULL);
