INSERT INTO `scan_suggest_log` VALUES (55632, 'Oracle Database, July 2011 Critical Patch Update', 'Oracle数据库2011年7月紧急补丁更新', '高危', 'Oracle数据库服务未修复2011年7月份的补丁包（CPU），内容管理、数据库目标类型、SQL性能报告、安全架构、安全管理层等可能存在安全问题。', '应用2011年7月份的补丁包（cpu），相关链接： http://www.nessus.org/u?a7c55943', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55649, 'IBM WebSphere Application Server 6.1 < 6.1.0.39 Multiple Vulnerabilities', 'IBM WebSphere应用服务器版本低于6.1.0.39，存在多种安全漏洞', '中危', 'IBM WebSphere Application Server 是一个完善的、开放的Web应用服务器，是IBM电子商务应用架构的核心。 IBM WebSphere Application Server (WAS) 6.1.0.39之前的6.1版本管理控制台中存在多种安全漏洞。', '升级IBM WebSphere应用服务器版本至6.1.0.39及以上 相关链接： http://www-01.ibm.com/support/docview.wss?uid=swg21404665 http://www-01.ibm.com/support/docview.wss?uid=swg27009778 http://www-01.ibm.com/support/docview.wss?uid=swg27007951#61039', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55690, 'IBM DB2 Unsupported Version Detection', 'IBM DB2不再支持的版本', '紧急', '根据其版本号，不再支持在远程主机上安装IBM DB2。 缺乏支持意味着供应商不会发布该产品的新安全补丁。因此，它可能包含安全漏洞。', '升级到当前支持的IBM DB2版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55786, 'Oracle Database Unsupported', '当前ORACLE数据库版本厂商已不支持', '紧急', '根据远程主机安装的数据库版本，厂家已不再支持，将会缺少安全补丁包，因此，可能存在安全威胁。', '升级数据库版本至当前最新版本，至少升级至厂家支持的版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55802, 'MS 2562937: Update Rollup for ActiveX Kill Bits (2562937)', 'Honeywell ScanServer ActiveX控件释放后重用远程代码执行漏洞', '中危', 'Honeywell ScanServer是扫描服务器。 Honeywell ScanServer ActiveX控件780.0.20.5版本的addOSPLext()方法中存在释放后使用漏洞。远程攻击者可利用特制web页面，解引用已释放的内存，并可借助特制HTML文档执行任意代码。', '目前厂商还没有提供此漏洞的相关补丁或者升级程序，建议使用此软件的用户随时关注厂商的主页以获取最新版本： http://honeywell.com/Pages/Home.aspxF884F883:F885F883:F886F883:F887', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55811, 'Adobe Flash Media Server < 3.5.7 / 4.0.3 Denial of Service (APSB11-20)', 'Adobe Flash Media Server缓冲区溢出漏洞（版本低于3.5.7 / 4.0.3）', '高危', 'Adobe Flash Media Server是基于Flash应用程序的服务器，可提供运行交互式应用及音频视频流的环境。 Adobe Flash Media Server (FMS) 3.5.7之前版本和4.0.3之前的4.x版本中存在缓冲区溢出漏洞。远程攻击者可以借助未明向量导致拒绝服务（内存破坏）。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.adobe.com/support/security/bulletins/apsb11-20.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55814, 'Adobe Media Server Unsupported Version Detection', 'Adobe Media Server当前版本已不再提供支持更新', '紧急', 'Adobe Media Server当前的版本，Adobe已不再对此系统版本提供支持，即当前出现新的安全漏洞时，也不会提供相应的安全补丁，从而导致使用该版本的软件存在较高的风险。', '建议升级Adobe Media Server 到最新版本，以便可持续更新，确保软件的安全。 Adobe 目前所支持的版本如下： http://www.adobe.com/support/programs/policies/supported.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55883, 'MS11-058: Vulnerabilities in DNS Server Could Allow Remote Code Execution (2562485) (remote check)', 'Microsoft Windows DNS Server NAPTR查询远程代码执行漏洞', '紧急', 'Windows DNS Server是微软公司推出的域名系统或者域名服务，域名系统为Internet上的主机分配域名地址和IP地址。 Windows DNS Server在NAPTR查询的实现上存在远程代码执行漏洞，由于在处理不存在域的查询时存在错误，可造成服务器停止响应。远程攻击者可利用此漏洞以系统级别的权限执行任意代码，完全控制受影响计算机。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.microsoft.com/technet/security/Bulletin/MS11-058.mspx', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55925, 'PHP 5.3 < 5.3.7 Multiple Vulnerabilities', 'PHP 版本低于 5.3.7', '高危', 'PHP版本较低，导致存在多个漏洞。', '建议升级 PHP 5.3.7 或者最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55958, 'Oracle Java JRE Unsupported Version Detection', 'Oracle Java JRE不支持的版本检测', '紧急', '根据其自报的版本号，至少有一个 甲骨文（以前称为Sun）的Java JRE在远程主机上安装 不再支持。 缺乏支持意味着该产品没有新的安全补丁 会由供应商被释放。其结果是，它可能包含 安全漏洞。 需要注意的是Oracle并提供“Oracle数据库环境下的支持合同 终身支持”计划。如果检测到的JRE是这样下支持 计划，这可能是假阳性。', '升级到版本当前支持的Oracle Java JRE。http://www.oracle.com/technetwork/java/eol-135779.html http://www.oracle.com/us/support/lifetime-support-068561.html', 'CVE-2011-3389', NULL);
INSERT INTO `scan_suggest_log` VALUES (55976, 'Apache HTTP Server Byte Range DoS', 'Apache HTTP Server拒绝服务漏洞', '高危', '当处理包含大量Ranges头的HTTP请求时，ByteRange过滤器存在一个错误，攻击者可以向服务器发送特制HTTP请求，消耗大量内存，造成应用程序崩溃。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://issues.apache.org/bugzilla/show_bug.cgi?id=51714', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (55992, 'SunSSH < 1.1.1 / 1.3 CBC Plaintext Disclosure', 'SunSSH < 1.1.1 / 1.3 CBC明文信息泄露', '紧急', '远程主机上运行SunSSH服务存在一个信息泄露设计缺陷，该缺陷能令SunSSH在遭受中间人攻击会启用32位标准的SSH保护明文连接配置，借此攻击者能获取访问敏感信息。', '将SunSSH升级至1.1.1/1.3的更高版本。', NULL, NULL);
