INSERT INTO `scan_suggest_log` VALUES (61393, 'MySQL Authentication Protocol Token Comparison Casting Failure Password Bypass', 'MySQL/MariaDB安全绕过漏洞', '高危', 'MariaDB是为MySQL提供偶然替代功能的数据库服务器。MySQL是开源数据库。 MariaDB和MySQL在用户验证的处理上存在漏洞。攻击者可利用该漏洞绕过某些安全限制，也可能导致攻击者无需知道正确口令就能登录到MySQL服务器。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.mysql.com/', 'CVE-2012-1531', NULL);
INSERT INTO `scan_suggest_log` VALUES (61644, 'Apache 2.4.x < 2.4.3 Multiple Vulnerabilities', 'Apache 2.4.x < 2.4.3 存在多个漏洞', '中危', '根据其版本，它受到以下漏洞的影响: -输入验证错误存在于‘mod_negotiation’，‘Multiviews’和不可信的上载可以允许跨站点脚本攻击(cve - 2012 - 2687)；一个错误与\'mod_proxy_ajp\'和\'mod_proxy_http\'相关，可以允许连接保持打开。这种情况下当结合特殊的请求时，可以允许信息公开(cve - 2012 - 3502)', '升级到Apache 2.4.3或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (61696, 'MySQL Default Account Credentials', 'MySQL默认账号密码', '高危', 'MySQL数据库服务器存在默认账户，该漏洞源于scrutinizer账户的默认账户', '删除受影响的默认账户，或者更换符合密码复杂度的口令', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (62101, 'Apache 2.2 < 2.2.23 Multiple Vulnerabilities', 'Apache版本低于2.2.23', '中危', 'Apache版本较低，导致存在多处漏洞。', '升级apache版本到2.2.23以上 http://httpd.apache.org/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (62369, 'IBM DB2 10.1 < Fix Pack 1 Multiple Vulnerabilities', 'IBM DB2 文件替换漏洞', '高危', 'IBM DB2中存在安全漏洞，该漏洞源于SQLJ.DB2_INSTALL_JAR程序中存在错误。攻击者可利用该漏洞替换JAR文件。以下版本中存在漏洞：9.1 Fix Pack 11版本和早期版本，9.5 Fix Pack 9版本和早期版本，9.7 Fix Pack 6版本和早期版本，9.8 Fix Pack 5版本和早期版本，10.1版本和早期版本。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?uid=swg21600837', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (62372, 'Cisco IOS Software DHCP Denial of Service Vulnerability (cisco-sa-20120926-dhcp)', 'Cisco IOS Device Sensor功能拒绝服务漏洞', '高危', 'Cisco IOS 15.0至15.2版本中的Device Sensor功能中存在漏洞。远程攻击者可利用该路段通过DHCP数据包导致拒绝服务（设备重载）又名Bug ID CSCty96049。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20120926-dhcp', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (62373, 'Cisco IOS Software DHCP Version 6 Server Denial of Service Vulnerability (cisco-sa-20120926-dhcpv6)', 'Cisco IOS/IOS XE DHCPv6服务器拒绝服务漏洞', '高危', 'Cisco IOS 12.2至12.4版本、15.0至15.2版本和IOS XE 2.1.x至2.6.x版本、3.1.4S之前的3.1.xS版本、3.1.xSG、3.2.5SG之前的3.2.xSG版本、3.2.xS、3.2.xXO、3.3.xS、3.3.1SG之前的3.3.xSG版本中的DHCPv6服务器中存在漏洞。远程攻击者可利用该漏洞通过恶意的DHCPv6数据包导致拒绝服务（设备重载），又名Bug ID CSCto57723。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20120926-dhcpv6', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (62376, 'Cisco IOS Software Session Initiation Protocol Denial of Service Vulnerability (cisco-sa-20120926-sip)', 'Cisco CUCM/ IOS/IOS XE SIP实现拒绝服务漏洞', '高危', 'Cisco Unified Communications Manager (CUCM) 6.x、7.1(5b)su5之前的7.x版本、8.5(1)su4之前的8.x版本、8.6(2a)su1之前的8.6版本；Cisco IOS 12.2至12.4版本和15.0至15.2版本；Cisco IOS XE 3.3.1SG之前的3.3.xSG版本、3.4.xS、3.5.xS版本中的SIP实现中存在漏洞。远程攻击者可利用该漏洞通过包含SDP会话描述的特制SIP消息，导致拒绝服务（服务器崩溃或设备重载）。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20120926-sip', NULL, NULL);
