INSERT INTO `scan_suggest_log` VALUES (67203, 'Cisco Unified Communications Manager Session Initiation Protocol Denial of Service Vulnerability (cisco-sa-20120926-cucm)', 'Cisco CUCM/ IOS/IOS XE SIP实现拒绝服务漏洞', '高危', 'Cisco Unified Communications Manager (CUCM) 6.x、7.1(5b)su5之前的7.x版本、8.5(1)su4之前的8.x版本、8.6(2a)su1之前的8.6版本；Cisco IOS 12.2至12.4版本和15.0至15.2版本；Cisco IOS XE 3.3.1SG之前的3.3.xSG版本、3.4.xS、3.5.xS版本中的SIP实现中存在漏洞。远程攻击者可利用该漏洞通过包含SDP会话描述的特制SIP消息，导致拒绝服务（服务器崩溃或设备重载）。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20120926-sip', NULL, NULL);
