INSERT INTO `scan_suggest_log` VALUES (67260, 'PHP 5.4.x < 5.4.17 Buffer Overflow', 'PHP 版本5.4.x 低于 5.4.17，存在缓冲区溢出漏洞', '高危', 'PHP 版本5.4.x 低于 5.4.17， \'ext/pdo_pgsql/pgsql_driver.c\'文件中的函数\'_pdo_pgsql_error\' 存在缓冲区溢出漏洞', '建议升级 PHP 5.4.17或者最新版本', NULL, NULL);
