INSERT INTO `scan_suggest_log` VALUES (70879, 'ESXi 5.0 < Build 1311175 Multiple Vulnerabilities (remote check)', 'ESXi 5.0 < Build 1311175 存在多种类型漏洞', '高危', '远程 VMware ESXi 5.0主机可能受到以下安全攻击威胁： 1、与OPENSSL相关的多种错误存在，可能引起信息泄露或者拒绝服务攻击； 2、libxml2存在一个错误，与XML扩展相关，可能引发拒绝服务攻击； 3、 \'hostd-vmdb\'存在一个未预期的错误，可能引发拒绝服务攻击； 等等。', '应用补丁ESXi500-201310101-SG, ESXi500-201310201-UG, or ESXi500-Update03，相关链接： http://kb.vmware.com/kb/2055559', NULL, NULL);
