INSERT INTO `scan_suggest_log` VALUES (73570, 'Oracle Java SE Multiple Vulnerabilities (April 2014 CPU)', 'libjpeg/libjpeg-turbo库内存损坏漏洞', '紧急', 'Google Chrome 31.0.1650.48之前的版本和Ghostscript软件中使用的libjpeg 6b和libjpeg-turbo 1.3.0及之前的版本中的jdmarker.c文件中的‘get_sos’函数中存在安全漏洞，该漏洞源于程序没有检查重复的组件数据。远程攻击者可借助特制的JPEG图像利用该漏洞获取未初始化内存位置的敏感信息。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://archives.neohapsis.com/archives/fulldisclosure/2013-11/0080.html', 'CVE-2014-2483', NULL);
INSERT INTO `scan_suggest_log` VALUES (73639, 'HP System Management Homepage OpenSSL Multiple Vulnerabilities (Heartbleed)', 'HP系统管理主页的OpenSSL存在多个漏洞（包含Heartbleed）', '高危', 'OpenSSL 1.0.1g之前的1.0.1版本的TLS和DTLS实现过程中存在安全漏洞，该漏洞源于程序没有正确处理Heartbeart Extension数据包。远程攻击者可借助特制的数据包利用该漏洞获取进程内存的敏感信息（如读取私钥）。', '建议升级HP系统管理主页的版本到7.2.3.1或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (73756, 'Microsoft SQL Server Unsupported Version Detection (remote check)', 'Microsoft SQL Server不支持的版本检测（远程检查）', '紧急', '根据其自行报告的版本号，不再支持在远程主机上安装Microsoft SQL Server。缺少支持意味着供应商不会发布产品的新安全补丁。因此，它可能包含安全漏洞。', '升级到当前受支持的Microsoft SQL Server版本。', NULL, '1');
INSERT INTO `scan_suggest_log` VALUES (73757, 'Nagios NRPE Command Argument Processing Enabled', 'Nagios NRPE命令参数处理启用', '高危', '在远程主机上运行的Nagios远程插件执行器(NRPE)版本启用了命令参数处理，并接受换行字符。未经身份验证的远程攻击者可以利用这个问题在易受攻击的应用程序上下文中执行任意命令，方法是通过在libexec/check_nrpe的“-a”选项中添加一个换行字符来附加这些命令。', '在NRPE配置中禁用命令参数处理。', 'CVE-2014-2913', NULL);
INSERT INTO `scan_suggest_log` VALUES (73862, 'PHP 5.4.x < 5.4.28 FPM Unix Socket Insecure Permission Escalation', 'PHP 版本5.4.x 低于 5.4.28,存在FastCGI Process Manager 权限许可和访问控制漏洞', '中危', 'FastCGI Process Manager（FPM）是英国软件开发者Andrei Nigmatulin所研发的一个用于PHP中的FastCGI进程管理器，它用来管理FastCGI进程。 PHP版本低于5.4.28和 5.5.0至5.5.11版本的FPM中的sapi/fpm/fpm/fpm_unix.c文件存在安全漏洞，该漏洞源于程序对UNIX套接字使用0666权限。本地攻击者可借助特制的FastCGI客户端利用该漏洞获取特权。', '建议升级 PHP 5.4.28/5.5.12或者最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (73863, 'PHP 5.5.x < 5.5.12 FPM Unix Socket Insecure Permission Escalation', 'PHP版本低于5.5.12', '中危', 'FastCGI Process Manager（FPM）是英国软件开发者Andrei Nigmatulin所研发的一个用于PHP中的FastCGI进程管理器，它用来管理FastCGI进程。 PHP 5.5.0至5.5.11版本的FPM中的sapi/fpm/fpm/fpm_unix.c文件存在安全漏洞，该漏洞源于程序对UNIX套接字使用0666权限。本地攻击者可借助特制的FastCGI客户端利用该漏洞获取特权。', '升级PHP版本至5.5.12及以上版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (73917, 'ESXi 5.5 < Build 1746974 / 5.5 Update 1 < Build 1746018 OpenSSL Library Multiple Vulnerabilities (including Heartbleed) (remote check)', 'ESXi未更新补丁，存在多个安全漏洞（包括心脏流血）', '高危', 'VMware ESXi host存在多个安全漏洞，例如OpenSSL ECDSA 加密问题漏洞、OpenSSL 缓冲区溢出漏洞。', '为ESXi 5.5应用补丁包ESXi550-201404420或 ESXi550-201404401，相关链接：http://kb.vmware.com/kb/2076665 http://heartbleed.com/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (73920, 'SSH Static Key Accepted', 'F5多个产品安全漏洞', '紧急', 'F5 BIG-IP是集成了网络流量管理、应用程序安全管理器、负载均衡等功能的多合一网络设备。 多个F5产品中存在安全漏洞，该漏洞源于未明配置错误。恶意人员可利用该漏洞操控受影响系统。该漏洞影响以下产品及版本：BIG-IP LTM 9.x，10.x和11.x版本；BIG-IP GTM 9.x，10.x和11.x版本；BIG-IP ASM 9.x，10.x和11.x版本；BIG-IP Link Controller 9.x，10.x和11.x版本；BIG-IP PSM 9.x，10.x和11.x版本；BIG-IP WOM 10.x和11.x版本；BIG-IP APM 10.x和11.x版本；BIG-IP Edge Gateway 10.x和11.x版本；BIG-IP Analytics 11.x版本；Enterprise Manager 1.x和2.x版本。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://support.f5.com/kb/en-us/solutions/public/13000/600/sol13600.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (74222, 'IBM Domino 8.5.x < 8.5.3 Fix Pack 5 Interim Fix 1 iNotes Buffer Overflow', 'IBM Domino 8.5.x < 8.5.3 存在缓冲区溢出漏洞', '高危', 'IBM iNotes 8.5.3和9.0面临缓冲区溢出漏洞的风险，该漏洞允许经过身份验证的攻击者使Domino服务器崩溃，对代码执行环境有详细了解的攻击者可以通过远程代码执行获得进一步访问', '应用IBM Domino 8.5.3 FP5 IF1或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (74235, 'IBM WebSphere Application Server 8.5 < Fix Pack 8.5.5.2 Multiple Vulnerabilities', 'IBM WebSphere Application Server 8.5 < Fix Pack 8.5.5.2多重漏洞', '高危', '补丁包8.5.5.2之前的IBM WebSphere Application Server 8.5似乎在远程主机上运行，因此可能受到以下漏洞的影响:-存在一个与Apache Ant和文件压缩相关的缺陷，可能导致拒绝服务条件。(CVE-2012-2098 / PM90088) -存在与管理控制台相关的未指定的错误，该管理控制台可能允许跨站点脚本攻击。(CVE-2013-0460 / PM72275, CVE-2013-5418 / PM96477, CVE-2013-5425 / PM93828) - IBM Eclipse帮助系统存在多个错误，该系统允许跨站脚本攻击和信息公开攻击。(CVE-2013-0464, CVE-2013-0467, CVE-2013-0599 / PM89893) -所包含的IBM HTTP服务器中可选的‘mod_rewrite’模块存在输入验证缺陷，该模块允许通过包含特定转义序列的HTTP请求执行任意命令。(CVE-2013-1862 / PM87808) -包含的IBM HTTP服务器中可选的“mod_dav”模块存在一个缺陷，该模块允许拒绝服务条件。(c5 -2013-1896 / PM89996) -存在一个用户提供的输入验证错误，可能导致跨站点请求伪造(CSRF)攻击。(CVE-2013-3029 / PM88746) -存在与管理控制台相关的用户提供的输入验证错误，该管理控制台可能允许跨站点脚本攻击。(CVE-2013-4004 / PM81571, CVE-2013-4005 / PM88208) -存在一个未指定的权限错误，允许本地攻击者获取敏感信息。注意这个问题只影响“自由配置文件”。(CVE-2013-4006 / PM90472)—存在一个与UDDI管理控制台相关的输入验证错误，该管理控制台可能允许跨站点脚本攻击。(c5 -2013-4052 / PM91892)——攻击者可能因为不适当的证书检查而获得提升的特权。必须启用WS-Security和XML数字签名。(CVE-2013-4053 / PM90949) -存在一个与不正确的管理安全角色和从版本6.1迁移相关的错误。(CVE-2013-5414 / PM92313) -存在允许跨站点脚本攻击的未指定的输入验证错误。(CVE-2013-5417 / PM93323和PM93944)', '为版本8.5(8.5.5.0)或更高版本应用补丁包8.5.5.2', 'CVE-2013-5372', NULL);
INSERT INTO `scan_suggest_log` VALUES (74241, 'Cisco NX-OS Multiple Vulnerabilities (cisco-sa-20140521-nxos)', '思科NX-OS多个漏洞', '高危', 'Cisco NX-OS on Nexus 7000 devices是美国思科（Cisco）公司的一套运行于Nexus 7000系列设备中的操作系统。 Nexus 7000设备上的Cisco NX-OS低版本中存在提权漏洞。当系统上存在多个VDCs和配置本地身份认证时，远程攻击者可借助特制的SSH密钥数据利用该漏洞获取特权。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20140521-nxos', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (74246, 'Apache Tomcat 7.0.x < 7.0.53 Multiple Vulnerabilities', 'Apache Tomcat 7.0。x<7.0.53多个漏洞', '中危', '根据其自我报告的版本号，Apache Tomcat 7.0的实例。在远程主机上侦听的x早于7.0.53。因此，它受到以下漏洞的影响：-存在与允许拒绝服务攻击的块大小和块请求相关的错误。（CVE-2014-0075）-存在一个与XSLT处理和安全管理器相关的错误，该错误允许绕过与外部XML实体相关的安全性。（CVE-2014-0096）-存在一个与内容长度头处理和在允许安全绕过的反向代理后面使用应用程序相关的错误。（CVE-2014-0099）请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 7.0.53或更高版本。', 'CVE-2014-0075', '1');
INSERT INTO `scan_suggest_log` VALUES (74247, 'Apache Tomcat 7.0.x < 7.0.54 XML Parser Information Disclosure', 'Apache Tomcat 7.0。x<7.0.54 XML解析器信息披露', '中危', '根据其自我报告的版本号，Apache Tomcat 7.0的实例。x在远程主机上监听的时间早于7.0.54。因此，它受到信息披露漏洞的影响。存在一个错误，该错误允许恶意web应用程序将不需要的XML解析器注入应用程序，允许绕过安全控制，并允许处理外部XML实体。请注意，Nessus没有针对这个问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 7.0.54或更高版本。', 'CVE-2014-0119', '1');
INSERT INTO `scan_suggest_log` VALUES (74291, 'PHP 5.4.x < 5.4.29 \'src/cdf.c\' Multiple Vulnerabilities', 'PHP 版本5.4.x 低于 5.4.29', '中危', 'PHP版本较低，\'src/cdf.c\'文件存在多个漏洞。', '建议升级 PHP 5.4.29或者最新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (74292, 'PHP 5.5.x < 5.5.13 \'src/cdf.c\' Multiple Vulnerabilities', 'PHP版本低于5.5.13', '中危', 'PHP 5.3.28及之前的版本和5.5.13之前的5.5.x版本的Fileinfo组件的cdf.c文件中‘cdf_unpack_summary_info’函数存在安全漏洞。远程攻击者可借助特制的CDF文件利用该漏洞造成拒绝服务（无限循环）。', '升级PHP版本至5.5.13及以上版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (74363, 'OpenSSL 0.9.8 < 0.9.8za Multiple Vulnerabilities', 'OpenSSL版本低于0.9.8za，存在多种安全漏洞', '高危', '主机OpenSSL版本低于0.9.8za,这使得主机存在多个安全漏洞。', '升级OpenSSL版本至0.9.8za及以上版本。 相关链接： http://www.openssl.org/news/vulnerabilities.html#2014-0076 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0221 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0224 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-3470 http://www.openssl.org/news/secadv_20140605.txt http://ccsinjection.lepidum.co.jp/ https://www.imperialviolet.org/2014/06/05/earlyccs.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (74364, 'OpenSSL 1.0.1 < 1.0.1h Multiple Vulnerabilities', 'OpenSSL版本低于1.0.1h，存在多个安全漏洞', '高危', 'OpenSSL版本低于1.0.1h，存在多个安全漏洞', '升级OpenSSL版本至1.0.1h及以上。 相关链接： http://www.openssl.org/news/secadv_20140605.txt http://www.openssl.org/news/vulnerabilities.html#CVE-2010-5298 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0195 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0198 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0221 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0224 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-3470 http://ccsinjection.lepidum.co.jp/ https://www.imperialviolet.org/2014/06/05/earlyccs.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (74470, 'ESXi 5.5 < Build 1881737 OpenSSL Library Multiple Vulnerabilities (remote check)', 'ESXi未更新补丁至Build 1881737，OpenSSL库存在多个安全漏洞', '中危', 'ESXi未更新补丁至Build 1881737，存在多个安全漏洞，例如OpenSSL 竞争条件漏洞（CVE-2010-5298）、OpenSSL‘do_ssl3_write’函数缓冲区溢出漏洞（CVE-2014-0198）等多个漏洞。', '为ESXi 5.5应用补丁包ESXi550-201406001，相关链接： http://kb.vmware.com/kb/2077359 https://www.openssl.org/news/secadv_20140605.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (74496, 'Unsupported Microsoft DNS Server Detection', 'DNS服务版本微软已不支持', '紧急', 'DNS服务版本微软已不支持，缺乏供应商支持意味着没有新的产品安全补丁将发布。因此,它可能包含安全漏洞。', '更新升级DNS服务', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76110, 'IBM DB2 10.1 < Fix Pack 3a Multiple Vulnerabilities', '多个TLS/DTLS实现加密问题漏洞', '高危', 'OpenSSL，OpenJDK，PolarSSL和其它产品中所使用的TLS协议1.1以及1.2，DTLS协议1.0以及1.2中存在漏洞，该漏洞源于程序在处理畸形的CBC填充期间没有正确地研究针对固执的MAC地址检查操作所进行的计时边信道攻击。通过对特制报文的计时数据的统计分析，远程攻击者可利用该漏洞实施区分攻击以及明文恢复攻击。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.openssl.org/news/secadv_20130204.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76111, 'IBM DB2 10.5 < Fix Pack 3a Multiple Vulnerabilities', 'IBM DB2 10.5 < Fix Pack 3a 存在多个漏洞', '高危', '受到以下一个或多个漏洞的影响: -与处理可能允许拒绝服务攻击的格式不正确的证书链相关的未指定错误(cve - 2013 - 6747) -在不安全的位置存在一个与库相关的构建错误，它允许本地用户执行权限升级攻击。注意，在Microsoft Windows操作系统上运行时，此问题不会影响应用程序(cve - 2014 - 0907) 与TLS实现相关的一个未指定的错误可能允许某些错误情况导致100%的CPU利用率(cve - 2014 - 0963)', '升级版本，同时供应商已经发布了一个关于构建错误问题的解决方案(CVE-2014-0907)，涉及到“sqllib/bin/db2chglibpath”命令', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76113, 'IBM DB2 9.5 <= Fix Pack 9 or 10 Multiple Vulnerabilities', 'IBM Global Security Toolkit 拒绝服务漏洞', '高危', 'IBM Security Directory Server (ISDS)和Tivoli Directory Server (TDS)中存在拒绝服务漏洞，该漏洞源于GSKit组件中存在错误。远程攻击者可借助畸形的证书链利用该漏洞造成服务器进程挂起或崩溃。以下版本受到影响：IBM GSKit 7.0.4.48之前的7.x版本和8.0.50.16之前的8.x版本。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?uid=swg21662902', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76114, 'IBM DB2 9.7 < Fix Pack 9a Multiple Vulnerabilities', 'IBM DB2 9.7 < Fix Pack 9a 存在多个漏洞', '高危', '根据其版本，它受到以下一个或多个漏洞的影响: 与处理允许拒绝服务攻击的格式不正确的证书链相关的未指定错误(cve - 2013 - 6747)；与允许本地用户执行权限升级攻击的不安全位置的库相关的构建错误。在Microsoft Windows操作系统上运行时不会影响(cve - 2014 - 0907)；与TLS实现相关的一个未指定的错误存在，允许某些错误用例导致100%的CPU利用率(cve - 2014 - 0963)', '应用IBM DB2 version 9.7 Fix Pack 9a或更高版本。 或者对于DB2 Version 9.7 Fix Pack 8，获得临时修复的特殊版本。 供应商已经发布了一个关于构建错误问题的解决方案(CVE-2014-0907)，涉及到“sqllib/bin/db2chglibpath”命令。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76116, 'IBM DB2 Stored Procedure Infrastructure Privilege Escalation Vulnerability', 'IBM DB2 提权漏洞', '高危', 'BM DB2 for Windows中的Stored Procedure基础设施中存在提权漏洞。远程攻击者可利用该漏洞获取特权。以下版本受到影响：IBM DB2 9.5，9.7，10.1，10.5。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www-01.ibm.com/support/docview.wss?uid=swg21673947', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76122, 'OSSIM tele_compress.php Directory Traversal', 'OSSIM tele_compress.php 目录遍历漏洞', '高危', '安装了OSSIM主机的存在一个目录遍历漏洞。 /ossim/ocsreports/tele_compress.php文件中的timestamp参数没有进行安全检查。远程攻击者可以利用这个来下载任意文件， 控制Web权限。', '更新到OSSIM 4.3.3.1 或者更新', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76203, 'ESXi 5.1 < Build 1900470 OpenSSL Library Multiple Vulnerabilities (remote check)', 'ESXi 5.1 < Build 1900470 Openssl库存在多个漏洞', '中危', 'ESXi 5.1 Build过低，导致存在多个漏洞： 因此，受以下漏洞的影响 CVE-2010-5298 CVE-2014-0198 CVE-2014-0224 CVE-2014-3470', '更新到ESXi 5.1.的ESXi510-201406401-SG', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76216, 'OSSIM < 4.3.3.1 tele_stats.php SQL Injection', 'OSSIM 版本低于 4.3.3.1 导致tele_stats.php 存在SQL注入漏洞', '高危', '安装了OSSIM的主机存在一个SQL注入漏洞。/ossim/ocsreports/tele_stats.php中的 stat 参数未进行合适的过滤导致。远程攻击者可能会利用这个来执行任意SQL命令为MySQL的根用户。', '更新到OSSIM 4.3.3.1 或者更新', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76262, 'OSSIM < 4.3.2 Multiple SQL Injection Vulnerabilities', 'OSSIM版本低于4.3.2 导致存在多个SQL注入漏洞', '高危', '安装了OSSIM的主机存在多个SQL注入漏洞。/RadarReport\' and \'/geoloc\' 目录下的date_from未进行合适的过滤。远程攻击者可能会利用这个来执行任意SQL命令为MySQL的根用户。', '更新到OSSIM 4.3.3.1 或者更新', NULL, NULL);
