INSERT INTO `scan_suggest_log` VALUES (76345, 'HP System Management Homepage < 7.2.4.1 / 7.3.3.1 OpenSSL Multiple Vulnerabilities', 'HP系统管理主页版本低于7.2.4.1 / 7.3.3.1，OpenSSL存在多个安全漏洞', '高危', '远程HP系统管理主页版本存在多个安全漏洞影响，例如OpenSSL 竞争条件漏洞。', '升级HP System Management Homepage版本至7.2.4.1 / 7.3.3.1及以上，相关链接： http://www.nessus.org/u?58824618 http://www.securityfocus.com/archive/1/532538/30/0/threaded http://www.securityfocus.com/archive/1/532642/30/0/threaded http://www.openssl.org/news/vulnerabilities.html#CVE-2010-5298 http://www.openssl.org/news/vulnerabilities.html#2014-0076 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0198 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0221 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-0224 http://www.openssl.org/news/vulnerabilities.html#CVE-2014-3470 http://www.openssl.org/news/secadv_20140605.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76357, 'HP Onboard Administrator < 4.22 Remote Information Disclosure', 'HP Onboard Administrator版本低于4.22，存在远程信息泄露漏洞', '中危', '远程服务存在信息泄露漏洞，HP Onboard Administrator版本低于4.22，因此可能受到 OpenSSL相关漏洞影响，存在一个位置的错误，可能允许攻击者启用弱密钥算法，降低了中间人攻击的难度。', '升级版本至4.22及以上版本，相关链接： http://www.nessus.org/u?7496652c', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (76368, 'ESXi 5.0 < Build 1918656 OpenSSL Library Multiple Vulnerabilities (remote check)', 'ESXi 5.0 < Build 1918656 OpenSSL库存在多种类型漏洞', '中危', '远程 VMware ESXi 5.0主机存在多个安全漏洞。', '应用补丁ESXi500-201407001 to ESXi 5.0.，相关链接：http://www.vmware.com/security/advisories/VMSA-2014-0006.html http://kb.vmware.com/kb/2078807 https://www.openssl.org/news/secadv_20140605.txt', NULL, NULL);
