INSERT INTO `scan_suggest_log` VALUES (78035, 'Cisco IOS Software RSVP DoS (cisco-sa-20140924-rsvp)', 'Cisco IOS和IOS XE 输入验证漏洞', '高危', 'Cisco IOS和IOS XE中存在安全漏洞。远程攻击者可通过发送畸形的RSVP数据包利用该漏洞造成拒绝服务（设备重载）。以下产品和版本受到影响：Cisco IOS 12.0版本，12.2版本，12.4版本，15.0版本，15.1版本，15.2本部和15.3版本，IOS XE 3.7.4S之前2.x版本和3.x版本，3.3.2SE之前3.2.xSE版本和3.3.xSE 版本，3.4.4SG之前3.3.xSG版本和3.4.xSG版本，3.10.1S之前3.8.xS版本、3.9.xS版本和3.10.xS版本。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20140924-rsvp', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78087, 'Oracle MapViewer Multiple Vulnerabilities (July 2012 CPU)', 'Oracle MapViewer Multiple漏洞（2012.7 CPU)', '中危', '据其版本号，安装了Oracle中间件MapViewer的远程主机上受到了以下漏洞的影响： 允许远程攻击者获得访问潜在的敏感信息相关的缺陷。 （cve-2012-1736，cve-2012-1749） 安装的子组件，允许远程攻击者对对系统的完整性造成影响。（cve-2012-3115）', '使用Oracle 2012年7月份 严重补丁更新文件进行更新', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78090, 'HP System Management Homepage < 7.4 Multiple Vulnerabilities', 'HP系统管理主页版本低于7.4，存在多个安全漏洞', '高危', '远程HP系统管理主页版本存在多个安全漏洞影响，例如Haxx cURL/libcurl 安全绕过漏洞。', '升级HP System Management Homepage版本至7.4及以上，相关链接： http://www.nessus.org/u?2d04bcb9 http://www.securityfocus.com/archive/1/533589/30/0/threaded', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78108, 'ESXi 5.5 < Build 1980513 glibc Library Multiple Vulnerabilities (remote check)', 'ESXi未更新补丁至Build 1980513，glibc库存在多个安全漏洞', '中危', 'ESXi未更新补丁至Build 1980513，存在多个安全漏洞，例如GNU C glibc ‘extend_buffers()’ 正则表达式处理拒绝服务漏洞（CVE-2013-0242）、GNU C Library “getaddrinfo()” 函数拒绝服务漏洞（CVE-2013-1914）。', '为ESXi 5.5应用补丁包ESXi550-201409101-SG，相关链接： http://lists.vmware.com/pipermail/security-announce/2014/000260.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78479, 'SSLv3 Padding Oracle On Downgraded Legacy Encryption Vulnerability (POODLE)', 'SSLv3 POODLE 安全漏洞', '中危', '支持SSLv3 协议的网站存在被中间人攻击的风险，“Poodle”攻击（全称为Padding Oracle On Downloaded Legacy Encryption）可以提取出SSL连接中的加密数据相应的明文信息。攻击者可以利用这个漏洞发动中间人攻击或解密受此影响的客户端之间的通信内容。', '禁用SSL 3.0', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78481, 'Oracle Java SE Multiple Vulnerabilities (October 2014 CPU)', 'Oracle Java SE 代码执行漏洞', '紧急', 'Oracle Java SE中的Deployment子组件中存在安全漏洞。远程攻击者可利用该漏洞执行任意代码，影响数据的完整性，可用性和机密性。以下版本受到影响：Oracle Java SE 6u81版本，7u67版本，8u20版本。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://www.oracle.com/technetwork/topics/security/cpuoct2014-1972960.html', 'CVE-2014-3566', NULL);
INSERT INTO `scan_suggest_log` VALUES (78545, 'PHP 5.4.x < 5.4.34 Multiple Vulnerabilities', 'PHP 版本5.4.x 低于 5.4.34', '高危', 'PHP版本较低，导致存在多个漏洞。', '建议升级 PHP 5.4.34或者最新版本', NULL, NULL);
