INSERT INTO `scan_suggest_log` VALUES (78552, 'OpenSSL 0.9.8 < 0.9.8zc Multiple Vulnerabilities (POODLE)', 'OpenSSL版本低于0.9.8zc，存在多种安全漏洞', '中危', '主机OpenSSL版本低于0.9.8zc,这使得主机存在多个安全漏洞。', '升级OpenSSL版本至0.9.8zc及以上版本。 相关链接： https://www.openssl.org/news/openssl-0.9.8-notes.html https://www.openssl.org/news/secadv_20141015.txt https://www.openssl.org/news/vulnerabilities.html https://www.imperialviolet.org/2014/10/14/poodle.html https://www.openssl.org/~bodo/ssl-poodle.pdf https://tools.ietf.org/html/draft-ietf-tls-downgrade-scsv-00', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78553, 'OpenSSL 1.0.0 < 1.0.1o Multiple Vulnerabilities (POODLE)', 'SSL3.0 加密协议信息泄露漏洞', '中危', 'SSL protocol是美国网景（Netscape）公司研发的安全套接层协议（Secure Socket Layer）的缩写，为互联网通信提供安全及数据完整性保障。 OpenSSL 1.0.1i及之前版本中使用的SSL protocol 3.0版本中存在安全漏洞，该漏洞源于程序使用非确定性的CBC填充。攻击者可借助padding-oracle攻击利用该漏洞实施中间人攻击，获取明文数据。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://www.openssl.org/news/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78554, 'OpenSSL 1.0.1 < 1.0.1j Multiple Vulnerabilities (POODLE)', 'OpenSSL版本低于1.0.1j，存在多个安全漏洞', '中危', 'OpenSSL版本低于1.0.1j，存在多个安全漏洞', '升级OpenSSL版本至1.0.1j及以上。 相关链接： https://www.openssl.org/news/openssl-1.0.1-notes.html https://www.openssl.org/news/secadv_20141015.txt https://www.openssl.org/news/vulnerabilities.html https://www.imperialviolet.org/2014/10/14/poodle.html https://www.openssl.org/~bodo/ssl-poodle.pdf https://tools.ietf.org/html/draft-ietf-tls-downgrade-scsv-00', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78555, 'OpenSSL Unsupported', 'OpenSSL版本已不再支持', '紧急', '当前主机的OpenSSL版本已不再受厂商支持，这意味着厂商不再为当前版本发布安全补丁包，即可能存在安全漏洞', '升级版本至厂商支持的版本 相关链接： https://www.openssl.org/news/openssl-old-notes.html', NULL, NULL);
