INSERT INTO `scan_suggest_log` VALUES (78628, 'HP Operations Manager / Operations Agent < 11.13 XSS (HPSBMU03126)', 'HP Operations Manager 跨站脚本漏洞 (HPSBMU03126)', '中危', 'HP Operations Manager 11.14之前版本的HP Operations Agent中存在跨站脚本漏洞。远程攻击者可利用该漏洞注入任意Web脚本或HTML。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://h20564.www2.hp.com/portal/site/hpsc/public/kb/docDisplay?docId=emr_na-c04472444', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (78693, 'Cisco NX-OS GNU Bash Environment Variable Command Injection Vulnerability (cisco-sa-20140926-bash) (Shellshock)', 'GNU Bash 操作系统命令注入漏洞', '紧急', 'GNU Bash 4.3及之前版本中存在安全漏洞，该漏洞源于程序没有正确处理环境变量值内的函数定义。远程攻击者可借助特制的环境变量利用该漏洞执行任意代码。', '目前厂商已经发布了升级补丁以修复此安全问题，详情请关注厂商主页： http://www.gnu.org/software/bash/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (79233, 'HP Data Protector \'EXEC_INTEGUTIL\' Arbitrary Command Execution', 'HP Data Protector \'EXEC_INTEGUTIL\' 远程命令执行漏洞', '紧急', 'HP Data Protector 9在处理EXEC_INTEGUTIL消息时会触发Backup client服务内的漏洞，该服务默认监听在TCP/555上，成功利用此漏洞后可利用EXEC_INTEGUTIL数据包执行任意代码。', '服务器和客户端上启用加密控制通信（ECC）服务。', NULL, NULL);
