INSERT INTO `scan_suggest_log` VALUES (85660, 'Ubuntu 12.04 LTS / 14.04 / 15.04 : gdk-pixbuf vulnerability (USN-2722-1)', 'Ubuntu 12.04 LTS / 14.04/15.04:gdk-pixbuf脆弱性(usn - 2722 - 1)', '中危', 'Gustavo格雷科发现GDK-PixBuf正确缩放处理位图图像。如果一个用户或自动化系统被骗BMP图像文件,远程攻击者可以利用这一缺陷的原因GDK-PixBuf崩溃,或可能导致拒绝服务执行任意代码。', '更新影响libgdk-pixbuf2.0-0包。', NULL, NULL);
