INSERT INTO `scan_suggest_log` VALUES (89784, 'Cisco Nexus TCP Packet TIME_WAIT State Handling DoS (cisco-sa-20160302-netstack)', NULL, '高危', 'Cisco Nexus设备受到拒绝服务漏洞的影响，这是由于在受影响的设备处于TIME_WAIT状态时，在TCP会话的结束顺序中某些TCP数据包处理不当所致。未经身份验证的远程攻击者可以通过在设备处于TIME_WAIT状态时发送精心设计的TCP数据包序列来利用此漏洞，导致重新加载TCP堆栈， 从而导致拒绝服务条件。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： Model : Nexus 7000 Installed version : 4.2(6) Fixed version : 5.2(9) Cisco bug ID : CSCub70579', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90007, 'PHP 5.5.x < 5.5.33 Multiple Vulnerabilities', 'PHP版本漏洞', '紧急', 'PHP是广泛使用的通用目的脚本语言，特别适合于Web开发，可嵌入到HTML中。 影响版本PHP 5.5.x < 5.5.33。', '厂商补丁： PHP版本较低，建议升级到最新版本 --- 目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载： http://php.net/ChangeLog-5.php http://php.net/ChangeLog-7.php', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90008, 'PHP 5.6.x < 5.6.19 Multiple Vulnerabilities', 'PHP版本低于5.6.19存在多个漏洞', '紧急', '服务器上运行的PHP版本过低，这将导致受到多个漏洞影响，非常危险。', '更新到5.6.19或者更新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90150, 'HP System Management Homepage < 7.5.4 Multiple Vulnerabilities (Logjam)', 'OpenSSL‘BN_GF2m_mod_inv’函数拒绝服务漏洞', '紧急', 'OpenSSL是OpenSSL团队开发的一个开源的能够实现安全套接层（SSL v2/v3）和安全传输层（TLS v1）协议的通用加密库，它支持多种加密算法，包括对称密码、哈希算法、安全散列算法等。 OpenSSL的crypto/bn/bn_gf2m.c文件中的‘BN_GF2m_mod_inv’函数存在安全漏洞，该漏洞源于程序没有正确处理ECParameters结构体。远程攻击者可借助使用Elliptic Curve算法的会话利用该漏洞造成拒绝服务（无限循环）。以下版本受到影响：OpenSSL 0.9.8s之前版本，1.0.0e之前1.0.0版本，1.0.1n之前1.0.1版本，1.0.2b之前1.0.2版本。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://www.openssl.org/news/secadv_20150611.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90251, 'HP System Management Homepage < 7.2.6 Multiple Vulnerabilities (FREAK)', 'Haxx cURL和libcurl 授权问题漏洞', '高危', 'CURL和Libcurl都是瑞典Haxx公司的产品。CURL是一套利用URL语法在命令行下工作的文件传输工具。Libcurl是一个免费、开源的客户端URL传输库。 cURL和libcurl 7.10.6至7.34.0版本中存在安全漏洞。该漏洞源于当使用多个身份验证方法时，应用程序重新使用NTLM连接。攻击者可通过发送请求利用该漏洞以其他用户的凭证执行某些操作。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://curl.haxx.se/docs/adv_20140129.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90265, 'Dell iDRAC6 / iDRAC7 / iDRAC8 Path Traversal Authentication Bypass', 'Dell iDRAC6 / iDRAC7 / iDRAC8 绕过认证目录遍历漏洞', '中危', '远程Dell访问控制器（idrac6 / idrac7 / iDRAC8）是存在路径遍历漏洞。一个未经身份验证的远程攻击者可以利用这个漏洞，通过一个特制的请求，绕过认证，获得特权进入iDRAC控制器。 请注意，应用程序可能也存在其他漏洞，包括格式字符串漏洞，一个用户名多于62个字符的SSH认证问题， SSH缓冲区溢出，和一个未指定的XML外部实体（XXE漏洞）', '更新固件版本到 2.80 (iDRAC6) / 2.21.21.21 (iDRAC7 and iDRAC8) 或者更新', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90317, 'SSH Weak Algorithms Supported', 'SSH弱算法支持', '中危', '目前该ssh服务采用的算法较低', '修改ssh算法采用aes128-cbc或者aes256-cbc', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90318, 'Apache Tomcat XSRF Token Disclosure', 'Apache Tomcat XSRF Token信息泄露', '中危', '一个未经身份验证的远程攻击者可以利用这个漏洞获取一个有效的跨站点请求伪造(XSRF)令牌在重定向时发出请求 /manager/或/ host-manager /。这个令牌可以被攻击者利用构造一个XSRF攻击。', '升级到Apache Tomcat版本7.0.68 / 8.0.32 / 9.0.0。M3或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90355, 'Cisco IOS IKEv2 Fragmentation DoS (cisco-sa-20160323-ios-ikev2)', 'Cisco IOS和IOS XE Software IKEv2 输入验证漏洞', '高危', 'Cisco IOS Software 15.0版本至15.6版本和IOS XE Software 3.3版本至3.17版本的IKEv2实现过程中存在安全漏洞。远程攻击者可借助分片的数据包利用该漏洞造成拒绝服务（设备重载）。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20160323-ios-ikev2', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90358, 'Cisco IOS Smart Install Packet Image List Parameter Handling DoS (cisco-sa-20160323-smi)', 'Cisco IOS和IOS XE Software Smart Install client功能拒绝服务漏洞', '高危', 'Cisco IOS Software和IOS XE Software的Smart Install client实现过程中存在安全漏洞。远程攻击者可借助Smart Install数据包中的图像列表参数利用该漏洞造成拒绝服务（设备重载）。以下产品及版本受到影响：Cisco IOS 12.2版本，15.0版本，15.2版本，IOS XE 3.2版本至3.7版本。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20160323-smi', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90360, 'PHP 5.5.x < 5.5.34 Multiple Vulnerabilities', 'PHP版本漏洞', '紧急', 'PHP是广泛使用的通用目的脚本语言，特别适合于Web开发，可嵌入到HTML中。 影响版本PHP 5.5.x < 5.5.34。', '厂商补丁： PHP版本较低，建议升级到最新版本 --- 目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载： http://php.net/ChangeLog-5.php http://php.net/ChangeLog-7.php', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90361, 'PHP 5.6.x < 5.6.20 Multiple Vulnerabilities', 'PHP版本低于5.6.20存在多个漏洞', '紧急', '服务器上运行的PHP版本过低，这将导致受到多个漏洞影响，非常危险。', '更新到5.6.20或者更新版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90509, 'Samba Badlock Vulnerability', 'Samba Badlock 漏洞', '中危', 'Badlock 是一种「协议/中间人」攻击漏洞，可用模拟 Windows AD 已验证的用户身份发动攻击。在此种攻击中，攻击者可被授予读写 SAM 数据库的权限，可能造成所有用户名密码和其它潜在敏感信息泄漏。', '升级到Samba4.2.11 / 4.3.8 / 4.4.2 or later', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90510, 'MS16-047: Security Update for SAM and LSAD Remote Protocols (3148527) (Badlock) (uncredentialed check)', 'Windows SAM 和 LSAD 降级漏洞', '中危', '当安全帐户管理器 (SAM) 和本地安全机构（域策略）(LSAD) 远程协议接受不充分保护它们的验证级别时，它们中会存在一个特权提升漏洞。此漏洞是由 SAM 和 LSAD 远程协议建立远程过程调用 (RPC) 通道的方式导致的。成功利用此漏洞的攻击者可以获得对 SAM 数据库的访问权限。', '请选择合适的版本的补丁进行Windows 更新https://technet.microsoft.com/zh-cn/library/security/ms16-047.aspx#ID0EPOAC', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90525, 'Cisco IOS Multiple OpenSSL Vulnerabilities (CSCut46130)', 'OpenSSL‘d2i_ECPrivateKey’函数释放后重用漏洞', '高危', 'OpenSSL的crypto/ec/ec_asn1.c文件中的‘d2i_ECPrivateKey’函数存在释放后重用漏洞。远程攻击者可借助畸形的Elliptic Curve(EC)private-key文件利用该漏洞造成拒绝服务（内存损坏和应用程序崩溃）。以下版本受到影响：OpenSSL 0.9.8zf之前版本，1.0.0r之前1.0.0版本，1.0.1m之前1.0.1版本，1.0.2a之前1.0.2版本。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://www.openssl.org/news/secadv_20150319.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90709, 'Oracle WebLogic Server Java Object Deserialization RCE (April 2016 CPU)', 'Oracle Fusion Middleware WebLogic Server组件未明安全漏洞', '紧急', 'Oracle Fusion Middleware是一套面向企业和云环境的业务创新平台。 Oracle Fusion Middleware的Oracle WebLogic Server组件中的Java Messaging Service子组件存在未明安全漏洞，允许远程攻击者可利用该漏洞影响系统的保密性，完整性及可用性。', '厂商提供的安全补丁以修复该漏洞： http://www.oracle.com/technetwork/security-advisory/cpuapr2016v3-2985753.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90710, 'VMware vCenter Server 5.5.x < 5.5u3d / 6.0.x < 6.0u2 Client Integration Plugin Session Hijacking', '安装在远程主机上的虚拟化管理应用程序受到会话劫持漏洞的影响。', '中危', '安装在远程主机上的VMware vCenter Server版本在5.5u3之前是5.5.x，6.0u2之前是6.0.x。 因此，由于无法以安全的方式处理会话内容，因此受VMware客户端集成插件的缺陷的影响。 远程攻击者可以通过说服用户访问恶意网页来进行会话劫持攻击。 它也可以被利用来进行中间人的攻击。', '升级到VMware vCenter服务器版本5.5u3d（5.5.0 build-3721164） / 6.0u2（6.0.0 build-3634788）或之后的版本。 https://www.vmware.com/security/advisories/VMSA-2016-0004.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90888, 'OpenSSL 1.0.1 < 1.0.1o ASN.1 Encoder Negative Zero Value Handling RCE', 'OpenSSL ASN.1编码器内存破坏漏洞', '紧急', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。ASN.1是一种 ISO/ITU-T 标准，描述了一种对数据进行表示、编码、传输和解码的数据格式。 OpenSSL中的ASN.1解析器在对数据解析时没有正确处理特定标签，当遇到V_ASN1_NEG_INTEGER和 V_ASN1_NEG_ENUMERATED标签时，ASN.1解析器也会将其视作ASN1_ANY类型，从而解析其中的 数据。当数据再次编码序列化时，可能造成数据越界写入，引起内存损坏。', 'OpenSSL Project已经为此发布了一个安全公告（20160503）以及相应补丁: OpenSSL 1.0.2 用户升级到 1.0.2c OpenSSL 1.0.1 用户升级到 1.0.1o 20160503：OpenSSL Security Advisory [3rd May 2016] 链接：https://www.openssl.org/news/secadv/20160503.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90890, 'OpenSSL 1.0.1 < 1.0.1t Multiple Vulnerabilities', 'OpenSSL EVP_EncryptUpdate函数的溢出漏洞', '中危', 'OpenSSL EVP_EncodeUpdate()存在溢出漏洞。攻击者利用漏洞可输入大量数据，导致长度检查时发生堆破坏溢出。', '厂商已发布升级程序修复该漏洞，请及时下载更新： https://www.openssl.org/news/vulnerabilities.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90891, 'OpenSSL 1.0.2 < 1.0.2h Multiple Vulnerabilities', 'OpenSSL 1.0.2 < 1.0.2h多个漏洞', '中危', '受到以下漏洞的影响:在文件crypto/evp/encode中的EVP_EncodeUpdate()函数中存在堆缓冲区溢出条件。在处理大量输入数据时触发的。未经身份验证的远程攻击者可以利用这一点导致拒绝服务条件(cve - 2016 - 2105)；在文件crypto/evp/evp_enc中的EVP_EncryptUpdate()函数中存在堆缓冲区溢出条件。在前一个调用发生在具有部分块的相同函数之后，在处理大量输入数据时触发的。未经身份验证的远程攻击者可以利用这一点导致拒绝服务条件(cve - 2016 - 2106)；文件crypto/evp/ e_aes_cbc_hmac_sha1_cipher()函数存在缺陷。中间人攻击者可以利用这些攻击来执行填充oracle攻击，从而能够解密网络流量(cve - 2016 - 2107) 当从一个BIO中读取ASN.1数据时，由于编码无效，导致大量内存分配，d2i BIO函数中存在多个未指定的缺陷。未经身份验证的远程攻击者可以利用这些资源，通过资源耗尽导致拒绝服务条件(cve - 2016 - 2109)等等', '升级到OpenSSL 1.0.2h或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (90920, 'PHP 5.5.x < 5.5.35 Multiple Vulnerabilities', 'PHP版本漏洞', '高危', 'PHP是广泛使用的通用目的脚本语言，特别适合于Web开发，可嵌入到HTML中。 影响版本PHP 5.5.x < 5.5.35。', '厂商补丁： PHP版本较低，建议升级到最新版本 --- 目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载： http://php.net/ChangeLog-5.php http://php.net/ChangeLog-7.php', NULL, NULL);
