INSERT INTO `scan_suggest_log` VALUES (91222, 'HP System Management Homepage Multiple Vulnerabilities (HPSBMU03593)', 'Apache HTTP Server拒绝服务漏洞', '紧急', 'Apache HTTP服务器是流行的开源WEB服务器程序，可使用在Unix和Windows操作系统下。 Apache HTTP Server 1.x和2.x版本中存在漏洞。正如Slowloris中展示的。远程攻击者可借助部分HTTP请求导致拒绝服务（守护进程中断），该漏洞与2.2.15之前版本中的mod_reqtimeout模块缺失有关。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://ha.ckers.org/slowloris/', NULL, NULL);
