INSERT INTO `scan_suggest_log` VALUES (104743, 'TLS Version 1.0 Protocol Detection', 'TLS版本1.0协议检测', '中危', '远程服务接受使用TLS 1.0加密的连接。TLS 1.0有许多密码设计缺陷。TLS 1.0的现代实现减轻了这些问题，但是像1.2和1.3这样的TLS的新版本是针对这些缺陷而设计的，应该尽可能使用。截至2020年3月31日，为TLS 1.2及更高版本启用的终结点将不再与主要web浏览器和主要供应商正常工作。PCI DSS v3.2要求在2018年6月30日之前完全禁用TLS 1.0，但POS POI终端（及其连接的SSL/TLS终端点）除外，这些终端可以被验证为不易受任何已知漏洞攻击。', '启用对TLS 1.2和1.3的支持，并禁用对TLS 1.0的支持。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (105042, 'Zabbix Server \'active checks\' Command Injection', 'Zabbix 安全漏洞', '中危', 'Zabbix Server 2.4.X版本中的trapper command功能存在远程代码执行漏洞。远程攻击者可利用该漏洞注入命令，执行代码。', '2.0.21rc1, 2.2.18rc1, 3.0.9rc1, 3.2.5rc1, 3.4.0alpha1 (trunk)版本已修复或直接从数据库中利用命令消除（use zabbix; delete * from scripts;） 或从GUI界面通过操作（Administration -> Scripts -> Checkmarks -> Delete Selected/Go）完成。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (105291, 'OpenSSL 1.0.2 < 1.0.2n Multiple Vulnerabilities', 'OpenSSL 1.0.2<1.0.2n多个漏洞', '中危', '根据它的横幅，在远程主机上运行的OpenSSL版本是1.0。x在1.0.2n之前。因此，它受到多个漏洞的影响，这些漏洞可能会导致私钥信息恢复或数据无法正确加密。', '升级至OpenSSL 1.0.2n或更高版本。', 'CVE-2017-3737', '1');
INSERT INTO `scan_suggest_log` VALUES (105292, 'OpenSSL 1.1.0 < 1.1.0h AVX2 Montgomery Multiplication Private Key Derivation Weakness', 'OpenSSL 信息泄露漏洞', '中危', 'OpenSSL 1.0.2版本至1.0.2m版本和1.1.0版本至1.1.0g版本中的AVX2 Montgomery乘法运算存在信息泄露漏洞。该漏洞源于网络系统或产品在运行过程中存在配置等错误。未授权的攻击者可利用漏洞获取受影响组件敏感信息。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://www.openssl.org/news/secadv/20171207.txt', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (105293, 'Jenkins JDK / Ant Tools Job Configuration Stored XSS Vulnerability (SECURITY-624)', 'Jenkins JDK/Ant工具配置文件存在XSS漏洞', '中危', 'Jenkins <= 2.93版本在实现上存在安全漏洞，可使远程管理员通过作业配置表内构造的工具名称，执行跨站脚本攻击。', '目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载：https://jenkins.io/security/advisory/2017-12-05/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (105484, 'Oracle WebLogic WSAT Remote Code Execution', 'Oracle Fusion Middleware Oracle WebLogic Server组件安全漏洞', '紧急', 'Oracle Fusion Middleware中的Oracle WebLogic Server组件的WLS Security子组件存在安全漏洞。攻击者可利用该漏洞控制组件，影响数据的可用性、保密性和完整性。以下组版本受到影响：Oracle WebLogic Server 10.3.6.0.0版本，12.1.3.0.0版本，12.2.1.1.0版本，12.2.1.2.0版本。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： http://www.oracle.com/technetwork/security-advisory/cpuoct2017-3236626.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (105486, 'ESXi 5.5 / 6.0 / 6.5 / Multiple Vulnerabilities (VMSA-2017-0021) (VMSA-2018-0002) (Spectre) (remote check)', 'VMware ESXi 跨站脚本漏洞', '高危', 'VMware ESXi中存在跨站脚本漏洞，该漏洞源于Host Client在显示输入前，没有正确的过滤用户提交输入中的HTML代码。远程攻击者可利用该漏洞执行Javascript代码。以下版本受到影响：VMware ESXi ESXi650-201712103-SG之前的6.5版本，ESXi600-201711103-SG之前的6.0版本，ESXi550-201709102-SG之前的5.5版本。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： https://www.vmware.com/security/advisories/VMSA-2017-0021.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (105614, 'ESXi 6.5 < Build 6765664 Heap Buffer Overflow (VMSA-2017-0021) (remote check)', 'ESXi 6.5<Build 6765664堆缓冲区溢出（VMSA-2017-0021）（远程检查）', '中危', '远程VMware ESXi 6.5主机的版本早于版本6765664。因此，它受到堆缓冲区溢出漏洞的影响，该漏洞可由一组特制的VNC数据包触发。', '根据供应商建议，应用ESXi650-201710401-BG补丁。', 'CVE-2017-4933', '1');
INSERT INTO `scan_suggest_log` VALUES (105771, 'PHP 5.6.x < 5.6.33 Multiple Vulnerabilities', 'PHP GD Graphics Library 安全漏洞', '中危', 'PHP中使用的GD Graphics Library的gd_gif_in.c文件存在安全漏洞。本地攻击者可借助特制的GIF文件利用该漏洞造成拒绝服务（无限循环）。以下版本受到影响：PHP 7.0.27之前的7.0.x版本，7.1.13之前的7.1.x版本，7.2.1之前的7.2.x版本。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： http://php.net/ChangeLog-7.php http://php.net/ChangeLog-7.php', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (105784, 'VMware vCenter Server 5.5.x < 5.5U3g / 6.0.x < 6.0U3d / 6.5.x < 6.5U1e Hypervisor-Assisted Guest Remediation (VMSA-2018-0004) (Spectre)', 'Intel和ARM CPU芯片信息泄露漏洞', '中危', '漏洞源于处理器数据边界机制中存在缺陷。本地攻击者可通过滥用‘错误推测执行’利用该漏洞读取内存信息。', '升级vm版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (106710, 'Apache Tomcat 7.0.79 < 7.0.83 Insecure CGI Servlet Search Algorithm Description Weakness', 'Apache Tomcat 7.0.79<7.0.83不安全CGI Servlet搜索算法描述弱点', '中危', '安装在远程主机上的Apache Tomcat版本是7.0.84之前的7.0.x。因此，它受到一个缺陷的影响，该缺陷是由于程序包含对CGI Servlet搜索算法的不正确描述，这可能导致管理员使系统处于不安全状态。', '升级至Apache Tomcat 7.0.84或更高版本。', 'CVE-2017-15706', '1');
