INSERT INTO `scan_suggest_log` VALUES (106975, 'Apache Tomcat 7.0.0 < 7.0.85 Security Constraint Weakness', 'Apache Tomcat 7.0.0<7.0.85安全约束弱点', '中危', '安装在远程主机上的Apache Tomcat版本是7.0.85之前的7.0.x。因此，它受到安全约束缺陷的影响，该缺陷可能会将资源暴露给未经授权的用户。', '升级至Apache Tomcat 7.0.85或更高版本。', 'CVE-2018-1304', '1');
