INSERT INTO `scan_suggest_log` VALUES (107056, 'Hikvision IP Camera Remote Authentication Bypass', '多款Hikvision产品安全漏洞', '紧急', '多款Hikvision产品中存在身份验证漏洞。攻击者可利用该漏洞提升权限，获取敏感信息的访问权限。以下产品和版本受到影响：Hikvision DS-2CD2xx2F-I Series 5.2.0 build 140721版本至5.4.0 build 160530版本；DS-2CD2xx0F-I Series 5.2.0 build 140721版本至5.4.0 Build 160401版本；DS-2CD2xx2FWD Series 5.3.1 build 150410版本至5.4.4 Build 161125版本；DS-2CD4x2xFWD Series 5.2.0 build 140721版本至5.4.0 Build 160414版本；DS-2CD4xx5 Series 5.2.0 build 140721版本至5.4.0 Build 160421版本；DS-2DFx Series 5.2.0 build 140805版本至5.4.5 Build 160928版本；DS-2CD63xx Series 5.0.9 build 140305版本至5.3.5 Build 160106版本。', '升级到漏洞修复后的版本 http://www.hikvision.com/us/about_10805.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (107216, 'PHP 5.6.x < 5.6.34 Stack Buffer Overflow', 'PHP 缓冲区错误漏洞', '高危', 'PHP中的ext/standard/http_fopen_wrapper.c文件的‘php_stream_url_wrap_http_ex’函数存在栈缓冲区溢出漏洞。攻击者可利用该漏洞造成拒绝服务（段错误）。以下版本受到影响：PHP 5.6.33及之前版本，7.0.28及之前的7.0.x版本，7.1.x版本至7.1.14版本，7.2.x版本至7.2.2版本。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： http://php.net/ChangeLog-7.php', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (107217, 'PHP 7.0.x < 7.0.28 Stack Buffer Overflow', 'PHP 缓冲区错误漏洞', '高危', 'PHP中的ext/standard/http_fopen_wrapper.c文件的‘php_stream_url_wrap_http_ex’函数存在栈缓冲区溢出漏洞。攻击者可利用该漏洞造成拒绝服务（段错误）。以下版本受到影响：PHP 5.6.33及之前版本，7.0.28及之前的7.0.x版本，7.1.x版本至7.1.14版本，7.2.x版本至7.2.2版本', '厂商已发布升级补丁以修复漏洞，补丁获取链接： http://php.net/ChangeLog-7.php', NULL, NULL);
