INSERT INTO `scan_suggest_log` VALUES (107265, 'nginx < 1.8.1 / 1.9.x < 1.9.10 Multiple Vulnerabilities', 'nginx<1.8.1/1.9。x<1.9.10多个漏洞', '高危', '根据其响应头中的自我报告版本，远程web服务器上托管的nginx版本低于1.8.1或1.9。x在1.9.10之前。因此，它受到供应商咨询中指出的多个漏洞的影响。', '升级至nginx版本1.8.1/1.9.10或更高版本。', 'CVE-2016-0742', '1');
INSERT INTO `scan_suggest_log` VALUES (108722, 'Cisco IOS Software Smart Install Remote Code Execution Vulnerability', 'Cisco IOS Software和IOS XE Software 输入验证漏洞', '紧急', 'Cisco IOS Software和IOS XE Software中的Smart Install功能存在输入验证漏洞，该漏洞源于程序没有正确的校验数据包数据。远程攻击者可通过向TCP 4786端口上的受影响设备发送特制的Smart Install消息利用该漏洞造成拒绝服务（设备重新加载）或执行任意代码。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20180328-smi2', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (108723, 'Cisco IOS XE Software Smart Install Remote Code Execution Vulnerability', 'Cisco IOS XE软件智能安装远程代码执行漏洞', '紧急', '根据其自我报告的版本，iosxe受到一个或多个漏洞的影响。请参阅包括思科出价和思科安全咨询更多信息。', '升级至Cisco bug ID CSCvg76186中引用的相关固定版本。', 'CVE-2018-0171', '1');
INSERT INTO `scan_suggest_log` VALUES (108758, 'Apache 2.4.x < 2.4.33 Multiple Vulnerabilities', 'Apache httpd 安全漏洞', '中危', 'Apache httpd 2.0.23版本至2.0.65版本、2.2.0版本至2.2.34和2.4.0版本至2.4.29版本中的mod_authnz_ldap存在安全漏洞。攻击者可利用该漏洞造成拒绝服务（崩溃和越边界写入）', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： https://httpd.apache.org/security/vulnerabilities_24.html', NULL, NULL);
