INSERT INTO `scan_suggest_log` VALUES (108880, 'Cisco IOS Software Link Layer Discovery Protocol Buffer Overflow Vulnerabilities (cisco-sa-20180328-lldp)', 'Cisco IOS Software、IOS XE Software和IOS XR Software Link Layer Discovery Protocol子系统缓冲区错误漏洞', '高危', 'Cisco IOS Software、IOS XE Software和IOS XR Software中的LLDP子系统存在缓冲区溢出漏洞，该漏洞源于程序没有正确的对畸形的LLDP消息执行错误处理。攻击者可通过提交LLDP协议数据单元利用该漏洞造成拒绝服务或以提升的权限执行任意代码。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20180328-lldp', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (108954, 'Cisco IOS Software Multiple Vulnerabilities in ntpd (cisco-sa-20150408-ntpd)', 'NTP ntpd 代码注入漏洞', '中危', 'NTP 4.2.7p444及之前版本的ntpd中的ntp_proto.c文件中的‘receive’函数中的对称密钥身份验证实现过程存在安全漏洞，该漏洞源于只有当‘MAC’字段是非零长度时，程序才会要求数据包中包含MAC（消息验证代码）。攻击者可通过省略MAC利用该漏洞实施中间人攻击，伪造数据包。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： http://support.ntp.org/bin/view/Main/SecurityNotice#Recent_Vulnerabilities', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (108956, 'Cisco IOS Software DNS Forwarder Denial of Service Vulnerability (cisco-sa-20160928-dns)', 'Cisco IOS和IOS XE Software DNS Forwarder 拒绝服务漏洞', '高危', 'Cisco IOS和IOS XE Software中的DNS Forwarder功能存在拒绝服务漏洞。远程攻击者可利用该漏洞造成设备重载，导致拒绝服务或损坏本地DNS缓存信息。以下版本受到影响：Cisco IOS 12.0至12.4版本，15.0至15.6 版本，IOS XE 3.1至3.15版本。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20160928-dns', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (109087, 'Cisco IOS DHCP Multiple Vulnerabilities', 'Cisco IOS Software和IOS XE Software 输入验证漏洞', '高危', 'Cisco IOS Software和IOS XE Software中的DHCP option 82 encapsulation功能存在输入验证漏洞，该漏洞源于程序没有对option 82信息执行完整的输入校验。远程攻击者可通过发送特制的DHCPv4数据包利用该漏洞造成拒绝服务（设备重新加载）。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20180328-dhcpr1', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (109088, 'Cisco IOS DHCP Multiple Vulnerabilities', 'Cisco IOS DHCP多个漏洞', '高危', '根据其自我报告的版本，远程设备上运行的Cisco IOS软件在解析DHCP数据包时受到DHCP客户端实现中多个拒绝服务漏洞的影响。未经验证的远程攻击者可以通过巧尽心思构建的DHCP数据包利用这些问题，使设备重新加载。', '升级至Cisco bug ID CSCvg62730、CSCvg62754和CSCuh91645中引用的相关固定版本。', 'CVE-2018-0172', '1');
INSERT INTO `scan_suggest_log` VALUES (109321, 'JBoss Enterprise Application Platform doFilter() Method Insecure Deserialization RCE', 'Red Hat JBoss Enterprise Application Platform Jboss Application Server 安全漏洞', '高危', 'Red Hat Jboss EAP 5.0版本中附带的Jboss Application Server存在远程代码执行漏洞。远程攻击者可借助特制的序列化数据利用该漏洞在受影响应用程序上下文中执行任意代码。', '查看红帽咨询CVE-2017-12149中提供的解决文档', NULL, NULL);
