INSERT INTO `scan_suggest_log` VALUES (109576, 'PHP 5.6.x < 5.6.36 Multiple Vulnerabilities', 'PHP 安全漏洞', '中危', 'PHP中存在安全漏洞，该漏洞源于iconv流过滤器没有过滤无效的多字节序列。攻击者可利用该漏洞造成绝服务（无限循环）。以下版本受到影响：PHP 5.6.36之前版本，7.0.30之前的7.0.x版本，7.1.17之前的7.1.x版本，7.2.5之前的7.2.x版本。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： http://php.net/ChangeLog-5.php http://php.net/ChangeLog-5.php', NULL, NULL);
