INSERT INTO `scan_suggest_log` VALUES (117665, 'Elasticsearch ESA-2018-15', 'Elasticsearch Alerting and Monitoring 信息泄露漏洞', '高危', 'Elasticsearch Alerting and Monitoring 6.4.1之前版本和5.6.12之前版本中存在信息泄露漏洞。攻击者可利用该漏洞获取敏感的配置信息，例如密码，令牌或用户名。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://discuss.elastic.co/t/elastic-stack-6-4-1-and-5-6-12-security-update/149035', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (117666, 'Kibana ESA-2018-14', 'Kibana ESA-2018-14', '中危', 'Kibana版本5.3.0到6.4.1通过源字段格式化程序存在跨站点脚本（XSS）漏洞，攻击者可以通过该漏洞从其他Kibana用户获取敏感信息或代表其他Kibana用户执行破坏性操作。', '用户应升级至Kibana版本6.4.1或5.6.12。此问题没有已知的解决方法。', 'CVE-2018-3830', '1');
INSERT INTO `scan_suggest_log` VALUES (117807, 'Apache 2.4.x < 2.4.35 DoS', 'Apache HTTP Server 安全漏洞', '中危', 'Apache HTTP Server 2.4.17版本至2.4.34版本中存在安全漏洞。攻击者可通过持续发送较大的SETTINGS帧利用该漏洞在客户端占据连接，服务器线程和CPU时间时造成连接超时失效，导致拒绝服务。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： https://httpd.apache.org/security/vulnerabilities_24.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (117944, 'Cisco IOS Software Cluster Management Protocol DoS Vulnerability (cisco-sa-20180926-cmp)', 'Cisco IOS Software和IOS XE Software 输入验证漏洞', '高危', 'Cisco IOS Software和IOS XE Software中的Cluster Management Protocol存在输入验证漏洞。物理位置邻近的攻击者可通过向受影响的设备发送恶意CMP消息利用该漏洞造成拒绝服务（交换机崩溃，重新加载或挂起）。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20180926-cmp', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (117945, 'Cisco IOS XE Software Cluster Management Protocol DoS Vulnerability (cisco-sa-20180926-cmp)', 'Cisco IOS XE软件群集管理协议DoS漏洞（Cisco-sa-20180926-cmp）', '中危', '根据其自我报告的版本，iosxe受到一个或多个漏洞的影响。请参阅包括思科出价和思科安全咨询更多信息。', '升级至Cisco bug ID CSCvg48576中引用的相关固定版本。', 'CVE-2018-0475', '1');
INSERT INTO `scan_suggest_log` VALUES (117946, 'Cisco IOS XE Software Errdisable Vulnerabilities (cisco-sa-20180926-errdisable)', 'Cisco IOS XE软件Errdisable漏洞（Cisco-sa-20180926-Errdisable）', '中危', '根据其自我报告的版本，iosxe受到一个或多个漏洞的影响。请参阅包括思科出价和思科安全咨询更多信息。', '升级至Cisco bug ID CSCvh13611中引用的相关固定版本。', 'CVE-2018-0480', '1');
INSERT INTO `scan_suggest_log` VALUES (117950, 'Cisco IOS XE Software IPv6 Hop-by-Hop DoS Vulnerability (cisco-sa-20180926-ipv6hbh)', 'Cisco IOS XE软件IPv6逐跳DoS漏洞（Cisco-sa-20180926-ipv6hbh）', '高危', '根据其自我报告的版本，iosxe受到一个或多个漏洞的影响。请参阅包括思科出价和思科安全咨询更多信息。', '升级至Cisco bug ID CSCuz28570中引用的相关固定版本。', 'CVE-2018-0467', '1');
INSERT INTO `scan_suggest_log` VALUES (118035, 'Apache Tomcat 7.0.0 < 7.0.91 Open Redirect Weakness', 'Apache Tomcat 7.0.0<7.0.91开放重定向漏洞', '中危', '安装在远程主机上的Apache Tomcat版本是7.0.91之前的7.0.x。因此，它受到开放重定向漏洞的影响。', '升级至Apache Tomcat 7.0.91或更高版本。', 'CVE-2018-11784', '1');
INSERT INTO `scan_suggest_log` VALUES (118036, 'Apache Tomcat 8.5.x < 8.5.34 Open Redirect Weakness', 'ApacheTomcat 8.5。x<8.5.34开放性重定向弱点', '中危', '远程主机上安装的Apache Tomcat版本为8.5。x在8.5.34之前。因此，它受到开放重定向漏洞的影响。', '升级至Apache Tomcat 8.5.34或更高版本。', 'CVE-2018-11784', '1');
INSERT INTO `scan_suggest_log` VALUES (118038, 'HPE Intelligent Management Center dbman Command 10001 Information Disclosure', 'HPE智能管理中心（iMC）dbman进程信息泄露漏洞', '中危', 'HPE智能管理中心（iMC）dbman进程信息泄露漏洞的影。攻击者可以通过10001请求命令来查看SYSTEM或root用户任意目录。', NULL, NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (118039, 'Java JMX Agent Insecure Configuration', 'Java JMX代理不安全配置', '高危', 'Java JMX代理不安全配置', 'JMX代理启用SSL客户端或密码身份验证。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (118151, 'nginx Data Disclosure Vulnerability', 'nginx数据泄露漏洞', '中危', '根据其服务器响应标头，nginx的安装版本早于1.12。1或1.13。x在1.13之前。3.因此，它受到范围筛选器模块中的整数溢出漏洞的影响。未经验证的远程攻击者可以通过精心编制的请求来利用此漏洞，以泄露潜在的敏感信息。', '手动应用修补程序或升级到nginx 1.12。1 / 1.13.3或更高版本。', 'CVE-2017-7529', '1');
INSERT INTO `scan_suggest_log` VALUES (118154, 'SSH Protocol Authentication Bypass (Remote Exploit Check)', 'SSH协议身份验证旁路（远程攻击检查）', '中危', '远程ssh服务器易受身份验证旁路攻击。攻击者可以通过显示SSH2_MSG_USERAUTH_成功消息来绕过身份验证，而不是通常会启动身份验证的SSH2_MSG_USERAUTH_请求方法。注意：此漏洞已在libssh公告中披露，但也被发现适用于其他应用程序和软件包。', '升级到libssh 0.7。6 / 0.8.4或更高版本，如适用。否则，请联系您的产品供应商。', 'CVE-2018-10933', '1');
INSERT INTO `scan_suggest_log` VALUES (118466, 'ESXi 6.0 / 6.5 / 6.7 Out-of-Bounds Read Vulnerability (VMSA-2018-0026) (Remote Check)', 'VMware ESXi、Workstation和Fusion 缓冲区错误漏洞', '高危', 'VMware ESXi、Workstation和Fusion中存在越界读取漏洞。攻击者可利用该漏洞在主机上执行代码。以下产品和版本受到影响：VMware ESXi ESXi670-201810101-SG之前的6.7版本，ESXi650-201808401-BG之前的6.5版本，ESXi600-201808401-BG之前的6.0版本；Workstation 14.1.3之前的14.x版本；Fusion 10.1.3之前的10.x版本。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： https://www.vmware.com/security/advisories/VMSA-2018-0026.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (118798, 'Kibana ESA-2018-18', 'Kibana ESA-2018-18', '高危', 'CyberArk实验室的Nethanel Coppenhagen发现6.4.3和5.6.13之前的Kibana版本在控制台插件中包含任意文件包含缺陷。有权访问Kibana控制台API的攻击者可以发送一个请求，试图执行javascript代码。这可能会导致攻击者在主机系统上以Kibana进程的权限执行任意命令。请注意，Nessus尚未对此问题进行测试，而是仅依赖于应用程序自我报告的版本号。', '用户应升级至Elastic Stack版本6.4.3或5.6.13或更高版本。无法升级其安装的用户应参考供应商咨询中概述的缓解说明。', 'CVE-2018-17246', '1');
INSERT INTO `scan_suggest_log` VALUES (118885, 'ESXi 6.0 / 6.5 / 6.7 Multiple Vulnerabilities (VMSA-2018-0027) (Remote Check)', 'Vmware ESXi、Workstation和Fusion 安全漏洞', '高危', 'Vmware ESXi、Workstation和Fusion中存在安全漏洞，该漏洞源于vmxnet3虚拟网络调机器使用了未初始化的栈内存。攻击者可通过发送来提交恶意的输入利用该漏洞在主机系统上执行任意代码。以下产品和版本受到影响：Vmware ESXi 6.7版本（不带有ESXi670-201811401-BG），6.5版本（不带有ESXi650-201811301-BG），6.0版本（不带有ESXi600-201811401-BG）；Workstation 15.x版本，14.x版本；Fusion 11.x版本，10.x版本。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接: https://www.vmware.com/security/advisories/VMSA-2018-0027.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (118956, 'nginx 1.x < 1.14.1 / 1.15.x < 1.15.6 Multiple Vulnerabilities', 'nginx1。x<1.14。1 / 1.15.x<1.15。6多个漏洞', '中危', '根据其服务器响应头，nginx的安装版本为1。x在1.14之前。1或1.15。x在1.15之前。6.因此，它受到以下问题的影响：-存在与模块“ngx_http_v2_module”相关的未指定错误，该错误允许过多的内存使用。（CVE-2018-16843）-存在与模块“ngx_http_v2_模块”相关的未指定错误，该模块允许过度使用CPU。（CVE-2018-16844）-存在与模块“ngx_http_mp4_模块”相关的未指定错误，该模块允许工作进程崩溃或内存泄漏。（CVE-2018-16845）', '升级至nginx1.14。1 / 1.15.6或更高版本。', 'CVE-2018-16843', '1');
INSERT INTO `scan_suggest_log` VALUES (119422, 'Kibana ESA-2018-17', 'Kibana ESA-2018-17', '中危', 'Kibana版本4.0到4.6、5.0到5.6.12以及6.0到6.4.2在生成PDF报告时使用授权凭据的方式中包含错误。如果报表请求外部资源，则HTTP请求中包含可由外部资源提供程序恢复的明文凭据。', '用户应升级至Elastic Stack版本5.6.13或6.4.3。无法升级的用户可以通过在Kibana.yml文件中将xpack.Reporting.enabled设置为false来禁用Kibana中的报告功能。这并不能防止以前泄漏的凭据被重用。有关从该漏洞中缓解的更多信息，请参阅我们的博客文章。', 'CVE-2018-17245', '1');
INSERT INTO `scan_suggest_log` VALUES (119501, 'H3C / HPE Intelligent Management Center PLAT < 7.3 E0605P06 Multiple Vulnerabilities', 'HPE Intelligent Management Center PLAT 缓冲区错误漏洞', '紧急', 'HPE iMC PLAT 7.3 (E0605P06)之前版本中的dbman存在缓冲区溢出漏洞，该漏洞源于程序在将用户提交的数据复制到固定长度的栈缓冲区之前，没有正确的验证其长度。远程攻击者可利用该漏洞执行任意代码。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： https://support.hpe.com/hpsc/doc/public/display?docId=hpesbhf03906en_us', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (119764, 'PHP 5.6.x < 5.6.39 Multiple vulnerabilities', 'University of Washington IMAP Toolkit 2007f 操作系统命令注入漏洞', '高危', '基于UNIX平台的University of Washington IMAP Toolkit 2007f中的‘imap_open()’函数存在命令注入漏洞，该漏洞源于程序没有正确的验证server URI。远程攻击者可借助‘imap_rimap’和‘tcp_aopen’函数利用该漏洞执行任意的操作系统命令。', '目前厂商暂未发布修复措施解决此安全问题，建议使用此软件的用户随时关注厂商主页或参考网址以获取解决办法： http://www.washington.edu/imap/', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (119765, 'PHP 7.1.x < 7.1.25 Multiple vulnerabilities', 'University of Washington IMAP Toolkit 2007f 操作系统命令注入漏洞', '高危', '基于UNIX平台的University of Washington IMAP Toolkit 2007f中的‘imap_open()’函数存在命令注入漏洞，该漏洞源于程序没有正确的验证server URI。远程攻击者可借助‘imap_rimap’和‘tcp_aopen’函数利用该漏洞执行任意的操作系统命令。', '升级到PHP 7.1.25或更高版本', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (119766, 'PHP 7.2.x < 7.2.13 Multiple vulnerabilities', 'PHP 7.2.x < 7.2.13多重漏洞', '高危', '根据它的标语，在远程web服务器上运行的PHP版本是7.2。x在7.2.13之前。因此，它受到多个漏洞的影响:- imap_open函数中存在一个任意的命令注入漏洞，这是由于在将邮箱名称传递给rsh或ssh命令之前没有对其进行适当的过滤。通过身份验证的远程攻击者可以通过发送特殊的IMAP服务器名称来利用这一点，从而在目标系统上执行任意命令。(CVE-2018-19518) -在phar_parse_pharfile函数中存在堆缓冲区overread。当试图解析.phar文件时，未经身份验证的远程攻击者可以利用这一点读取超过实际数据的已分配或未分配内存。(cve - 2018 - 20783)', '升级到PHP版本7.2.13或更高版本.', 'CVE-2018-19518', NULL);
INSERT INTO `scan_suggest_log` VALUES (120944, 'IBM Spectrum Protect Client 7.1.x < 7.1.8.4 / 8.1.x < 8.1.6.1 Denial of Service Vulnerability (CVE-2018-1786)', 'IBM Spectrum Protect Client 7.1.x <7.1.8.4 / 8.1.x <8.1.6.1拒绝服务漏洞（CVE-2018-1786）', '中危', 'IBM Spectrum Protect的前身是Tivoli Storage Manager，安装在远程主机上的版本是7.1.x <7.1.8.4或8.1.x <8.1.6.1。 因此，它可能受到拒绝服务(DoS)的影响。由于TCP/IP套接字的错误积累而导致的漏洞CLOSE_WAIT状态。未经身份验证的远程攻击者可以利用此问题将导致进程停止响应。', '升级到IBM Spectrum Protect 7.1.8.4或8.1.6.1或更高版本。', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (121007, 'SSH Known Hard Coded Private Keys', '多款Cisco产品安全漏洞', '中危', '多款Cisco产品的加密机制中存在安全漏洞，该漏洞源于程序没有生成唯一的密钥和证书。远程攻击者可通过实施暴力破解攻击利用该漏洞解密用户的机密信息。以下产品受到影响：Cisco RV320 Dual Gigabit WAN VPN Router，RV325 Dual Gigabit WAN VPN Router，RV325 Dual WAN Gigabit VPN Router，RVS4000 4-port Gigabit Security Router - VPN，WRV210 Wireless-G VPN Router - RangeBooster，WAP4410N Wireless-N Access Point - PoE/Advanced Security，WRV200 Wireless-G VPN Router - RangeBooster，WRVS4400N Wireless。', '厂商已经发布了升级补丁以修复这个安全问题，请到厂商的相应页面下载： http://tools.cisco.com/security/center/content/CiscoSecurityAdvisory/cisco-sa-20151125-ci', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (121010, 'TLS Version 1.1 Protocol Detection', 'TLS 1.1版本协议检测', '中危', '远程服务接受使用TLS 1.1加密的连接。TLS 1.1不支持当前的和推荐的密码套件。从2020年3月31日起，支持MAC计算前加密的密码以及经过认证的加密模式(如GCM)将不能在TLS 1.1中使用，aren__启用的TLS 1.2及以上版本的端点将不能在主要的web浏览器和主要供应商上正常运行。截至2018年6月30日，PCI DSS v3.2仍然允许TLS 1.1，但强烈建议使用TLS 1.2。在IETF之前已经有了一个提案，完全反对TLS 1.1，很多供应商已经主动这么做了', '启用对TLS 1.2和/或1.3的支持，禁用对TLS 1.1的支持', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (121116, 'Apache Tomcat 7.0.x < 7.0.47 / 8.0.x < 8.0.0-RC3 Information Disclosure', 'Apache Tomcat 7.0。x<7.0.47/8.0。x<8.0.0-RC3信息披露', '中危', '根据其自我报告的版本号，在远程主机上侦听的Apache Tomcat实例是7.0。x在7.0.47或8.0之前。8.0.0-RC3之前的x。因此，它受到以下漏洞的影响：-Tomcat中存在一个信息泄露漏洞，这是由于对内容长度头的不当处理造成的。未经验证的远程攻击者可以利用此漏洞泄露潜在的敏感信息。请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 7.0.47/8.0.0-RC3或更高版本。', 'CVE-2013-4286', '1');
INSERT INTO `scan_suggest_log` VALUES (121117, 'Apache Tomcat 7.0.x < 7.0.65 / 8.0.x < 8.0.27 Directory Traversal', 'Apache Tomcat 7.0.x<7.0.65/8.0.x<8.0.27目录遍历', '中危', '根据其自我报告的版本号，在远程主机上侦听的apachetomcat实例是7.0.65之前的7.0.x，或8.0.27之前的8.0.x。因此，它受到以下漏洞的影响：-当使用以“/…”开头的路径通过ServletContext方法访问资源时，Tomcat中存在目录遍历漏洞。未经验证的远程攻击者可以通过发送巧尽心思构建的请求来利用此漏洞，获取部署应用程序的目录的目录列表。请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 7.0.65/8.0.27或更高版本。', 'CVE-2015-5174', '1');
INSERT INTO `scan_suggest_log` VALUES (121118, 'Apache Tomcat < 7.0.67 Session Fixation', 'Apache Tomcat<7.0.67会话固定', '中危', '根据其自我报告的版本号，在远程主机上侦听的apachetomcat实例早于7.0.67。因此，它受到会话固定漏洞的影响：-将ID分配给新会话时，由于未能使以前的会话ID无效而存在缺陷。攻击者可以通过使用requestedSessionSSL字段固定会话ID的特制请求利用此漏洞进行攻击，以确保用户使用已知会话ID进行身份验证，从而允许会话随后被劫持。请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 7.0.67或更高版本。', 'CVE-2015-5346', '1');
INSERT INTO `scan_suggest_log` VALUES (121119, 'Apache Tomcat 7.0.x < 7.0.70 / 8.0.x < 8.0.36 / 8.5.x < 8.5.3 / 9.0.x < 9.0.0.M8 Denial of Service', 'Apache Tomcat 7.0.x<7.0.70/8.0.x<8.0.36/8.5.x<8.5.3/9.0.x<9.0.0.M8拒绝服务', '高危', '根据其自我报告的版本号，在远程主机上侦听的Apache Tomcat实例是7.0.x早于7.0.70、8.0.x<8.0.36、8.5.x<8.5.3或9.0.x<9.0.0.M8。因此，它受到拒绝服务漏洞的影响：-在Commons FileUpload中发现了一个拒绝服务漏洞，当多部分边界的长度刚好低于用于读取上载文件的缓冲区（4096字节）的大小（如果边界的长度通常为数十字节）时，就会出现该漏洞。请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 7.0.70/8.0.36/8.5.3/9.0.0.M8或更高版本。', 'CVE-2016-3092', '1');
INSERT INTO `scan_suggest_log` VALUES (121120, 'Apache Tomcat 7.0.x < 7.0.76 / 8.0.x < 8.0.42 / 8.5.x < 8.5.12 / 9.0.x < 9.0.0.M18 Improper Access Control', 'Apache Tomcat 7.0.x<7.0.76/8.0.x<8.0.42/8.5.x<8.5.12/9.0.x<9.0.0.M18访问控制不当', '中危', '根据其自我报告的版本号，在远程主机上侦听的Apache Tomcat实例是7.0.x早于7.0.76、8.0.x<8.0.42、8.5.x<8.5.12或9.0.x<9.0.0.M18。因此，它受以下漏洞的影响：-当对应用程序侦听器的调用未使用适当的facade对象时，存在不正确的访问控制漏洞。这允许不受信任的应用程序访问和修改与其他web应用程序相关的信息。请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 7.0.76/8.0.42/8.5.12/9.0.0.M18或更高版本。', 'CVE-2017-5648', '1');
INSERT INTO `scan_suggest_log` VALUES (121121, 'Apache Tomcat 7.0.x < 7.0.88 Denial of Service', 'Apache Tomcat 7.0.x<7.0.88拒绝服务', '中危', '根据其自我报告的版本号，在远程主机上侦听的apachetomcat实例是7.0.x之前的7.0.88。因此，它受到以下漏洞的影响：-由于UTF-8解码器中不正确的溢出处理，Tomcat中存在拒绝服务（DoS）漏洞。未经验证的远程攻击者可以利用此问题在解码器中造成无限循环，从而导致拒绝服务情况。请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 7.0.88或更高版本。', 'CVE-2018-1336', '1');
INSERT INTO `scan_suggest_log` VALUES (121123, 'Apache Tomcat < 8.0.30 Directory Disclosure', 'Apache Tomcat<8.0.30目录公开', '中危', '根据其自我报告的版本号，在远程主机上侦听的Apache Tomcat实例早于8.0.30。因此，它受到信息泄露漏洞的影响：-信息泄露漏洞的存在是因为在处理缺少尾部斜杠的目录请求时未能强制执行访问限制。未经验证的远程攻击者可以利用此漏洞枚举有效目录。（CVE-2015-5345）请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 8.0.30或更高版本。', 'CVE-2015-5345', '1');
INSERT INTO `scan_suggest_log` VALUES (121124, 'Apache Tomcat 8.0.x < 8.0.52 / 8.5.x < 8.5.31 / 9.0.x < 9.0.8 Denial of Service', 'Apache Tomcat 8.0.x<8.0.52/8.5.x<8.5.31/9.0.x<9.0.8拒绝服务', '中危', '根据其自我报告的版本号，在远程主机上侦听的Apache Tomcat实例是8.0.x<8.0.52、8.5.x<8.5.31或9.0.x<9.0.8。因此，它受到以下漏洞的影响：-由于UTF-8解码器中不正确的溢出处理，Tomcat中存在拒绝服务（DoS）漏洞。未经验证的远程攻击者可以利用此问题在解码器中造成无限循环，从而导致拒绝服务情况。请注意，Nessus没有针对这些问题进行测试，而是只依赖于应用程序的自我报告版本号。', '升级至Apache Tomcat 8.0.52/8.5.31/9.0.8或更高版本。', 'CVE-2018-1336', '1');
INSERT INTO `scan_suggest_log` VALUES (121353, 'PHP 7.2.x < 7.2.14 Multiple vulnerabilities.', 'PHP 7.2.x < 7.2.14多重漏洞', '高危', '根据它的标语，在远程web服务器上运行的PHP版本是7.2。x在7.2.14之前。因此，它受到多个漏洞的影响:- gd_插值.c中的_gdContributionsAlloc函数存在整数欠流状态。未经身份验证的远程攻击者可以通过与减少u变量相关的向量产生未指定的影响。(CVE-2016-10166) - ext/imap/php_imap.c中存在拒绝服务(DoS)漏洞。未经身份验证的远程攻击者可以利用这个问题，通过imap_mail函数的消息参数中的空字符串，使应用程序停止响应。(CVE-2018-19935) - GD图形库(又名LibGD) 2.2.5中gd_color_match.c中的gdImageColorMatch中存在基于堆的缓冲区溢出。未经身份验证的远程攻击者可以通过触发imagecolormatch调用来利用这一点，从而导致拒绝服务条件或执行任意代码。(CVE-2019-6977) -由于对输入数据的验证不当，xmlrpc_decode函数中存在基于堆的缓冲区overread。未经身份验证的远程攻击者可以利用这一点，通过一个特殊设计的请求，导致堆越界读或无后读条件，这可能导致整个系统的破坏。(CVE-2019-9020) - PHAR扩展中的PHAR读取函数中存在基于堆的缓冲区超读，这是由于内存操作实现不当造成的。未经身份验证的远程攻击者可以利用这一点，通过说服用户解析目标系统上一个特别设计的文件名，从而泄露敏感信息。(CVE-2019-9021) -由于DNS响应解析不当，dns_get_record函数存在信息泄露漏洞。未经身份验证的远程攻击者可以利用这一点，通过从恶意DNS服务器发送的一个特别精心设计的DNS应答来泄露潜在的敏感信息。由于内存操作的实现不当，mbstring正则表达式函数中存在多个基于堆的缓冲区超读实例。未经身份验证的远程攻击者可以通过发送一个特殊设计的包含多字节序列的正则表达式来利用这一点，从而导致允许攻击者完全破坏目标系统的条件。(CVE-2019-9023) -由于内存操作实现不当，xmlrpc_decode函数中存在一个越界读取错误。未经身份验证的远程攻击者可以利用这一点，通过敌对的XMLRPC服务器使PHP从分配区域以外的内存中读取数据。(cve - 2019 - 9024)', '升级到PHP版本7.2.14或更高版本.', 'CVE-2016-10166', NULL);
INSERT INTO `scan_suggest_log` VALUES (121355, 'Apache 2.4.x < 2.4.38 Multiple Vulnerabilities', 'Apache HTTP Server 资源管理错误漏洞', '中危', 'Apache HTTP Server 2.4.17版本至2.4.37版本中存在资源管理错误漏洞。该漏洞源于网络系统或产品对系统资源（如内存、磁盘空间、文件等）的管理不当。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： https://httpd.apache.org/security/vulnerabilities_24.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (121383, 'OpenSSL 1.0.x < 1.0.2q Multiple Vulnerabilities', 'OpenSSL 信息泄露漏洞', '中危', 'OpenSSL是OpenSSL团队的一个开源的能够实现安全套接层（SSLv2/v3）和安全传输层（TLSv1）协议的通用加密库。该产品支持多种加密算法，包括对称密码、哈希算法、安全散列算法等。 OpenSSL中存在信息泄露漏洞。该漏洞源于网络系统或产品在运行过程中存在配置等错误。未授权的攻击者可利用漏洞获取受影响组件敏感信息。', '厂商暂未发布修复措施解决此安全问题，建议使用此软件的用户随时关注厂商主页或参考网址以获取解决办法： https://github.com/bbbrumley/portsmash', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (121384, 'OpenSSL 1.1.0 < 1.1.0j Multiple Vulnerabilities', 'OpenSSL 加密问题漏洞', '中危', 'OpenSSL 1.1.1版本、1.1.0版本至1.1.0i版本和1.0.2版本至1.0.2p版本中的DSA签名算法存在加密问题漏洞。该漏洞源于网络系统或产品未正确使用相关密码算法，导致内容未正确加密、弱加密、明文存储敏感信息等。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://www.openssl.org/news/secadv/20181030.txt', NULL, NULL);
