INSERT INTO `scan_suggest_log` VALUES (121510, 'PHP 7.1.x < 7.1.26 Multiple vulnerabilities.', 'GD Graphics Library 缓冲区错误漏洞', '高危', 'GD Graphics Library 2.2.4之前的版本中的gd_interpolation.c文件的‘_gdContributionsAlloc’函数存安全漏洞。远程攻击者可利用该漏洞造成拒绝服务。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://github.com/libgd/libgd/commit/60bfb401ad5a4a8ae995dcd36372fe15c71e1a35', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (121602, 'PHP 5.6.x < 5.6.40 Multiple vulnerabilities.', 'GD Graphics Library 缓冲区错误漏洞', '高危', 'GD Graphics Library 2.2.4之前的版本中的gd_interpolation.c文件的‘_gdContributionsAlloc’函数存安全漏洞。远程攻击者可利用该漏洞造成拒绝服务。', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://github.com/libgd/libgd/commit/60bfb401ad5a4a8ae995dcd36372fe15c71e1a35', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (122032, 'iLO 4 < 2.60 / iLO 5 < 1.30 Multiple Vulnerabilities', 'HPE Integrated Lights-Out 4和Integrated Lights-Out 5 安全漏洞', '高危', 'HPE iLO 4 2.60之前版本和iLO 5 1.30之前版本中存在安全漏洞。攻击者可利用该漏洞执行代码。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://support.hpe.com/hpsc/doc/public/display?docLocale=en_US&docId=emr_na-hpesbhf03844en_us', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (122060, 'Apache 2.4.x < 2.4.33 Multiple Vulnerabilities', 'Apache httpd 安全漏洞', '中危', 'Apache httpd 2.0.23版本至2.0.65版本、2.2.0版本至2.2.34和2.4.0版本至2.4.29版本中的mod_authnz_ldap存在安全漏洞。攻击者可利用该漏洞造成拒绝服务（崩溃和越边界写入）。', '目前厂商已发布升级补丁以修复漏洞，补丁获取链接： https://httpd.apache.org/security/vulnerabilities_24.html', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (122095, 'iLO 4 < 2.53 Remote Code Execution Vulnerability', 'HP Integrated Lights-Out 4 安全漏洞', '紧急', 'HP iLO 4 2.53之前的版本中存在安全漏洞。攻击者可利用该漏洞绕过身份验证，执行代码。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://support.hpe.com/hpsc/doc/public/display?docId=emr_na-hpesbhf03769en_us', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (122187, 'iLO 3 < 1.88 / iLO 4 < 2.44 XSS Vulnerability', 'HPE Integrated Lights-Out 3和4 跨站脚本漏洞', '中危', 'HPE iLO 3 1.88之前的版本和HPE iLO 4 2.44之前的版本中存在跨站脚本漏洞。远程攻击者可利用该漏洞在用户浏览器中执行任意的Web脚本或HTML。', '厂商已发布升级补丁以修复漏洞，补丁获取链接： https://support.hpe.com/hpsc/doc/public/display?docLocale=en_US&docId=emr_na-c05337025', NULL, NULL);
INSERT INTO `scan_suggest_log` VALUES (122190, 'iLO 2 < 2.27 / iLO 3 < 1.82 / iLO 4 < 2.10 Denial of Service Vulnerability', 'HP Integrated Lights-Out 安全漏洞', '中危', 'HP iLO中存在安全漏洞。远程攻击者可利用该漏洞绕过既定的访问限制，或造成拒绝服务。以下版本受到影响：HP iLO 2固件2.25及之前版本，HP iLO 3固件1.80及之前版本，HP iLO 4固件2.03及之前版本。', '厂商已经发布了升级补丁以修复此安全问题，补丁获取链接： https://h20564.www2.hp.com/hpsc/doc/public/display?docId=emr_na-c04582368', NULL, NULL);
