INSERT INTO `scan_suggest_log` VALUES (145518, 'Ubuntu 18.04 LTS : Linux kernel vulnerability (USN-4710-1)', 'Ubuntu 18.04 LTS:Linux内核漏洞（USN-4710-1）', '中危', '远程Ubuntu 18.04 LTS主机安装的软件包受USN-4710-1建议中提到的漏洞影响。-在使用PERF_EVENT_IOC_SET_FILTER的过程中，发现Linux内核性能监视子系统中存在漏洞内存泄漏。本地用户可能会利用此漏洞耗尽资源，从而导致拒绝服务。（CVE-2020-25704）请注意，Nessus未针对该问题进行测试，而是仅依赖于应用程序的自我报告版本号。', '更新受影响的包。', 'CVE-2020-25704', '1');
